INSERT INTO `creature_text` VALUES (32638, 0, 0, 'Hakmud cannot work under these conditions! Goodbye!', 12, 0, 100, 0, 0, 0, 33017, 0, 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES (32638, 0, 1, 'I thought we were friend, buddy! You leave Hakmud stranded?', 12, 0, 100, 0, 0, 0, 33018, 0, 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES (32638, 0, 2, 'Thanks, buddy! Thanks for nothing!', 12, 0, 100, 0, 0, 0, 33014, 0, 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES (32638, 0, 3, 'What is Hakmud supposed to do now, buddy?', 12, 0, 100, 0, 0, 0, 33016, 0, 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES (32638, 0, 4, 'Why do you treat Hakmud like this, buddy?', 12, 0, 100, 0, 0, 0, 33015, 0, 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES (32639, 0, 0, 'Goodbye! You were Gnimo\'s best friend ever!', 12, 0, 100, 0, 0, 0, 33020, 0, 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES (32639, 0, 1, 'Nobody loves Gnimo...', 12, 0, 100, 0, 0, 0, 33019, 0, 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES (32639, 0, 2, 'Time for Gnimo to shut down.', 12, 0, 100, 0, 0, 0, 33021, 0, 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES (19228, 49, 0, 'Is it just me, or is the Horde about the ugliest enemy we could find?', 12, 7, 100, 6, 0, 0, 16678, 0, 'Perry Gatner');
INSERT INTO `creature_text` VALUES (19228, 50, 0, 'Just so you guys don\'t feel left out, all I said was how attractive the blood elves are.  It loses something in the translation.', 12, 1, 100, 6, 0, 0, 16679, 0, 'Perry Gatner');
INSERT INTO `creature_text` VALUES (19228, 0, 6, 'Are you ready to laugh!', 12, 0, 100, 5, 0, 0, 16329, 0, 'Perry Gatner');
INSERT INTO `creature_text` VALUES (4880, 0, 0, 'Ok, let\'s get started.', 12, 0, 100, 0, 0, 0, 1610, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 1, 0, 'Now let\'s look for the herb.', 12, 0, 100, 0, 0, 0, 1611, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 2, 0, 'Nope, not here...', 12, 0, 100, 0, 0, 0, 1612, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 3, 0, 'There must be one around here somewhere...', 12, 0, 100, 0, 0, 0, 1613, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 4, 0, 'Ah, there\'s one!', 12, 0, 100, 0, 0, 0, 1614, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 5, 0, 'Come, $n!  Let\'s go over there and collect it!', 12, 0, 100, 0, 0, 0, 1615, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 6, 0, '%s picks the herb from the ground.', 16, 0, 100, 0, 0, 0, 1616, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 7, 0, 'Ok, now let\'s get out of here!', 12, 0, 100, 0, 0, 0, 1617, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 8, 0, 'I can make it from here.  Thanks, $n!  And talk to my employer about a reward!', 12, 0, 100, 0, 0, 0, 1618, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 9, 0, '%s disappears back into the swamp.', 16, 0, 100, 0, 0, 0, 1619, 0, '\"Stinky\" Ignatz');
