INSERT INTO `creature_text` VALUES (31334, 1, 0, '%s nods.', 16, 0, 100, 0, 0, 0, 32046, 0, 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES (31334, 2, 0, 'They will not.', 12, 0, 100, 1, 0, 0, 32047, 0, 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES (28451, 0, 0, 'What a fight you missed, Dorian! It was one for the ages!', 12, 0, 100, 0, 0, 0, 28026, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 1, 0, 'There we were, riding into battle at full speed on the back of Stampy...', 12, 0, 100, 0, 0, 0, 28027, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 2, 0, 'Everything was shaking all over the place. I could hardly steady my gun.', 12, 0, 100, 0, 0, 0, 28028, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 3, 0, 'And then we saw her, the fearsome beast!', 12, 0, 100, 0, 0, 0, 28029, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 4, 0, 'It took all of $n\'s skill to maintain control of Stampy.', 12, 0, 100, 0, 0, 0, 28030, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 5, 0, 'The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster!', 12, 0, 100, 0, 0, 0, 28032, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 6, 0, 'But, in the end, $n and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory.', 12, 0, 100, 0, 0, 0, 28033, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 7, 0, 'Do I detect a bit of jealousy, Dorian?', 12, 0, 100, 0, 0, 0, 28036, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28376, 0, 0, 'Well, I don\'t know. I was here tending the fire and discussing the drawbacks of cogs with Zootfizzle. That\'s pretty epic, too, in its own way.', 12, 0, 100, 0, 0, 0, 28037, 0, 'Dorian');
INSERT INTO `creature_text` VALUES (28376, 1, 0, 'It\'s your lucky day, Zootfizzle. $n has brought back more hatchlings for you.', 12, 0, 100, 0, 0, 0, 28892, 0, 'Dorian');
INSERT INTO `creature_text` VALUES (28376, 2, 0, 'Well, you can always go back to the camp and help Weslex with all his cogs...', 12, 0, 100, 0, 0, 0, 28894, 0, 'Dorian');
INSERT INTO `creature_text` VALUES (28374, 0, 0, 'I\'m not here to be the keeper of your hatchlings, Dorian. I\'m doing research of my own, as well.', 12, 0, 100, 0, 0, 0, 28893, 0, 'Zootfizzle');
INSERT INTO `creature_text` VALUES (28374, 1, 0, 'No, not the cogs! Anything but the cogs!', 12, 0, 100, 0, 0, 0, 28895, 0, 'Zootfizzle');
INSERT INTO `creature_text` VALUES (21315, 0, 0, 'The skies will darken and all will go quiet. Only then will you know the sweet serenity of death...', 12, 0, 100, 0, 0, 0, 19291, 0, 'Ruul the Darkener');
INSERT INTO `creature_text` VALUES (21315, 1, 0, 'Your world is at an end.', 12, 0, 100, 0, 0, 0, 19290, 0, 'Ruul the Darkener');
INSERT INTO `creature_text` VALUES (28229, 0, 0, 'More intruders? You\'ll soon learn, no one messes with the Venture Company!', 14, 0, 100, 0, 0, 0, 27611, 0, 'Venture Co. Air Patrol');
INSERT INTO `creature_text` VALUES (27987, 0, 0, 'Over here, to the southeast. I see they got you too...', 14, 0, 100, 0, 0, 0, 27610, 0, 'Monte Muzzleshot');
INSERT INTO `creature_text` VALUES (28192, 0, 0, 'WARNING: Missile lock detected. Engaging evasive maneuvers.', 12, 0, 100, 0, 0, 0, 27621, 0, 'Archimage Pentarus Flying Machine');
INSERT INTO `creature_text` VALUES (33662, 0, 0, 'Any luck?', 12, 0, 100, 0, 0, 0, 34184, 0, 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES (33662, 1, 0, 'Keep at it. If our suspicions are right and this is a transporter, it could prove extremely useful in our assault.', 12, 0, 100, 0, 0, 0, 34186, 0, 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES (33701, 0, 0, 'I heard a story or two of a Lore Keeper in Uldaman that fit your description. Do you serve a similar purpose?', 12, 0, 100, 0, 0, 0, 33702, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 1, 0, 'Frontal defense systems? Is there something I should let Brann know before he has anyone attempt to enter the complex?', 12, 0, 100, 0, 0, 0, 33704, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 2, 0, 'Can you detail the nature of these defense systems?', 12, 0, 100, 0, 0, 0, 33706, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 3, 0, 'Got it. At least we don\'t have to deal with those orbital emplacements.', 12, 0, 100, 0, 0, 0, 33708, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 4, 0, 'Rhydian, make sure you let Brann and Archmage Pentarus know about those defenses immediately.', 12, 0, 100, 0, 0, 0, 33709, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 5, 0, 'And you mentioned an imprisoned entity? What is the nature of this entity and what is its status?', 12, 0, 100, 0, 0, 0, 33710, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 6, 0, 'Yogg-Saron is here? It sounds like we really will have our hands full then.', 12, 0, 100, 0, 0, 0, 33713, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33626, 0, 0, 'Not a bit. For the life of me, I can\'t figure out how this thing works.', 12, 0, 100, 0, 0, 0, 34183, 0, 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES (33626, 1, 0, 'I know, I know... I haven\'t given up yet. Don\'t get your hopes up though, this technology is way beyond me.', 12, 0, 100, 0, 0, 0, 34187, 0, 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES (33686, 3, 0, 'Primary defensive emplacements consist of iron constructs and Storm Beacons, which will generate additional constructs as necessary. Secondary systems consist of orbital defense emplacements.', 12, 0, 0, 0, 0, 0, 33712, 0, 'Norgannon SAY_EVENT_4');
INSERT INTO `creature_text` VALUES (33686, 2, 0, 'Compromise of complex detected, security override enabled - query permitted.', 12, 0, 0, 0, 0, 0, 33707, 0, 'Norgannon SAY_EVENT_3');
INSERT INTO `creature_text` VALUES (33686, 1, 0, 'Access to the interior of the complex is currently restricted. Primary defensive emplacements are active. Secondary systems are currently non-active.', 12, 0, 0, 0, 0, 0, 33705, 0, 'Norgannon SAY_EVENT_2');
