INSERT INTO `creature_text` VALUES (1538, 0, 2, 'The Scarlet Crusade shall smite the wicked and drive evil from these lands!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (1538, 0, 1, 'You carry the taint of the scourge. Prepare to enter the twisting nether.', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (1538, 0, 0, 'The light condemns all who harbor evil. Now you will die!', 12, 0, 50, 0, 0, 0, 0, 0, 'on Aggro Text');
