INSERT INTO `creature_text` VALUES (39038, 0, 0, 'Father!', 12, 0, 100, 0, 0, 0, 39138, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39038, 1, 0, 'Shut up.', 12, 0, 100, 0, 0, 0, 39140, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39038, 2, 0, 'You raised me to be a killer.  How am I doing, daddy?', 12, 0, 100, 0, 0, 0, 39141, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39038, 3, 0, 'But wait... I remember now. You taught me to only kill the undead. So you do want me to kill myself, daddy?', 12, 0, 100, 0, 0, 0, 39143, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39038, 4, 0, 'Then again, why kill myself... when I can kill YOU instead!', 12, 0, 100, 0, 0, 0, 39145, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39038, 5, 0, 'Thanks for everything... father.', 12, 0, 100, 0, 0, 0, 39146, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (39097, 1, 0, 'I, ah...', 12, 0, 100, 0, 0, 0, 39142, 0, 'High Priest Benedictus Voss');
INSERT INTO `creature_text` VALUES (39097, 0, 0, 'Lilian... you\'re... it\'s so nice to see you well.', 12, 0, 100, 0, 0, 0, 39139, 0, 'High Priest Benedictus Voss');
INSERT INTO `creature_text` VALUES (11057, 0, 0, 'Now THAT\'s what I call oomph! I wish all our potions did this!', 12, 0, 100, 1, 0, 0, 44553, 0, 'Dithers');
INSERT INTO `creature_text` VALUES (49337, 0, 0, 'Hello again.', 12, 0, 100, 3, 0, 0, 49455, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 1, 0, 'I know the way to Deathknell. Come with me!', 12, 0, 100, 273, 0, 0, 49456, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 2, 0, 'Good, you\'re still here. Now, where\'s Deathguard Saltain?', 12, 0, 100, 1, 0, 0, 49457, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 3, 0, 'Ah, here he is.', 12, 0, 100, 1, 0, 0, 49458, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 4, 0, 'Let\'s get moving, $n. Saltain said that we\'d fine some corpses up here.', 12, 0, 100, 1, 0, 0, 49459, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 5, 0, 'I think I see some corpses up ahead. Let\'s go, $n! You do the searching and fighting. I\'ll do the lifting.', 12, 0, 100, 0, 0, 0, 49460, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (66168, 0, 0, 'I see. Well then, let\'s get to work, $n! The Dark Lady needs us, right?', 12, 0, 100, 1, 0, 0, 49351, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (66168, 1, 0, 'Valdred Moray, reporting for duty, sir!', 14, 0, 100, 1, 0, 0, 49408, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (66167, 0, 0, 'Who are you calling a monster? You\'re the monster! I\'m just a man who died.', 12, 0, 100, 1, 0, 0, 49345, 0, 'Marshal');
INSERT INTO `creature_text` VALUES (38999, 3, 0, 'Gebler, father, why would you...', 12, 0, 100, 1, 0, 0, 39001, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 2, 0, 'What? NO! This can\'t be! Gebler, you know me... we were friends once!', 12, 0, 100, 1, 0, 0, 38996, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 1, 0, 'Gebler, you came! What did he say?', 12, 0, 100, 1, 0, 0, 38994, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (38999, 0, 0, 'Yes, my... wait, be quiet! I hear the lieutenant approaching.', 12, 0, 100, 0, 0, 0, 39000, 0, 'Lilian Voss');
INSERT INTO `creature_text` VALUES (39002, 3, 0, 'Unfortunately, you were too dangerous in life, and you\'re far too dangerous in undeath. I will enjoy killing you, you Scourged witch...', 12, 0, 100, 1, 0, 0, 38998, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` VALUES (39002, 2, 0, 'The High Priest sends his regrets. He had hoped that one day you would be a powerful weapon against our enemies.', 12, 0, 100, 1, 0, 0, 38997, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` VALUES (39002, 1, 0, 'High Priest Voss denounces you as a daughter. He\'s ordered that you be executed immediately.', 12, 0, 100, 1, 0, 0, 38995, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` VALUES (39002, 0, 0, 'The time has come, my little captive... word has come back from your father.', 12, 0, 100, 0, 0, 0, 38993, 0, 'Scarlet Lieutenant Gebler');
INSERT INTO `creature_text` VALUES (38910, 0, 0, 'You don\'t understand... I CAN\'T be undead! Not me, not now...', 12, 0, 100, 22, 0, 0, 38982, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (1543, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
