INSERT INTO `creature_text` VALUES (60729, 4, 0, 'Ji... if this is what you must do, I will not stop you, but we could lose everything. I hope you\'re right.', 12, 0, 100, 1, 0, 27416, 60194, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (60729, 3, 0, 'Doing THIS risks everything!', 12, 0, 100, 5, 0, 27415, 60192, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (60729, 2, 0, 'If you stop this, then maybe we can come up with one.', 12, 0, 100, 274, 0, 27414, 60190, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (60729, 1, 0, 'Ji, stop! This is reckless and stupid.', 12, 0, 100, 5, 0, 27413, 60188, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (60729, 0, 0, 'Ji may kill Shen-zin Su if we let him go through with this! Come with me, $p, quickly!', 12, 0, 100, 5, 0, 27412, 55140, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (56416, 3, 0, 'We\'re wearing him down.  Keep it up!', 12, 0, 100, 0, 0, 27387, 64852, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT INTO `creature_text` VALUES (56416, 4, 0, 'I don\'t know what I would have done without you.  It\'s good that you came.', 12, 0, 100, 0, 0, 27388, 64853, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT INTO `creature_text` VALUES (65469, 0, 0, 'Such skill for a new trainee.', 12, 0, 100, 511, 0, 0, 56431, 0, 'Aspiring Trainee to Training Target');
