INSERT INTO `creature_text` VALUES (65469, 0, 2, 'One day you will have to teach me your secrets.', 12, 0, 100, 509, 0, 0, 56432, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (65469, 0, 3, 'You are bound for a great things, trainee.', 12, 0, 100, 508, 0, 0, 56435, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (60741, 1, 0, 'So we just wait until we think of something? Shen-zin Su is dying! Doing nothing risks everything!', 12, 0, 100, 6, 0, 27330, 60191, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60741, 0, 0, 'Aysa, this is our only option. We blow the ship free, then we heal his wound. We have no other solution.', 12, 0, 100, 1, 0, 27329, 60189, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60729, 5, 0, 'Aysa sighs.', 16, 0, 100, 274, 0, 0, 60211, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (34574, 3, 0, 'Our immortality-the very essence of our beings!-was lost.', 12, 0, 100, 1, 0, 0, 34794, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34574, 1, 0, 'Much has changed for our people since the Battle of Mount Hyjal.', 12, 0, 100, 1, 0, 0, 34792, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34574, 2, 0, 'Nordrassil lies a pale shadow of what it once was, its power used to defeat Archimonde and drive back the Burning Legion.', 12, 0, 100, 1, 0, 0, 34793, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34574, 0, 0, '%s fades into existence as you approach, nodding a subtle greeting.', 16, 0, 100, 0, 0, 0, 34832, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (49480, 4, 0, 'I\'m going to stay and do what I can to free these spiders. Thank you for your help, $n.', 12, 0, 100, 0, 0, 0, 49549, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 3, 0, 'My dear friends... I\'m sorry..', 12, 0, 100, 0, 0, 0, 49671, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 6, 'The corruption is buried deep within him.', 12, 0, 100, 0, 0, 0, 49669, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 2, 0, 'This totem has been corrupting the eggs! It seems a greater threat looms. The Gnarlpine remain tainted by something most foul.', 12, 0, 100, 0, 0, 0, 49697, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 3, 'Corruption this deep could not have come from outside.', 12, 0, 100, 0, 0, 0, 49675, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 4, 'It almost seems as if he was born with this corruption inside of him.', 12, 0, 100, 0, 0, 0, 49676, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 5, 'They are surely unredeemable, but it still hurts to see them die.', 12, 0, 100, 0, 0, 0, 49670, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 1, 'His body screams for death, but his soul', 12, 0, 100, 0, 0, 0, 49672, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 2, 'I weep at these atrocities we commit. It is almost unbearable.', 12, 0, 100, 0, 0, 0, 49674, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 0, 'The corruption goes much further than these spiders. I fear the true source is not within this cave.', 12, 0, 100, 0, 0, 0, 49673, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 0, 0, 'You\'ve come to help, $c? Let us stay together for a while.', 12, 0, 100, 0, 0, 0, 49550, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (34757, 0, 0, 'Don\'t get me wrong... I\'m grateful to have Shando Stormrage back, but this is all really suspicious. What happened to Fandral?', 12, 0, 100, 0, 0, 0, 35024, 0, 'Doranel Amberleaf');
INSERT INTO `creature_text` VALUES (34756, 0, 0, 'Shh! Someone\'s here.', 12, 0, 100, 0, 0, 0, 35023, 0, 'Moriana Dawnlight');
INSERT INTO `creature_text` VALUES (49479, 3, 0, 'The ramp up to Aldrassil is just in sight over there. Circle around and find Tenaron up top.', 12, 0, 100, 0, 0, 0, 49667, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (2079, 0, 0, 'I\'m sorry, Huntress. None of our new $cs are ready. They still have much to learn.', 12, 0, 100, 0, 0, 0, 49681, 0, 'Ilthalaine');
INSERT INTO `creature_text` VALUES (2079, 1, 0, 'You\'ll find the trainer inside of Aldrassil, $n.', 12, 0, 100, 0, 0, 0, 49664, 0, 'Ilthalaine');
INSERT INTO `creature_text` VALUES (2077, 0, 0, 'You\'ll find the trainer inside of Aldrassil, $n.', 12, 0, 100, 0, 0, 0, 49664, 0, 'Melithar Staghelm');
INSERT INTO `creature_text` VALUES (49477, 0, 0, 'I fear time may not be on our side, Ilthalaine. The battle may carry to Teldrassil itself if we do not act soon.', 12, 0, 100, 0, 0, 0, 49682, 0, 'Huntress Sandrya Moonfall');
INSERT INTO `creature_text` VALUES (49478, 0, 0, 'We will leave you to your training, Ilthalaine. This is your area of expertise.', 12, 0, 100, 0, 0, 0, 49683, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (49478, 1, 0, 'Study well, $n. We may need you fighting by our side before the next moon fills.', 12, 0, 100, 0, 0, 0, 49684, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (49479, 0, 0, 'Iverron\'s poison is cured, but it will take some time for him to recover.', 12, 0, 100, 0, 0, 0, 49668, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (49479, 1, 0, 'Shadowthread Cave lies to the north. Be careful, it\'s dangerous there of late.', 12, 0, 100, 0, 0, 0, 49665, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (49479, 2, 0, 'The moonwell is to the northeast, on the other side of the pool and up the hill.', 12, 0, 100, 0, 0, 0, 49666, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (3438, 0, 0, 'Come and face me! *snort* I can smell your $r stench from here.', 14, 0, 100, 0, 0, 0, 44319, 0, 'Kreenig Snarlsnout');
INSERT INTO `creature_text` VALUES (14872, 0, 0, 'Help! I\'m being chased by a swarm of bees!', 12, 1, 100, 0, 0, 0, 10259, 0, 'Trok');
INSERT INTO `creature_text` VALUES (14872, 0, 1, 'I\'m so tired of running, but these bees won\'t leave me alone!', 12, 1, 100, 0, 0, 0, 10260, 0, 'Trok');
INSERT INTO `creature_text` VALUES (14872, 0, 2, 'I didn\'t know bees didn\'t like fire!', 12, 1, 100, 0, 0, 0, 10261, 0, 'Trok');
INSERT INTO `creature_text` VALUES (14872, 1, 3, 'Karu will you please help get these bees off of me? They sting!', 12, 1, 100, 0, 0, 0, 10262, 0, 'Trok');
INSERT INTO `creature_text` VALUES (22902, 0, 0, 'A %s erupts into the waking world from the Emerald Dream!', 16, 0, 100, 0, 0, 0, 20562, 0, 'Phantasmal Lash');
INSERT INTO `creature_text` VALUES (22889, 0, 0, 'A %s erupts into the waking world from the Emerald Dream!', 16, 0, 100, 0, 0, 0, 20562, 0, 'Manifest Nightmare');
INSERT INTO `creature_text` VALUES (54067, 0, 0, 'This is great!', 12, 0, 100, 0, 0, 0, 52697, 0, 'Jaga');
INSERT INTO `creature_text` VALUES (54067, 0, 1, 'Thanks, $p! I\'ll tell my pa you helped me see the world!', 12, 0, 100, 0, 0, 0, 52696, 0, 'Jaga');
INSERT INTO `creature_text` VALUES (54067, 0, 2, 'Wahoo!', 12, 0, 100, 0, 0, 0, 44157, 0, 'Jaga');
INSERT INTO `creature_text` VALUES (34586, 0, 0, '%s smiles at you knowingly as it approaches.', 16, 0, 100, 0, 0, 0, 34835, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34586, 1, 0, 'With Malfurion Stormrage gone, the druids were left in disarray and in desperate need of a new leader.', 12, 0, 100, 1, 0, 0, 34797, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34586, 2, 0, 'Arch Druid Fandral Staghelm roused the druids and convinced the Circle of Ancients in Darkshore that it was time for our people to rebuild.', 12, 0, 100, 1, 0, 0, 34798, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34586, 3, 0, 'In an effort to regain immortality for our people, and with the approval of the Circle, Staghelm and the most powerful druids grew Teldrassil, upon whose boughs you now stand.', 12, 0, 100, 1, 0, 0, 34799, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34586, 4, 0, 'Though we have our new home, our immortal lives have not been restored.', 12, 0, 100, 1, 0, 0, 34800, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34586, 5, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 0, 0, '%s stares into the moonwell, lost in distant memories.', 16, 0, 100, 0, 0, 0, 34819, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 1, 0, 'The Arch Druid approached the dragons for their blessings, as the dragons had placed on Nordrassil in ancient times.', 12, 0, 100, 1, 0, 0, 34801, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 2, 0, 'But Nozdormu, Lord of Time, refused to give his blessing, chiding the druid for his arrogance.', 12, 0, 100, 1, 0, 0, 34802, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 3, 0, 'In agreement, Alexstrasza also refused Staghelm, and without her blessing, Teldrassil\'s growth has been flawed and unpredictable.', 12, 0, 100, 1, 0, 0, 34803, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 4, 0, 'Staghelm retreated to his enclave in Darnassus, ever seeking a new direction by which to bless Teldrassil, and restore the immortality of our people.', 12, 0, 100, 1, 0, 0, 34804, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34583, 5, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (2081, 0, 0, 'Some of our huntresses have already made their way to Ban\'ethil Barrow Den. They will find you when you get inside. Go ahead and speak to Citarre to secure a ride there.', 12, 0, 100, 0, 0, 0, 34694, 0, 'Sentinel Kyra Starsong');
INSERT INTO `creature_text` VALUES (34249, 0, 0, 'You\'re finally here. Take the scenic route?', 12, 0, 100, 0, 0, 0, 34985, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 1, 0, 'The exit is to the northwest... the mist will lead the way.', 15, 0, 100, 0, 0, 0, 34669, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 2, 0, 'The mist will lead the way.', 15, 0, 100, 0, 0, 0, 34668, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 3, 0, 'The Rune of Nesting is located across the bridge in the upper section of the southern chamber.', 15, 0, 100, 0, 0, 0, 34650, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 4, 0, 'The Sapphire of Sky is located at the separate room in the lower section of the northern chamber.', 15, 0, 100, 0, 0, 0, 34651, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 5, 0, 'The Black Feather Quill is located down the ramp in the lower section of the southern chamber.', 15, 0, 100, 0, 0, 0, 34652, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 6, 0, 'The Raven Claw Talisman is located on the ledge in the upper section of the northern chamber.', 15, 0, 100, 0, 0, 0, 34653, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 7, 0, 'Oben Rageclaw resides at the double bridges in the western chamber, near the hallway that leads out of the Barrow Den.', 15, 0, 100, 0, 0, 0, 34654, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34584, 0, 0, '%s approaches the moonwell slowly, a familiar, thoughtful look upon its face.', 16, 0, 100, 0, 0, 0, 34834, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 1, 0, 'Over time, Staghelm only grew more disconnected from our people.', 12, 0, 100, 1, 0, 0, 34805, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 2, 0, 'Some pose that he blames us, his people, for the loss of his son, long ago during the War of the Shifting Sands.', 12, 0, 100, 1, 0, 0, 34806, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 3, 0, 'Others cite rumors of his escalating conflicts with Tyrande Whisperwind, the shining light of our people, our leader in Darnassus.', 12, 0, 100, 1, 0, 0, 34807, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 4, 0, 'But many druids of the Cenarion Circle stand firm that he wants only what is best for all of the Kaldorei, and serve him unerringly towards that end.', 12, 0, 100, 1, 0, 0, 34808, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 5, 0, 'Fandral Staghelm carries the burden of our sins and our follies, and in so doing deserves the respect of us all.', 12, 0, 100, 1, 0, 0, 34809, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34584, 6, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 66, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 0, 0, '%s rests on the side of the moonwell in peaceful contemplation.', 16, 0, 100, 0, 0, 0, 34833, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 1, 0, 'In weeks past, all of those living atop Teldrassil have found their memories blurry, as if time itself seized a portion of their lives.', 12, 0, 100, 1, 0, 0, 34810, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 2, 0, 'We break from this haze to find that Fandral Staghelm and many of his closest followers have disappeared without a word.', 12, 0, 100, 1, 0, 0, 34811, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 3, 0, 'But in his place, Shan\'do Stormrage awoke from the dream and returned to his beloved Tyrande Whisperwind.', 12, 0, 100, 1, 0, 0, 34812, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 4, 0, 'And with his return came Alexstrasza and Ysera, to at long last offer their blessings upon Teldrassil', 12, 0, 100, 1, 0, 0, 34813, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 5, 0, 'Though a small corruption lingers, desperately longing to be cleansed, the boughs of Teldrassil itself seem changed. Growth has begun anew.', 12, 0, 100, 1, 0, 0, 34814, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 6, 0, 'None know what to expect next, but with Malfurion and Tyrande reunited, a new hope dawns on the Kaldorei.', 12, 0, 100, 1, 0, 0, 34815, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 7, 0, 'Hope for lasting relief from the shadow that has been cast on our people for so long.', 12, 0, 100, 1, 0, 0, 44123, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (34585, 8, 0, 'The Shade of the Kaldorei closes its eyes and fades away.', 16, 0, 100, 0, 0, 0, 34820, 0, 'Shade of the Kaldorei');
INSERT INTO `creature_text` VALUES (10382, 0, 0, '%s collapses but the broken body rises again!', 16, 0, 100, 0, 0, 0, 5772, 0, 'Mangled Cadaver');
INSERT INTO `creature_text` VALUES (11142, 0, 0, 'No tampering with the mail!', 12, 0, 100, 0, 0, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (32331, 1, 0, 'Whoa! You\'re me, but from the future! Hey, my equipment got an upgrade! Cool!', 15, 0, 100, 396, 0, 0, 32764, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 2, 0, 'Here come the Infinites! I\'ve got to keep the hourglass safe. Can you help?', 15, 0, 100, 396, 0, 0, 32769, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 3, 0, 'This equipment looks cool and all, but couldn\'t we have done a little better? Are you even raiding?', 15, 0, 100, 396, 0, 0, 32770, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 4, 0, 'So, how does it all turn out in Icecrown?', 15, 0, 100, 396, 0, 0, 32771, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 5, 0, 'It might help if you could tell me how many of these guys we\'re going to fight.  You can remember that, right, grandpa?', 15, 0, 100, 396, 0, 0, 32775, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 6, 0, 'Chromie said that if I don\'t do this just right, I might wink out of existence. If I go, then you go!', 15, 0, 100, 396, 0, 0, 32772, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 7, 0, 'I just want you to know that if we get through this alive, I\'m making sure that we turn out better than you. No offense.', 15, 0, 100, 396, 0, 0, 32776, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 8, 0, 'Looks like I\'m an underachiever.', 15, 0, 100, 396, 0, 0, 32773, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 9, 0, 'Wait a minute! If you\'re here, then that means that in the not-so-distant future I\'m going to be you helping me? Are we stuck in a time loop?!', 15, 0, 100, 396, 0, 0, 32777, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 10, 0, 'I think I\'m going to turn to drinking after this.', 15, 0, 100, 396, 0, 0, 32774, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 11, 0, 'What the heck? Nozdormu is up there!', 15, 0, 100, 396, 0, 0, 32781, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 12, 0, 'I feel like I\'m being pulled away through time. Thanks for the help....', 15, 0, 100, 396, 0, 0, 32782, 0, 'Past You');
INSERT INTO `creature_text` VALUES (19622, 14, 0, '%s begins to cast Pyroblast!', 41, 0, 100, 0, 0, 0, 20775, 0, 'kaelthas EMOTE_PYROBLAST');
INSERT INTO `creature_text` VALUES (34965, 0, 0, 'Foolish $c, you cannot stop the Kvaldir!', 12, 0, 100, 0, 0, 0, 35393, 0, 'Mistcaller Yngvar');
INSERT INTO `creature_text` VALUES (30107, 0, 0, 'I can\'t thank you enough for all of your help in putting together the keystone. Great things will come of this, I assure you.', 12, 0, 100, 0, 0, 0, 31023, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 1, 0, 'Iron dwarves... everywhere.... they\'re making their way down from the top. They certainly are persistent.', 12, 0, 100, 0, 0, 0, 31024, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 2, 0, 'They\'re trying to take down the plane! I can\'t pull up any steeper... you need to keep them off of us!', 12, 0, 100, 0, 0, 0, 31025, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 3, 0, 'There are more just ahead - keep at it!', 12, 0, 100, 0, 0, 0, 31026, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 4, 0, 'We\'re almost out... just a little bit further.', 12, 0, 100, 0, 0, 0, 31027, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 5, 0, 'Barring stray boulders from those giants, we should be clear... it seems a mess from down there though.', 12, 0, 100, 0, 0, 0, 31028, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 6, 0, 'Their numbers are unbelievable... I\'m glad to hear King Stormheart came to your aid. I only hear good things of him - I think it\'s high time we meet.', 12, 0, 100, 0, 0, 0, 31029, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 7, 0, 'Poor Creteus. He was a good keeper... I\'m glad he at least got to see his task to completion, I imagine that\'s all that really mattered to him.', 12, 0, 100, 0, 0, 0, 31030, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30107, 8, 0, 'We\'re coming up on Frosthold. I would be very appreciative if you would introduce me to King Stormeheart before you go. I believe we are both very much in his debt.', 12, 0, 100, 0, 0, 0, 31031, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 0, 0, 'By all the gods... it can\'t be... Muradin?', 12, 0, 100, 0, 0, 0, 31032, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 1, 0, 'Come on boy, there\'s no mistak\'n it - it\'s definately you. Don\'t ya recognize your younger brother?', 12, 0, 100, 0, 0, 0, 31033, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 2, 0, 'I can\'t believe this! You were dead! All accounts said so... what happened, Muradin. How did you get here?', 12, 0, 100, 0, 0, 0, 31034, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 3, 0, 'Indeed! Magni will be so happy to see you too! He\'s gotten nothing but bad news for a long time now, but this changes everything!', 12, 0, 100, 0, 0, 0, 31035, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 4, 0, 'He\'s here in Northrend, brother, looking for you. A seer in Wintergarde brought word that you were not dead, and he left Ironforge immediately to come find you.', 12, 0, 100, 0, 0, 0, 31036, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 5, 0, 'Speaking of which...', 12, 0, 100, 0, 0, 0, 31087, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 6, 0, 'That\'s in the past, Muradin. Regrets won\'t change anything.', 12, 0, 100, 0, 0, 0, 31096, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 7, 0, 'Aye, be safe Muradin. I\'d join you, but I\'m on top of the most amazing discovery the world has yet seen. I can\'t abandon it now.', 12, 0, 100, 0, 0, 0, 31102, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30408, 0, 0, 'What\'s that? You talkin\' to me, lad?', 12, 0, 100, 0, 0, 0, 31037, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 1, 0, 'My brother... yes... I do have brothers...', 12, 0, 100, 0, 0, 0, 31038, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 2, 0, 'Muradin clutches his head and reels for a moment as the memories rush back to him.', 16, 0, 100, 0, 0, 0, 31039, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 3, 0, '...Brann?', 12, 0, 100, 0, 0, 0, 31040, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 4, 0, 'I... I dunno, Brann. I\'ve been \'ere a long time... all I \'ave of me life before this place are flashes and nightmares.', 12, 0, 100, 0, 0, 0, 31042, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 5, 0, 'It\'s good te see you though, brother. More than words can say.', 12, 0, 100, 0, 0, 0, 31043, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 6, 0, 'Magn! Forgive me, the memories are comin\' back slowly, brother.', 12, 0, 100, 0, 0, 0, 31045, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 7, 0, 'The frostborn have been very good to me. They\'re strong people.', 12, 0, 100, 0, 0, 0, 31048, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 8, 0, 'Not much, Magni. I\'ve had nightmares of a human... tall... light hair... death black armor. His name rests on the tip of me tongue, but...', 12, 0, 100, 0, 0, 0, 31089, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 9, 0, '...Arthas.', 12, 0, 100, 0, 0, 0, 31091, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 10, 0, 'Aye, I know. I watched him turn... I watched him give up all that was right and I didn\'t lift a hand... I didn\'t even consider it until it was too late.', 12, 0, 100, 0, 0, 0, 31094, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 11, 0, 'No... no they won\'t. But I can make this right. I have te. I\'m goin\' after the boy. I\'ll make\'m answer for everything he\'s done.', 12, 0, 100, 0, 0, 0, 31097, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 12, 0, 'I\'m sure, Magni. I\'ll see this through, don\'t ya worry.', 12, 0, 100, 0, 0, 0, 31100, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 13, 0, 'Go Brann. Bring back an epic tale when we meet again. Farewell for now, brothers...', 12, 0, 100, 0, 0, 0, 31103, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30411, 0, 0, 'Look, Lagnus, I consider you a capable man, but my patience is wearing thin. I know that Muradin is here, can you point me to him or not?', 14, 0, 100, 0, 0, 0, 31084, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 1, 0, 'Brother! There you are! I can barely believe my eyes... you\'re alive!', 14, 0, 100, 0, 0, 0, 31085, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 2, 0, 'It\'s so good to see you again, Muradin. And what\'s this I heard about you being a King in your own right now? The Bronzebeards were always destined to greatness.', 12, 0, 100, 0, 0, 0, 31086, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 3, 0, 'So it seems! And you haven\'t lost any muscle yourself. Do you remember anything of what happened, Muradin? Fate as turned ill in your absence.', 12, 0, 100, 0, 0, 0, 31088, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 4, 0, 'Magni nods.', 16, 0, 100, 0, 0, 0, 31092, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 5, 0, 'He\'s not the boy of your memories anymore, Muradin. He\'s become something else entirely.', 12, 0, 100, 0, 0, 0, 31093, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 6, 0, 'Are you sure Muradin? I just got you back after years of thinking you were dead. I do not want to lose you again.', 12, 0, 100, 0, 0, 0, 31099, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 7, 0, 'So be it then. I have to return to my people, brothers. Come back to me in one piece.', 12, 0, 100, 0, 0, 0, 31101, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 8, 0, '...farewell brother.', 12, 0, 100, 0, 0, 0, 31129, 0, 'Magni');
INSERT INTO `creature_text` VALUES (24838, 0, 0, 'Land ho, Mr. Kowalski! Port is in sight, sir!', 12, 7, 100, 0, 0, 0, 23941, 0, 'Sailor Henders');
INSERT INTO `creature_text` VALUES (24835, 0, 0, 'Thank you, Mr. Henders.', 12, 7, 100, 0, 0, 0, 23942, 0, 'First Mate Kowalski');
INSERT INTO `creature_text` VALUES (24835, 1, 0, 'Bring us into port, please, Mr. Mehran.', 12, 7, 100, 0, 0, 0, 23943, 0, 'First Mate Kowalski');
INSERT INTO `creature_text` VALUES (24837, 0, 0, 'Aye aye, Mr. Kowalski.', 12, 0, 100, 0, 0, 0, 23951, 0, 'Navigator Mehran');
INSERT INTO `creature_text` VALUES (24835, 2, 0, 'Captain Stash! We\'ll be arriving shortly, sir!', 12, 7, 100, 0, 0, 0, 23944, 0, 'First Mate Kowalski');
INSERT INTO `creature_text` VALUES (24833, 0, 0, 'Thank you, Mr. Kowalski! You may return to your post to oversee docking!', 12, 7, 100, 0, 0, 0, 23945, 0, 'Captain \"Stash\" Torgoley');
INSERT INTO `creature_text` VALUES (24833, 1, 0, 'Well done, all. Please bring us safely to our next port. ', 12, 7, 100, 0, 0, 0, 23946, 0, 'Captain \"Stash\" Torgoley');
