INSERT INTO `creature_text` VALUES (28788, 0, 0, 'Impressive, death knight. Return to me in the world of the living for your reward.', 12, 0, 100, 0, 0, 0, 28835, 0, 'SALANAR_SAY');
INSERT INTO `creature_text` VALUES (24314, 0, 0, 'So then we too are cursed?', 12, 0, 100, 1, 0, 0, 23116, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24314, 1, 0, '%s points to the infant.', 16, 0, 100, 0, 0, 0, 23117, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24315, 0, 0, '%s sobs.', 16, 0, 100, 0, 0, 0, 23118, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24314, 2, 0, 'The gods have forsaken us! We must dispose of it before Ymiron is notified!', 12, 0, 100, 53, 0, 0, 23119, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24315, 1, 0, 'NO! You cannot! I beg of you! It is our child!', 12, 0, 100, 0, 0, 0, 23120, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24314, 3, 0, 'Then what are we to do, wife? The others cannot find out. Should they learn of this aberration, we will all be executed.', 12, 0, 100, 1, 0, 0, 23121, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24315, 2, 0, 'I... I will hide it. I will hide it until I find it a home, far away from here...', 12, 0, 100, 0, 0, 0, 23122, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24248, 0, 0, '%s turns to face you.', 16, 0, 100, 0, 0, 0, 23102, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 1, 0, '%s grins. ', 16, 0, 100, 11, 0, 14820, 23146, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 2, 0, 'Shamanism has brought you here... Its scent permeates the air. *The Lich King laughs* I was once a shaman.', 12, 0, 100, 0, 0, 14742, 23147, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24327, 0, 0, 'Shall we prepare it for you, my lord?', 12, 0, 100, 0, 0, 0, 23152, 0, 'Val\'kyr Soulclaimer');
INSERT INTO `creature_text` VALUES (24248, 3, 0, 'No, minion. This one is not ready.', 12, 0, 100, 274, 0, 14743, 23148, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 4, 0, 'Do you feel it, mortal? Death seeps through me, enveloping all that I touch. With just a snap of my finger your soul will languish in damnation for all eternity.', 12, 0, 100, 1, 0, 14744, 23149, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 5, 0, 'But... It is not yet your time to serve the Lich King. Yes, a greater destiny awaits you. Power... You must become more powerful before you are to serve me.', 12, 0, 100, 1, 0, 14745, 23150, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 6, 0, 'Now watch, val\'kyr. Observe as I apply pressure. Can you see that it is not yet ripe? Watch as it pops and falls lifeless to the floor.', 12, 0, 100, 1, 0, 14746, 23151, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 7, 0, 'Persistence or stupidity? It matters not. Let this be a lesson learned, mortal!', 12, 0, 100, 1, 0, 14747, 27351, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (22431, 0, 0, 'It is time. The rite of exorcism will now commence...', 12, 0, 100, 0, 0, 0, 20400, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 1, 0, 'Prepare yourself. Do not allow the ritual to be interrupted or we may lose our patient...', 12, 0, 100, 0, 0, 0, 20401, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 2, 0, 'Back foul beings of darkness! You have no power here!', 12, 0, 100, 0, 0, 0, 20404, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 3, 0, 'I... must not... falter', 12, 0, 100, 0, 0, 0, 20407, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 4, 0, 'The Light is my guide... it is my sustenance!', 12, 0, 100, 0, 0, 0, 20408, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 5, 0, 'The power of light compells you! Back to your pit!', 12, 0, 100, 0, 0, 0, 20405, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 6, 0, 'Be cleansed with Light, human! Let not the demonic corruption overwhelm you.', 12, 0, 100, 0, 0, 0, 20403, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 7, 0, 'Back! I cast you back... corruptor of faith! Author of pain! Do not return, or suffer the same fate as you did here today!', 12, 0, 100, 0, 0, 0, 20419, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22432, 0, 0, 'Keep away. The fool is mine.', 12, 0, 100, 0, 0, 0, 20402, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 1, 0, 'Ah! Cease the incantations, Anchorite! Cease, or I will show you such pain that your pathetic people have never imagined!', 12, 0, 100, 0, 0, 0, 20414, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 2, 0, 'This is fruitless, draenei! You and your little helper cannot wrest control of this pathetic human. He is mine!', 12, 0, 100, 0, 0, 0, 20416, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 3, 0, 'I see your ancestors, Anchorite! They writhe and scream in the darkness... they are with us!', 12, 0, 100, 0, 0, 0, 20415, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 4, 0, 'I will tear your soul into morsels and slow roast them over demon fire.', 12, 0, 100, 0, 0, 0, 20417, 0, 'Colonel Jules');
