INSERT INTO `creature_text` VALUES (25784, 0, 0, 'I thank you, friend of Dalaran.', 12, 0, 100, 0, 0, 0, 24995, 0, 'Archmage Evanor');
INSERT INTO `creature_text` VALUES (25784, 1, 0, 'We mustn\'t tarry here... stay close - I\'ll take us somewhere safe.', 12, 0, 100, 0, 0, 0, 24996, 0, 'Archmage Evanor');
