INSERT INTO `creature_text` VALUES (26860, 0, 0, 'I\'ve never seen you so eager to cause harm to someone, sister.  Such a pleasant development... very well, go!', 12, 0, 100, 25, 0, 0, 26229, 0, 'Conqueror Krenna to Player');
INSERT INTO `creature_text` VALUES (26860, 1, 0, 'That spineless troll has failed me for the last time.  Anthis!  Break his legs!', 12, 0, 100, 53, 0, 0, 26227, 0, 'Conqueror Krenna to Player');
