INSERT INTO `creature_text` VALUES (27102, 0, 0, 'Let me take care of it, sister.', 12, 0, 100, 0, 0, 0, 27108, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27102, 1, 0, 'What were you thinking, provoking her like that?', 15, 0, 100, 0, 0, 0, 27109, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27102, 2, 0, 'I used good judgment when you showed none.  I don\'t want to see you lead us into ruin, Krenna!', 12, 0, 100, 0, 0, 0, 27027, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27102, 3, 0, 'We\'ll see about that.', 12, 0, 100, 0, 0, 0, 27028, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27102, 4, 0, 'You... half-idiot!  You really did it this time.  I\'m going to have to make it look like your leg\'s broken.', 12, 0, 100, 0, 0, 0, 27110, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27102, 5, 0, 'Now don\'t move if you ever want to walk again!', 12, 0, 100, 0, 0, 0, 27111, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` VALUES (27037, 0, 0, 'No, please!  Don\'t!', 12, 0, 100, 430, 0, 0, 27112, 0, 'Hidetrader Jun\'ik to Player');
