INSERT INTO `creature_text` VALUES (1378, 0, 0, 'C\'mon, let\'s get you another ale.', 12, 0, 100, 5, 0, 0, 43204, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` VALUES (5595, 0, 0, 'Glad to see that Dark Iron scum is finally getting what he deserves!', 12, 0, 100, 25, 0, 0, 42039, 0, 'Ironforge Guard to Ambassador Slaghammer');
INSERT INTO `creature_text` VALUES (5595, 1, 0, 'Quiet! Moira\'s in a foul mood and if she hears you, she\'ll have your head...', 12, 0, 100, 5, 0, 0, 42040, 0, 'Ironforge Guard to Ambassador Slaghammer');
INSERT INTO `creature_text` VALUES (5595, 2, 0, 'I\'ll escort you to your new \'accommodations\', Your Excellency.', 12, 0, 100, 1, 0, 0, 42048, 0, 'Ironforge Guard to Arrest the Ambassador Credit');
INSERT INTO `creature_text` VALUES (1263, 1, 0, 'Come back, Fluffy!', 12, 6, 100, 0, 0, 0, 159, 0, 'Yarlyn Amberstill');
INSERT INTO `creature_text` VALUES (46185, 0, 0, 'Commencing decontamination sequence...', 12, 0, 100, 0, 0, 0, 46323, 0, NULL);
INSERT INTO `creature_text` VALUES (46185, 1, 0, 'Decontamination complete. Standby for delivery.', 12, 0, 100, 0, 0, 0, 46324, 0, NULL);
INSERT INTO `creature_text` VALUES (46185, 2, 0, 'Warning, system overload. Malfunction imminent!', 12, 0, 100, 0, 0, 0, 46325, 0, NULL);
INSERT INTO `creature_text` VALUES (46230, 0, 0, 'Ugh! Not this again! I\'m asking for a new station next expedition...', 12, 0, 100, 0, 0, 0, 46342, 0, NULL);
INSERT INTO `creature_text` VALUES (47836, 0, 0, 'You can follow me to the Loading Room, $N', 12, 0, 100, 0, 0, 0, 48091, 0, '');
INSERT INTO `creature_text` VALUES (60741, 2, 0, 'I\'d rather die knowing that at least we tried.', 12, 0, 100, 1, 0, 27331, 60193, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60851, 0, 0, 'We\'ll cover this front. You keep my priests safe.', 12, 0, 100, 396, 0, 27503, 59720, 0, 'Delora Lionheart to Player');
INSERT INTO `creature_text` VALUES (60852, 0, 0, 'We stand by your side, friend.  This battle is not yours alone.', 12, 0, 100, 6, 0, 28119, 59719, 0, 'Korga Strongmane to Player');
INSERT INTO `creature_text` VALUES (60770, 0, 0, 'Thank you for the help. I\'ll go help with the wound.', 12, 0, 100, 396, 0, 0, 60248, 0, 'Horde Druid to Player');
INSERT INTO `creature_text` VALUES (60770, 0, 1, 'I\'m heading to the wound. Try to keep these beasts off of me.', 12, 0, 100, 396, 0, 0, 60342, 0, 'Horde Druid to Player');
INSERT INTO `creature_text` VALUES (56661, 0, 0, '...Ji, they were in the Wood of Staves.  You know where Master Shang is now.', 12, 0, 100, 1, 0, 27431, 55555, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (56661, 1, 0, 'Ji, be respectful when we speak to Shen-zin Su.', 12, 0, 100, 1, 0, 27432, 55560, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (56661, 2, 0, 'I might if you embarrass us.', 12, 0, 100, 1, 0, 27433, 55562, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Shen-zin Su, we are the descendants of Liu Lang.  We\'ve sensed your pain, and we want to help.', 14, 0, 100, 1, 0, 27434, 55563, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (64881, 4, 0, 'Hey!', 12, 0, 100, 1, 0, 0, 65642, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 5, 0, 'Yeah, you should all be more like me.', 12, 0, 100, 1, 0, 0, 65643, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 6, 0, 'Does that mean our moms and dads and grandparents were all explorers, too?', 12, 0, 100, 1, 0, 0, 65648, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 7, 0, 'I want to explore the world!', 12, 0, 100, 1, 0, 0, 65651, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 8, 0, 'Oh. Well, forget it then.', 12, 0, 100, 1, 0, 0, 65653, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 9, 0, 'What?', 12, 0, 100, 1, 0, 0, 65655, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 10, 0, 'That\'s un-possible!', 12, 0, 100, 1, 0, 0, 65657, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 0, 0, 'What is the song about?', 12, 0, 100, 1, 0, 0, 65634, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 1, 0, 'Sheesh, Hao. Haven\'t you ever paid attention?', 12, 0, 100, 1, 0, 0, 65638, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 2, 0, 'Even Hao?', 12, 0, 100, 6, 0, 0, 65641, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 3, 0, 'Blerch! I\'d rather jump off this bridge.', 12, 0, 100, 1, 0, 0, 65644, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 4, 0, 'Because sea turtles always return to the beach where they were born!', 12, 0, 100, 1, 0, 0, 65647, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 5, 0, 'Like Chen and Li Li Stormstout!', 12, 0, 100, 1, 0, 0, 65650, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 6, 0, 'No you don\'t. It\'s full of trolls.', 12, 0, 100, 1, 0, 0, 65652, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64880, 7, 0, 'What? What?', 12, 0, 100, 1, 0, 0, 65656, 0, 'Nan to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 0, 0, 'Shen-zin Su!', 12, 0, 100, 1, 0, 0, 65635, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 1, 0, '...Whenever he got hungry?', 12, 0, 100, 1, 0, 0, 65645, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 2, 0, 'But isn\'t Pandaria hidden somewhere? Nobody can find it!', 12, 0, 100, 1, 0, 0, 65646, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 3, 0, 'But we don\'t explore very much anymore, do we?', 12, 0, 100, 1, 0, 0, 65649, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 4, 0, 'Lorewalker! What ever happened to Liu Lang?', 12, 0, 100, 1, 0, 0, 65654, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64879, 5, 0, 'That\'s a sad story!', 12, 0, 100, 1, 0, 0, 65658, 0, 'Yin to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (57670, 0, 0, 'These are the densest stones across all of Shen-zin Su, yet they are as nothing before my mighty head.', 12, 0, 100, 1, 0, 0, 56355, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (57670, 1, 0, 'Any who yet doubt my strength are simply foolish.', 12, 0, 100, 2, 0, 0, 56356, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (65558, 0, 0, 'Let\'s tackle this one together!', 12, 0, 100, 5, 0, 27320, 60609, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 0, 'I like the way you fight!', 12, 0, 100, 5, 0, 27322, 60610, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 1, 'He had that one coming.', 12, 0, 100, 273, 0, 27323, 60611, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 2, 'Ouch! That one looked painful!', 12, 0, 100, 5, 0, 27324, 60612, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 3, 'You make this look easy.', 12, 0, 100, 1, 0, 27325, 60613, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 4, 'They never stood a chance.', 12, 0, 100, 1, 0, 27326, 60614, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 5, 'You\'ve got this handled.', 12, 0, 100, 273, 0, 27327, 60615, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 1, 6, 'I need to fight by you more often.', 12, 0, 100, 1, 0, 27328, 60616, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 2, 0, 'Let\'s do this, monkeyface!', 12, 0, 100, 5, 0, 27318, 60619, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 3, 0, 'That all you\'ve got?!', 12, 0, 100, 5, 0, 27316, 60620, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 4, 0, 'This monkey\'s about to get slapped!', 12, 0, 100, 5, 0, 27317, 60621, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 5, 0, 'And he\'s down for the count! Good going.', 12, 0, 100, 5, 0, 27315, 60622, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (65558, 6, 0, 'See you at the dock!', 12, 0, 100, 0, 0, 27319, 60618, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (57692, 0, 0, 'This is magnificent!  I\'ve never seen craftsmanship so fine.', 12, 0, 100, 1, 0, 0, 56363, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (57692, 1, 0, 'There is only one thing to do.  I must smash it!  Once and for all, all shall know the power of my mighty brow.', 12, 0, 100, 1, 0, 0, 56361, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (57692, 2, 0, 'Oh... my head... it hurts so badly!  I\'ve... the pillar must contain enormous power... perhaps stronger even than my brow...', 12, 0, 100, 18, 0, 0, 56362, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (57692, 3, 0, 'I\'m... I\'m just going to lie down a while... until this headache goes away.', 12, 0, 100, 0, 0, 0, 56364, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (55744, 0, 0, 'Wait!', 12, 0, 100, 5, 0, 27400, 54355, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (55744, 1, 0, 'We need to wait for the winds to settle, then make a break for the cover of the far hallway.', 12, 0, 100, 1, 0, 27401, 54356, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (55744, 2, 0, 'Wait for another opening. I\'ll meet you on the far side.', 12, 0, 100, 1, 0, 27402, 54357, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (55595, 0, 0, 'Dafeng! What\'s wrong? Why are you hiding back here?', 12, 0, 100, 1, 0, 27403, 54362, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (64506, 0, 0, 'That\'s the way to do it!', 12, 0, 100, 0, 0, 27380, 64766, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT INTO `creature_text` VALUES (64506, 1, 0, 'You\'ve done it!  Now quickly, lets attack it while it\'s grounded.', 12, 0, 100, 0, 0, 27381, 64767, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT INTO `creature_text` VALUES (64506, 2, 0, 'It\'s down!  Let\'s finish it off.', 12, 0, 100, 0, 0, 27382, 64768, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT INTO `creature_text` VALUES (56159, 0, 0, 'Come child. We have one final journey to take together before your training is complete.', 12, 0, 100, 1, 0, 27797, 55520, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56159, 1, 0, 'Beyond the Elders\' Path lies the Wood of Staves, a sacred place that only the worthy may enter.', 12, 0, 100, 1, 0, 27800, 55521, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56159, 2, 0, 'Of the many ways to prove your worth, I require the simplest of you now. I must know that you will fight for our people. I must know that you can keep them safe.', 12, 0, 100, 1, 0, 27801, 55522, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56159, 3, 0, 'Defeat the Guardian of the Elders, and we may pass.', 12, 0, 100, 1, 0, 27802, 55523, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56159, 4, 0, 'You\'ve become strong indeed, child. This is good. You will need that strength soon.', 12, 0, 100, 1, 0, 27803, 56484, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (55672, 0, 0, 'And here we are. Help me with a couple small tasks while I prepare, if you would.', 12, 0, 100, 1, 0, 27805, 56485, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 0, 0, 'For 3,000 years, we have passed the knowledge of our people down. Elder to youth. Master to student.', 12, 0, 100, 1, 0, 27807, 55528, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 1, 0, 'Every elder reaches the day where he must pass on and plant his stave with the staves of his ancestors. Today is the day when my staff joins these woods.', 12, 0, 100, 1, 0, 27808, 55529, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 2, 0, '$p, our people have lived the wholes of their lives on this great turtle, Shen-zin Su, but not in hundreds of years has anyone spoken to him.', 12, 0, 100, 1, 0, 27809, 55530, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 3, 0, 'Now Shen-zin Su is ill, and we are all in danger. With the help of the elements, you will break the silence. You will speak to him.', 12, 0, 100, 1, 0, 27810, 55531, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 4, 0, 'Aysa and Ji have retrieved the spirits and brought them here. You are to go with them, speak to the great Shen-zin Su, and do what must be done to save our people.', 12, 0, 100, 1, 0, 27811, 55532, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56686, 5, 0, 'You\'ve come far, my young student. I see within you a great hero. I leave the fate of this land to you.', 12, 0, 100, 396, 0, 27812, 55533, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (56662, 0, 0, 'We\'re ready to go whenever you are, $n.', 12, 0, 100, 1, 0, 27428, 55549, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (56662, 1, 0, 'Don\'t listen to him.  We\'ve got work to do.', 12, 0, 100, 1, 0, 27430, 55553, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (56662, 2, 0, 'Hop in.', 12, 0, 100, 1, 0, 27429, 55551, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (56663, 0, 0, 'Or if you want to wander the woods a while, Aysa and I can hang out here.  You know... just the two of us.', 12, 0, 100, 1, 0, 27296, 55552, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (56660, 0, 0, '$p, where\'s Master Shang?', 12, 0, 100, 6, 0, 27297, 55554, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 1, 0, 'Bah, let a pandaren hope, would you?  I\'m going to miss the old man.', 12, 0, 100, 1, 0, 27298, 55556, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 2, 0, 'When am I not respectful?  You hurt me, Aysa.', 12, 0, 100, 6, 0, 27299, 55561, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'A thorn?  And I left my tweezers at home.', 12, 0, 100, 6, 0, 27300, 55574, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 4, 0, 'How could such a thing cause pain to something so large?', 12, 0, 100, 6, 0, 27301, 55575, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 5, 0, 'Are you seeing what I\'m seeing?!  Is that a boat?!', 12, 0, 100, 5, 0, 27302, 55577, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56660, 6, 0, 'And those aren\'t pandaren down there.  They\'ve got no fur.', 12, 0, 100, 1, 0, 27303, 55579, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (64881, 3, 0, 'Does the turtle know we\'re here?', 12, 0, 100, 1, 0, 0, 65640, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 2, 0, 'I just thought it was weird that the mountains had flippers!', 12, 0, 100, 1, 0, 0, 65639, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 1, 0, 'We live on the back of a big turtle?!', 12, 0, 100, 5, 0, 0, 65637, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64881, 0, 0, 'Why can\'t I understand any of the words?', 12, 0, 100, 1, 0, 0, 65631, 0, 'Hao to Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 38, 0, 'Thank you, Ruolin! That was beautiful.', 12, 0, 100, 21, 0, 0, 65621, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 39, 0, 'Could you sing it once more, for the students who just arrived?', 12, 0, 100, 6, 0, 0, 65622, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 37, 0, 'We tell his story, so that he will always be remembered.', 12, 0, 100, 2, 0, 0, 65620, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 28, 0, 'Tired now, Liu Lang said goodbye to Shen-zin Su, his oldest friend.', 12, 0, 100, 1, 0, 0, 65611, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 29, 0, 'Then, Liu Lang went up to the Wood of Staves, carrying with him a bamboo umbrella that he always took on his adventures.', 12, 0, 100, 1, 0, 0, 65612, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 30, 0, 'He opened his umbrella, planted it in the ground, and sat underneath its cool shade.', 12, 0, 100, 6, 0, 0, 65613, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 31, 0, 'He closed his eyes, and became one with the land. And then - do you know what happened?', 12, 0, 100, 2, 0, 0, 65614, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 32, 0, 'His umbrella... sprouted! It grew roots, flowered, and became a tree!', 12, 0, 100, 5, 0, 0, 65615, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 33, 0, 'Well, it\'s true. If you ever go to the Wood of Staves, you can see it.', 12, 0, 100, 1, 0, 0, 65616, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 34, 0, 'Along with the budding staves of all the elders who came before us, growing now into giant trees.', 12, 0, 100, 274, 0, 0, 65617, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 35, 0, 'It\'s not a sad story! Liu Lang himself said, \"Never mourn a life well-lived.\"', 12, 0, 100, 1, 0, 0, 65618, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (64875, 36, 0, 'And I think he lived a very very good life. We owe everything we have to Liu Lang, the first pandaren explorer.', 12, 0, 100, 273, 0, 0, 65619, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT INTO `creature_text` VALUES (53565, 0, 0, 'Such skill for a new trainee.', 12, 0, 100, 511, 0, 0, 56431, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 1, 'That target didn\'t stand a chance.', 12, 0, 100, 511, 0, 0, 56433, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 2, 'One day you will have to teach me your secrets.', 12, 0, 100, 509, 0, 0, 56432, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 3, 'You are bound for a great things, trainee.', 12, 0, 100, 508, 0, 0, 56435, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 4, 'I must train harder so I can be like you.', 12, 0, 100, 1, 0, 0, 56437, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 5, 'I hope the instructors saw that!', 12, 0, 100, 1, 0, 0, 56434, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 6, 'I must work as hard as you.', 12, 0, 100, 1, 0, 0, 56436, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 0, 7, 'Your form is impeccable.', 12, 0, 100, 1, 0, 0, 56430, 0, 'Aspiring Trainee to Training Target');
INSERT INTO `creature_text` VALUES (53565, 1, 0, 'My money\'s on Lam. Did you see him take down Chu during training last week?', 12, 0, 100, 1, 0, 0, 56420, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 2, 0, 'His stamina is nothing against Lam\'s strength.', 12, 0, 100, 0, 0, 0, 56423, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 3, 0, 'He has the legs of an ox! Have you ever been kicked by him in training?', 12, 0, 100, 1, 0, 0, 56424, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 4, 0, 'Raise you a keg of Stormshout Gold. It\'ll be your loss.', 12, 0, 100, 1, 0, 0, 0, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 5, 0, 'That\'s where you\'re mistaken, friend. Zhou is as sturdy as an oak.', 12, 0, 100, 1, 0, 0, 56421, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 6, 0, 'Zhou has the stamina of three men, so he can out-stay most opponents.', 12, 0, 100, 0, 0, 0, 56422, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 7, 0, 'Four coppers on Zhou.', 12, 0, 100, 0, 0, 0, 56427, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 0, 'That was a good match. Thank you.', 12, 0, 100, 1, 0, 0, 56444, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 1, 'My skills are no match for yours. I admit defeat.', 12, 0, 100, 1, 0, 0, 56441, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 2, 'Your skills are too great. I yield.', 12, 0, 100, 1, 0, 0, 56442, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 3, 'You fought well. I must learn more from you in the future.', 12, 0, 100, 1, 0, 0, 56443, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 4, 'Thank you for reminding me that I must train more diligently.', 12, 0, 100, 1, 0, 0, 56439, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 5, 'You fight honorably, friend.', 12, 0, 100, 1, 0, 0, 56440, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 6, 'You are an honorable opponent.', 12, 0, 100, 1, 0, 0, 56438, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54586, 0, 7, 'I have never seen a trainee with skills such as yours. I must tell the others.', 12, 0, 100, 1, 0, 0, 56445, 0, 'Huojin Trainee');
INSERT INTO `creature_text` VALUES (54587, 0, 0, 'That was a good match. Thank you.', 12, 0, 100, 1, 0, 0, 56444, 0, 'Tushui Trainee');
INSERT INTO `creature_text` VALUES (54587, 0, 1, 'My skills are no match for yours. I admit defeat.', 12, 0, 100, 1, 0, 0, 56441, 0, 'Tushui Trainee');
