INSERT INTO `creature_text` VALUES (60183, 0, 0, 'I hope you\'re ready, $p. Jaomin Ro awaits you just over the bridge.', 12, 0, 100, 113, 0, 0, 59710, 0, 'Trainee Nim to Player');
INSERT INTO `creature_text` VALUES (54611, 0, 0, 'A challenger approaches.', 12, 0, 100, 113, 0, 0, 59692, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 0, 'You\'ve been trained well.', 12, 0, 100, 2, 0, 0, 55205, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 1, 'Well fought. Master Shang will be pleased.', 12, 0, 100, 2, 0, 0, 55206, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 2, 'Amazing! You\'re much stronger than you look.', 12, 0, 100, 2, 0, 0, 55207, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 3, 'It seems I have more training to do. Thank you for the lesson.', 12, 0, 100, 2, 0, 0, 55208, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 4, 'I bow to your skill. You bested me fairly.', 12, 0, 100, 2, 0, 0, 55209, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (54611, 1, 5, 'It was an honor to be defeated by you.', 12, 0, 100, 2, 0, 0, 55210, 0, 'Jaomin Ro to Player');
INSERT INTO `creature_text` VALUES (60244, 0, 0, 'You\'re departing so soon?  I am envious of your prowess, $c. Good luck to you.', 12, 0, 100, 2, 0, 0, 59711, 0, 'Trainee Guang to Player');
INSERT INTO `creature_text` VALUES (54943, 0, 0, 'Shhhh! Come quietly. She\'s practicing.', 12, 0, 100, 396, 0, 0, 59712, 0, 'Merchant Lorvo to Player');
INSERT INTO `creature_text` VALUES (56503, 0, 0, 'Master Shang has trained you well. Thank you, friend!', 12, 0, 100, 2, 0, 0, 53265, 0, 'Min Dimwind to Player');
INSERT INTO `creature_text` VALUES (56503, 1, 0, 'I couldn\'t have fought them off alone. Now, if you\'ll excuse me, I should go find my cart.', 12, 0, 100, 1, 0, 0, 56452, 0, 'Min Dimwind to Player');
INSERT INTO `creature_text` VALUES (56503, 2, 0, 'Cart!', 14, 0, 100, 22, 0, 0, 56453, 0, 'Min Dimwind to Player');
INSERT INTO `creature_text` VALUES (56503, 3, 0, 'Hello, cart. Still upside-downed, I see.', 12, 0, 100, 2, 0, 0, 56454, 0, 'Min Dimwind to Player');
INSERT INTO `creature_text` VALUES (54130, 0, 0, 'Look out! Its friendses are coming!', 14, 0, 100, 2, 0, 0, 56473, 0, 'Amberleaf Scamp to Player');
INSERT INTO `creature_text` VALUES (54130, 1, 0, 'Run away!', 14, 0, 100, 2, 0, 0, 56474, 0, 'Amberleaf Scamp to Player');
INSERT INTO `creature_text` VALUES (54567, 0, 0, 'A challenge in compassion and humility. By helping Merchant Lorvo, it would seem you\'ve passed, $c. Master Shang would be pleased.', 12, 0, 100, 2, 0, 27404, 60599, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (54567, 1, 0, 'And so our path lays before us. Speak to Master Shang Xi, he will tell you what comes next.', 12, 0, 100, 0, 0, 27399, 60046, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (59652, 0, 0, 'Meet me up in the cave if you would, friend.', 12, 0, 100, 396, 0, 27397, 59147, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (59642, 0, 0, 'Keep those creatures at bay while I meditate. We\'ll soon have the answers we seek.', 12, 0, 100, 1, 0, 27398, 59161, 0, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (54856, 0, 0, 'Master Li Fei\'s voice echoes, \"The way of the Tushui... enlightenment through patience and meditation... the principled life.\"', 16, 0, 100, 6, 0, 0, 53129, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 1, 0, 'Master Li Fei\'s voice echoes, \"It is good to see you again, Aysa. You\'ve come with respect, and so I shall give you the answers you seek.\"', 16, 0, 100, 1, 0, 0, 53845, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 2, 0, 'Master Li Fei\'s voice echoes, \"Huo, the spirit of fire, is known for his hunger. He wants for tinder to eat. He needs the caress of the wind to rouse him.\"', 16, 0, 100, 1, 0, 0, 53841, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Master Li Fei\'s voice echoes, \"If you find these things and bring them to his cave, on the far side of Wu-Song Village, you will face a challenge within.\"', 16, 0, 100, 1, 0, 0, 53842, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 4, 0, 'Master Li Fei\'s voice echoes, \"Overcome that challenge, and you shall be graced by Huo\'s presence. Rekindle his flame, and if your spirit is pure, he shall follow you.\"', 16, 0, 100, 1, 0, 0, 53843, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 5, 0, 'Master Li Fei\'s voice echoes, \"Go, children. We shall meet again very soon.\"', 16, 0, 100, 2, 0, 0, 53844, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 6, 0, 'Master Li Fei fades away.', 16, 0, 100, 0, 0, 0, 53130, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (54568, 0, 0, 'Take that monkeybutt!', 12, 0, 100, 5, 0, 0, 59757, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (60176, 0, 0, 'The fire spirit is upset. It\'s dangerous to enter the shrine now...', 12, 0, 100, 396, 0, 0, 59680, 0, 'Huojin Monk to Player');
INSERT INTO `creature_text` VALUES (54135, 0, 0, 'You have earned the right to proceed.  Huo lies beyond.', 12, 0, 100, 1, 0, 0, 55251, 0, 'Master Li Fei to Player');
INSERT INTO `creature_text` VALUES (60248, 0, 0, 'Is that... is that Huo?', 12, 0, 100, 25, 0, 0, 59751, 0, 'Chia-hui Autumnleaf to Player');
INSERT INTO `creature_text` VALUES (60253, 0, 0, 'It is! Well done, $p!', 12, 0, 100, 71, 0, 0, 59752, 0, 'Brewer Lin to Player');
INSERT INTO `creature_text` VALUES (54786, 0, 0, 'Welcome, Huo. The people have missed your warmth.', 12, 0, 100, 2, 0, 27788, 60608, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 1, 0, 'You have conquered every challenge I put before you, $n. You have found Huo and brought him safely to the temple.', 12, 0, 100, 1, 0, 27789, 60600, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 2, 0, 'There is a much larger problem we face now, my students. Shen-zin Su is in pain. If we do not act, the very land on which we stand could die, and all of us with it.', 12, 0, 100, 1, 0, 27790, 60601, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 3, 0, 'We need to speak to Shen-zin Su and discover how to heal it. And to do that, we need the four elemental spirits returned. Huo was the first.', 12, 0, 100, 1, 0, 27791, 60602, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 4, 0, 'Ji, I\'d like you to go to the Dai-Lo Farmstead in search of Wugou, the spirit of earth.', 12, 0, 100, 1, 0, 27792, 60603, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 5, 0, 'Aysa, I want you to go to the Singing Pools to find Shu, the spirit of water.', 12, 0, 100, 1, 0, 27793, 60604, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 6, 0, 'And $n, you shall be the hand that guides us all. Speak with me for a moment before you join Aysa at the Singing Pools to the east.', 12, 0, 100, 1, 0, 27794, 60605, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 7, 0, 'You\'ve returned with the spirits of water and earth. You make an old master proud.', 12, 0, 100, 1, 0, 27776, 55295, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 8, 0, 'Wugou and Shu are welcome here. We will care for them well.', 12, 0, 100, 1, 0, 27777, 55296, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 9, 0, 'The only remaining spirit is Dafeng, who hides somewhere across the Dawning Span to the west.', 12, 0, 100, 1, 0, 27778, 55297, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (54786, 10, 0, 'I can carry you to the top of the temple. Ji awaits you there to lead the way.', 12, 0, 100, 1, 0, 27779, 55298, 0, 'Master Shang Xi to Player');
INSERT INTO `creature_text` VALUES (61127, 0, 0, 'On it!', 12, 0, 100, 0, 0, 27306, 60606, 0, 'Ji Firepaw to Fire Central Temple Turn-in Controller');
INSERT INTO `creature_text` VALUES (61126, 0, 0, 'Yes master.', 12, 0, 100, 2, 0, 27406, 60607, 0, 'Aysa Cloudsinger to Fire Central Temple Turn-in Controller');
INSERT INTO `creature_text` VALUES (60250, 0, 0, 'Hey there! Are you the one that brought back the fire spirit?', 12, 0, 100, 3, 0, 0, 59875, 0, 'Cai to Deng');
INSERT INTO `creature_text` VALUES (60250, 1, 0, 'Of course he was hot! Don\'t be dumb, Deng.', 12, 0, 100, 5, 0, 0, 59877, 0, 'Cai to Deng');
INSERT INTO `creature_text` VALUES (60250, 2, 0, 'Nuh uh. Bet they could for sure kick you over that hill, though! Hah!', 12, 0, 100, 274, 0, 0, 59880, 0, 'Cai to Deng');
INSERT INTO `creature_text` VALUES (60250, 3, 0, 'Okay bye.', 12, 0, 100, 3, 0, 0, 59878, 0, 'Cai to Deng');
INSERT INTO `creature_text` VALUES (60249, 0, 0, 'Was he hot?  I bet he was hot!', 12, 0, 100, 6, 0, 0, 59876, 0, 'Deng to Cai');
INSERT INTO `creature_text` VALUES (60249, 1, 0, 'I bet you\'re really strong, huh?  You could probably kick that bridge right in half if you wanted to!', 12, 0, 100, 6, 0, 0, 59879, 0, 'Deng to Cai');
INSERT INTO `creature_text` VALUES (60249, 2, 0, 'Bye!', 12, 0, 100, 3, 0, 0, 59884, 0, 'Deng to Cai');
INSERT INTO `creature_text` VALUES (57638, 0, 0, 'The reeds of the Singing Pools are the hardest in all of the land, but they are as air before my mighty brow.', 12, 0, 100, 1, 0, 0, 56333, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (57638, 1, 0, 'Many have tested my claim, and I yet stand proven.', 12, 0, 100, 2, 0, 0, 56334, 0, 'Jojo Ironbrow to Player');
INSERT INTO `creature_text` VALUES (54975, 0, 0, 'I have to admit, that looked pretty fun!', 12, 0, 100, 0, 0, 27394, 66056, 1, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (54975, 1, 0, 'And it looks to me like you made a new friend.', 12, 0, 100, 0, 0, 27395, 66057, 1, 'Aysa Cloudsinger to Player');
INSERT INTO `creature_text` VALUES (57712, 0, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Dai-Lo Farmstead.', 12, 0, 100, 3, 0, 0, 56406, 0, 'Delivery Cart Tender to Player');
INSERT INTO `creature_text` VALUES (57712, 1, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Temple of Five Dawns.', 12, 0, 100, 3, 0, 0, 56415, 0, 'Delivery Cart Tender to Player');
INSERT INTO `creature_text` VALUES (55477, 0, 0, 'Wake... up... DANGIT!', 12, 0, 100, 15, 0, 27345, 60447, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 1, 0, 'This is ridiculous...', 12, 0, 100, 274, 0, 27346, 60448, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 2, 0, 'You sleep like a rock!', 12, 0, 100, 5, 0, 27347, 60449, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 3, 0, 'There\'s no way you\'ll sleep through THIS.', 12, 0, 100, 274, 0, 27348, 60450, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 4, 0, 'Seriously?', 12, 0, 100, 18, 0, 27349, 60451, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 5, 0, 'Why...', 12, 0, 100, 507, 0, 27350, 60452, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 6, 0, 'won\'t...', 12, 0, 100, 509, 0, 27351, 60453, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 7, 0, 'you...', 12, 0, 100, 507, 0, 27352, 60454, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 8, 0, 'just...', 12, 0, 100, 509, 0, 27353, 60455, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 9, 0, 'WAKE UP?!', 12, 0, 100, 22, 0, 27354, 60456, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 10, 0, 'I will break you, little rock man!', 12, 0, 100, 25, 0, 27355, 60457, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (55477, 11, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 55942, 0, 'Ji Firepaw to Player');
INSERT INTO `creature_text` VALUES (55483, 0, 0, 'Gimme all your vegetables!', 12, 0, 100, 0, 0, 0, 54873, 0, 'Plump Virmen to Player');
INSERT INTO `creature_text` VALUES (55483, 0, 1, 'AIIIIEEEEEEE!', 12, 0, 100, 0, 0, 0, 54874, 0, 'Plump Virmen to Player');
INSERT INTO `creature_text` VALUES (55483, 0, 2, 'This virmen land!', 12, 0, 100, 0, 0, 0, 54876, 0, 'Plump Virmen to Player');
INSERT INTO `creature_text` VALUES (55483, 0, 3, 'You no take carrot! You take turnip instead!', 12, 0, 100, 0, 0, 0, 54877, 0, 'Plump Virmen to Player');
INSERT INTO `creature_text` VALUES (55504, 0, 0, 'Gimme all your vegetables!', 12, 0, 100, 0, 0, 0, 54873, 0, 'Plump Carrotcruncher to Player');
INSERT INTO `creature_text` VALUES (55504, 0, 1, 'AIIIIEEEEEEE!', 12, 0, 100, 0, 0, 0, 54874, 0, 'Plump Carrotcruncher to Player');
