INSERT INTO `creature_text` VALUES (105296, 0, 0, 'Your arrival is fortuitous. Tonight, the nobles have gathered in honor of the Grand Magistrix.', 12, 0, 100, 396, 0, 70446, 108582, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105296, 1, 0, 'Our spies report that Elisande herself will attend. This presents a rare opportunity to catch her away from her source of power.', 12, 0, 100, 396, 0, 70447, 108583, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105296, 2, 0, 'We\'ll take a side entrance to the grounds... too many eyes are watching the front.', 12, 0, 100, 396, 0, 70448, 108584, 0, 'Ly\'leth Lunastre to Player');
