INSERT INTO `creature_text` VALUES (107324, 0, 0, 'Intruders!', 12, 0, 100, 0, 0, 0, 121052, 0, 'Suramar Child');
INSERT INTO `creature_text` VALUES (107435, 0, 0, 'You insult my honor, $n. Who exactly are you?', 12, 0, 100, 463, 0, 0, 111881, 0, 'Suspicious Noble to Player');
INSERT INTO `creature_text` VALUES (107435, 1, 0, '$n, you think I would spy on my own? Maybe we should look closer at you instead!', 12, 0, 100, 463, 0, 0, 111882, 0, 'Suspicious Noble to Player');
INSERT INTO `creature_text` VALUES (107435, 2, 0, 'Now now, let\'s not be hasty $n. Why don\'t you follow me so we can talk about this in a more private setting...', 12, 0, 100, 463, 0, 0, 111875, 0, 'Suspicious Noble to Player');
INSERT INTO `creature_text` VALUES (107435, 3, 0, 'How dare you accuse me of such a thing, $n? Do you know who I am? I don\'t recognize you at all...', 12, 0, 100, 463, 0, 0, 111874, 0, 'Suspicious Noble to Player');
INSERT INTO `creature_text` VALUES (107435, 4, 0, 'I don\'t know how you found me out, but now it\'s time to die!', 12, 0, 100, 11, 0, 0, 111886, 0, 'Suspicious Noble to Player');
