INSERT INTO `creature_text` VALUES (110443, 0, 0, 'Do you expect me to waste time chattering with vapid nobles even as my enemies close in? Look there, you fool! We are not alone.', 12, 0, 100, 0, 0, 67614, 117106, 0, 'Elisande to Player');
INSERT INTO `creature_text` VALUES (110443, 1, 0, 'Yet another failure, Melandrus. Consider this your chance to correct it. Dispose of these outsiders. I must return to the Nighthold.', 12, 0, 100, 0, 0, 67613, 117104, 0, 'Elisande to Player');
INSERT INTO `creature_text` VALUES (110958, 0, 0, 'This wine is terrible. I\'ll wager Melandrus watered it down.', 12, 0, 100, 1, 0, 0, 118921, 0, 'Fancy Noble');
INSERT INTO `creature_text` VALUES (110958, 1, 0, 'I saw Spellblade Aluriel pacing about. She always seems so... angry.', 12, 0, 100, 1, 0, 0, 118920, 0, 'Fancy Noble');
INSERT INTO `creature_text` VALUES (90417, 0, 0, 'Nobody touch it! Not until our guest arrives.', 12, 0, 100, 1, 0, 67263, 101006, 0, 'Archmage Khadgar to Player');
INSERT INTO `creature_text` VALUES (104217, 4, 1, 'Not very clever of you to challenge all of us!', 14, 0, 100, 0, 0, 58638, 0, 0, 'TALIXAE - AGGRO_TEAM');
