INSERT INTO `creature_text` VALUES (98266, 3, 0, 'Time is short. If you have found this message, you are capable of finding me as well.', 12, 0, 100, 1, 0, 67268, 100094, 0, 'Echo of First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (98312, 0, 0, 'Rath-anu\'tanos!', 12, 0, 100, 0, 0, 67511, 100280, 0, 'First Arcanist Thalyssra to Player');
