INSERT INTO `creature_text` VALUES (99267, 1, 0, 'The shal\'dorei are... chosen...', 12, 0, 100, 0, 0, 66014, 116624, 0, 'Nightborne Scion to Player');
INSERT INTO `creature_text` VALUES (99267, 2, 0, 'We have... failed.', 12, 0, 100, 0, 0, 65810, 116758, 0, 'Nightborne Scion to Player');
INSERT INTO `creature_text` VALUES (99524, 0, 0, 'Selentia wails, calling forth ancient keepers!', 41, 0, 100, 463, 0, 53888, 101162, 0, 'Keeper Selentia to Player');
INSERT INTO `creature_text` VALUES (99524, 1, 0, 'The tethers have broken. Selentia is vulnerable!', 41, 0, 100, 0, 0, 0, 114593, 0, 'Keeper Selentia to Eternal Tether');
INSERT INTO `creature_text` VALUES (101116, 0, 0, 'My, my. You look rather gaunt, honored teacher. Are you eating enough? Ahahaha!', 12, 0, 100, 396, 0, 67293, 104159, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101116, 1, 0, 'You should be proud! Your handiwork is going to help us reclaim every corner of our ancient lands.', 12, 0, 100, 397, 0, 67294, 104158, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101116, 2, 0, 'Face it, old man. I have surpassed you.', 12, 0, 100, 0, 0, 67282, 104232, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101116, 3, 0, 'My warp field is not working! Where have you taken us?!', 12, 0, 100, 0, 0, 67290, 104248, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101116, 4, 0, 'No! This place... it must bend to my will!', 12, 0, 100, 0, 0, 67288, 104270, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101116, 5, 0, 'An\'ratha... an\'tal-thandros!', 12, 0, 100, 0, 0, 67289, 104271, 0, 'Warpcaster Thwen to Player');
INSERT INTO `creature_text` VALUES (101499, 0, 0, 'That is far enough.', 12, 0, 100, 0, 0, 67418, 103939, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 1, 0, 'Well, you are not from the palace... and you are no demon, at least not entirely.', 12, 0, 100, 274, 0, 67419, 103940, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 2, 0, 'Identify yourself, before I teleport you to the depths of the Great Sea.', 12, 0, 100, 25, 0, 67420, 103943, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 3, 0, 'What is this? A coin...', 12, 0, 100, 0, 0, 67421, 103949, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 4, 0, 'The sign of the dusk lily... This is the First Arcanist\'s seal! Is she alive?!', 12, 0, 100, 1, 0, 67422, 103950, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 5, 0, 'Then there is still a chance... Come quickly! I need your help.', 12, 0, 100, 1, 0, 67423, 103969, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 6, 0, 'Right. The trap.', 12, 0, 100, 1, 0, 67424, 103951, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 7, 0, 'N\'eth ana!', 12, 0, 100, 0, 0, 67425, 103967, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 8, 0, 'The teleporter is just outside. Activate it first, then step on the pad.', 12, 0, 100, 0, 0, 67484, 103970, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101499, 9, 0, 'Go ahead, I am right behind you.', 12, 0, 100, 1, 0, 67287, 104149, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 0, 0, 'Urgh!', 12, 0, 100, 482, 0, 67281, 104162, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 1, 0, 'Good trick... <cough> Warping the space around your body like that.', 12, 0, 100, 0, 0, 67283, 104233, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 2, 0, 'But what if that space is already unstable?', 12, 0, 100, 0, 0, 67284, 104235, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 3, 0, '<cough> Unstable... space...', 12, 0, 100, 0, 0, 67291, 104249, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 4, 0, 'Thwen always took magic too lightly... She was a terrible student... <cough>', 12, 0, 100, 1, 0, 67285, 104268, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101782, 5, 0, 'I haven\'t much left... <cough> I will get us as close as I can... Come close.', 12, 0, 100, 0, 0, 67286, 104269, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101821, 0, 0, 'My magic... defeated?', 12, 0, 100, 0, 0, 66016, 116433, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 1, 0, 'Your age has ended!', 12, 0, 100, 0, 0, 65799, 116705, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 2, 0, 'Arash-falas...', 12, 0, 100, 0, 0, 65811, 116791, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 3, 0, 'Tor\'theras falar!', 12, 0, 100, 0, 0, 66017, 116790, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 4, 0, 'None may challenge us!', 12, 0, 100, 0, 0, 66026, 116701, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 5, 0, 'No! It is not my time....', 12, 0, 100, 0, 0, 66015, 116778, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 6, 0, 'Anath\'ashar!', 12, 0, 100, 0, 0, 65818, 116709, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101821, 7, 0, 'Your death is fated!', 12, 0, 100, 0, 0, 65814, 116703, 0, 'Nightborne Warpcaster to Player');
INSERT INTO `creature_text` VALUES (101825, 0, 0, 'Feel the might of the shal\'dorei!', 12, 0, 100, 0, 0, 65905, 116690, 0, 'Nightborne Enforcer to Player');
INSERT INTO `creature_text` VALUES (101848, 0, 0, 'The darkness lifts... thank you.', 12, 0, 100, 0, 0, 0, 104167, 0, 'Absolon to Player');
INSERT INTO `creature_text` VALUES (101848, 1, 0, 'I left the City to find my daughter. She was one of those who dared speak against Elisande...', 12, 0, 100, 0, 0, 0, 119837, 0, 'Absolon to Player');
INSERT INTO `creature_text` VALUES (101848, 2, 0, 'She is gone. I thought I would join her in the ruins as a mindless husk, but you...', 12, 0, 100, 0, 0, 0, 119838, 0, 'Absolon to Player');
INSERT INTO `creature_text` VALUES (101848, 3, 0, 'You saved me.', 12, 0, 100, 0, 0, 0, 119839, 0, 'Absolon to Player');
INSERT INTO `creature_text` VALUES (101848, 4, 0, 'I will seek shelter now. I hope we meet again, friend.', 12, 0, 100, 0, 0, 0, 119840, 0, 'Absolon to Player');
INSERT INTO `creature_text` VALUES (101852, 0, 0, 'Here we are - my old sanctuary!', 12, 0, 100, 1, 0, 67275, 104154, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 1, 0, 'Strange... the defenses are offline...', 12, 0, 100, 1, 0, 67276, 104155, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 2, 0, 'Intruders! How did they get in here?!', 12, 0, 100, 1, 0, 67277, 104156, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 3, 0, 'This coil taps into the ancient leylines. Crank it up to full power!', 12, 0, 100, 1, 0, 67278, 104221, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 4, 0, 'Good. If we overcharge enough coils, the entire system will failsafe into a lockdown.', 12, 0, 100, 0, 0, 67280, 104224, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 5, 0, 'There are more coils in the chamber below - and more intruders, I imagine.', 12, 0, 100, 0, 0, 67279, 104223, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 6, 0, 'Thwen... I should have recognized your uniquely oafish meddling on my network.', 12, 0, 100, 25, 0, 67292, 104157, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (101852, 7, 0, 'I can deal with Thwen. You overload the coils!', 12, 0, 100, 1, 0, 67296, 104161, 0, 'Chief Telemancer Oculeth to Player');
