INSERT INTO `creature_text` VALUES (111863, 0, 0, 'What is this place?', 12, 0, 100, 0, 0, 67381, 94147, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 1, 0, 'This chamber... I have never seen anything like it.', 12, 0, 100, 0, 0, 67383, 117057, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 2, 0, 'So dark...', 12, 0, 100, 0, 0, 67384, 117539, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 3, 0, 'In\'alah!', 12, 0, 100, 463, 0, 67385, 117454, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 4, 0, 'Much better.', 12, 0, 100, 0, 0, 67386, 117220, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 5, 0, 'Old teleporters. Not so different from the ones in Suramar city. Seems they are inactive.', 12, 0, 100, 0, 0, 67387, 117064, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 6, 0, 'Let us explore downstairs. Watch your step.', 12, 0, 100, 0, 0, 67388, 117219, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 7, 0, 'Strange. The floor looks like... a map?', 12, 0, 100, 0, 0, 67389, 117221, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 8, 0, 'And we are at the center...', 12, 0, 100, 0, 0, 67390, 117551, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 9, 0, 'One thing is certain - leyline energy is being fed into this chamber from elsewhere.', 12, 0, 100, 0, 0, 67391, 117222, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 10, 0, 'What could have required such power? A weapon, perhaps?', 12, 0, 100, 0, 0, 67392, 117557, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 11, 0, 'And those keepers protecting it... How long their vigil must have been...', 12, 0, 100, 0, 0, 67393, 117558, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 12, 0, '<Sigh.> I have more questions now than before!', 12, 0, 100, 0, 0, 67394, 117559, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 13, 0, 'For now, at least, we know the withered have learned to avoid this place.', 12, 0, 100, 0, 0, 67395, 101214, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 14, 0, 'And nobody from the palace would think to search for us here.', 12, 0, 100, 0, 0, 67396, 101215, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 15, 0, 'It is a perfect refuge. A temporary home, until I can return to Suramar.', 12, 0, 100, 0, 0, 67397, 117561, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 16, 0, 'But first, I will need allies.', 12, 0, 100, 0, 0, 67398, 117562, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (32642, 1, 0, 'Dis bad mojo what you doin\'. Don\'t say Mojodishu didn\'t warn ya!', 12, 0, 100, 1, 0, 0, 33028, 0, 'Mojodishu to Player');
INSERT INTO `creature_text` VALUES (32642, 2, 0, 'Really, mon? Just like dat you be leavin\' Mojodishu behind?', 12, 0, 100, 1, 0, 0, 33027, 0, 'Mojodishu to Player');
INSERT INTO `creature_text` VALUES (91003, 0, 0, 'Rok SMASH!', 14, 0, 100, 0, 0, 54551, 102714, 0, 'Rokmora');
