INSERT INTO `creature_text` VALUES (94960, 0, 0, 'The undeserving will perish!', 14, 0, 100, 0, 0, 54650, 102955, 0, 'Hymdall');
INSERT INTO `creature_text` VALUES (94960, 1, 0, 'Resist! Fight to the last!', 14, 0, 100, 0, 0, 54646, 102949, 0, 'Hymdall');
INSERT INTO `creature_text` VALUES (94960, 2, 0, '|TInterface\\Icons\\INV_Misc_Horn_03:20|t Hymdall blows the |cFFFF0404|Hspell:191284|h[Horn of Valor]|h|r summoning Storm Drakes!', 41, 0, 100, 0, 0, 54646, 104163, 0, 'Hymdall');
INSERT INTO `creature_text` VALUES (94960, 3, 0, 'I will bleed the weakness from your veins!', 14, 0, 100, 0, 0, 54637, 102942, 0, 'Hymdall');
INSERT INTO `creature_text` VALUES (94960, 4, 0, 'A battle well fought! The path forward is open.', 14, 0, 100, 0, 0, 54648, 102951, 0, 'Hymdall');
INSERT INTO `creature_text` VALUES (95674, 0, 0, '|TInterface\\Icons\\spell_hunter_lonewolf:20|t Fenryr begins to cast |cFFFF0404|Hspell:196543|h[Unnerving Howl]|h|r!', 41, 0, 100, 0, 0, 0, 104038, 0, 'Fenryr');
INSERT INTO `creature_text` VALUES (95674, 1, 0, 'Wounded, Fenryr retreats to his den.', 41, 0, 100, 0, 0, 0, 104029, 0, 'Fenryr');
INSERT INTO `creature_text` VALUES (95675, 0, 0, 'Give up the aegis or die!', 14, 0, 100, 0, 0, 54614, 102921, 0, 'God-King Skovald');
INSERT INTO `creature_text` VALUES (95675, 1, 0, 'By flame be scourged!', 14, 0, 100, 0, 0, 54628, 102935, 0, 'God-King Skovald');
INSERT INTO `creature_text` VALUES (95675, 2, 0, '|TInterface\\Icons\\Spell_Fire_FelFlameRing:20|t Skovald begins to cast |cFFFF0404|Hspell:193826|h[Ragnarok]|h|r!', 41, 0, 100, 0, 0, 54628, 106469, 0, 'God-King Skovald');
INSERT INTO `creature_text` VALUES (95675, 3, 0, 'All my plans... brought to ruin...', 14, 0, 100, 0, 0, 54620, 102927, 0, 'God-King Skovald to Player');
INSERT INTO `creature_text` VALUES (95676, 0, 0, 'Throughout your trials, you have demonstrated strength, skill, and intellect-traits shared by the greatest of our vrykul kings. Now... you will face them in combat. Emerge victorious, and you may yet claim your prize!', 14, 0, 100, 0, 0, 54613, 100271, 0, 'Odyn to Player');
INSERT INTO `creature_text` VALUES (95676, 1, 0, 'To the victor go the spoils... The Aegis of Aggramar is yours for the taking.', 14, 0, 100, 0, 0, 54578, 100240, 0, 'Odyn to King Bjorn');
INSERT INTO `creature_text` VALUES (95676, 2, 0, 'Most impressive! I never thought I would meet anyone who could match the Valarjar\'s strength... and yet here you stand.', 14, 0, 100, 0, 0, 54584, 102879, 0, 'Odyn to Odyn');
INSERT INTO `creature_text` VALUES (95676, 3, 0, 'There is one final trial that awaits: me. Should you triumph, I will carve your names in these sacred halls myself and grant you a reward worthy of your valor.', 14, 0, 100, 0, 0, 54583, 102881, 0, 'Odyn to Odyn');
INSERT INTO `creature_text` VALUES (95676, 4, 0, 'It has been ages since I faced a worthy opponent. Let the battle begin!', 14, 0, 100, 0, 0, 54604, 102913, 0, 'Odyn');
INSERT INTO `creature_text` VALUES (95676, 5, 0, 'Enough! I... I yield! You are truly remarkable creatures. As promised, you will have your just reward.', 12, 0, 100, 0, 0, 54598, 102907, 0, 'Odyn');
INSERT INTO `creature_text` VALUES (95833, 0, 0, 'I will not fail you, Eyir!', 14, 0, 100, 0, 0, 54687, 102993, 0, 'Hyrja');
INSERT INTO `creature_text` VALUES (95833, 1, 0, 'You will pollute these halls no more!', 14, 0, 100, 0, 0, 54681, 102986, 0, 'Hyrja');
INSERT INTO `creature_text` VALUES (95833, 2, 0, '|TInterface\\Icons\\Spell_Nature_EyeOfTheStorm:20|t Hyrja begins to cast |cFFFF0404|Hspell:192305|h[Eye of the Storm]|h|r!', 41, 0, 100, 0, 0, 0, 122899, 0, 'Hyrja');
INSERT INTO `creature_text` VALUES (95833, 3, 0, 'Eyir... you were wrong to put your faith in me...', 14, 0, 100, 0, 0, 54683, 102988, 0, 'Hyrja to Player');
INSERT INTO `creature_text` VALUES (95843, 0, 0, 'I will take joy in crushing you!', 14, 0, 100, 0, 0, 54577, 102583, 0, 'King Haldor to King Haldor');
INSERT INTO `creature_text` VALUES (95843, 1, 0, 'My strength... fails.', 14, 0, 100, 0, 0, 54575, 102585, 0, 'King Haldor to Player');
INSERT INTO `creature_text` VALUES (95885, 0, 0, 'It\'s so good to fight again.', 14, 0, 100, 0, 0, 55406, 97634, 0, 'Tirathon Saltheril');
INSERT INTO `creature_text` VALUES (95885, 1, 0, '|TInterface\\Icons\\Ability_Rogue_ShadowStrikes:20|t%s empowers himself with |cFFFF0000|Hspell:191941|h[Darkstrikes]|h|r!', 15, 0, 100, 0, 0, 0, 103258, 0, 'Tirathon Saltheril to Player');
INSERT INTO `creature_text` VALUES (95885, 2, 0, 'My blades hunger.', 14, 0, 100, 0, 0, 55416, 97641, 0, 'Tirathon Saltheril');
INSERT INTO `creature_text` VALUES (95885, 3, 0, 'My power.. Wasn\'t enough?', 14, 0, 100, 0, 0, 55408, 97650, 0, 'Tirathon Saltheril to Player');
INSERT INTO `creature_text` VALUES (95886, 0, 0, 'So ... long ... where is my Lord of Fire, why does he not answer my call?', 14, 0, 100, 0, 0, 55437, 97655, 0, 'Ash\'Golm');
INSERT INTO `creature_text` VALUES (95886, 1, 0, 'Your bones will blacken, your life extinguished.', 14, 0, 100, 0, 0, 55433, 97659, 0, 'Ash\'Golm');
INSERT INTO `creature_text` VALUES (95886, 2, 0, 'We give rise to flame.', 14, 0, 100, 0, 0, 55432, 97660, 0, 'Ash\'Golm');
INSERT INTO `creature_text` VALUES (95886, 3, 0, 'Your flesh will know the sweet caress of fire.', 14, 0, 100, 0, 0, 55434, 97658, 0, 'Ash\'Golm to Player');
INSERT INTO `creature_text` VALUES (95886, 4, 0, 'Fire has a life of its own.', 14, 0, 100, 0, 0, 55431, 97661, 0, 'Ash\'Golm');
INSERT INTO `creature_text` VALUES (95886, 5, 0, 'I am... Extinguished.', 14, 0, 100, 0, 0, 55426, 97667, 0, 'Ash\'Golm to Player');
INSERT INTO `creature_text` VALUES (95887, 0, 0, 'Visitors? How exciting!', 14, 0, 100, 0, 0, 55424, 97672, 0, 'Glazer');
INSERT INTO `creature_text` VALUES (95887, 1, 0, 'Watch your step!', 14, 0, 100, 0, 0, 55420, 97676, 0, 'Glazer');
INSERT INTO `creature_text` VALUES (95887, 2, 0, 'If only I could see the future...', 14, 0, 100, 0, 0, 55474, 97684, 0, 'Glazer to Player');
INSERT INTO `creature_text` VALUES (95888, 0, 0, 'So be it. I will slay you all.', 14, 0, 100, 0, 0, 55472, 97689, 0, 'Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 1, 0, 'Watch your step! It\'s a long way down.', 14, 0, 100, 0, 0, 55465, 97696, 0, 'Cordana Felsong to Player');
INSERT INTO `creature_text` VALUES (95888, 2, 0, 'All night elf children learn to love the dark.', 14, 0, 100, 0, 0, 55463, 97699, 0, 'Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 3, 0, '|TInterface\\Icons\\Ability_Rogue_Shadowstep:20|t%s |cFFFF0000|Hspell:197823|h[Shadowsteps]|h|r away from the party. Reveal her with Elune\'s Light!', 41, 0, 100, 0, 0, 0, 103279, 0, 'Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 4, 0, 'I will not be stopped! I have fought too long, suffered too much. Victory must be mine!', 14, 0, 100, 0, 0, 55461, 97701, 0, 'Cordana Felsong to Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 5, 0, '|TInterface\\Icons\\ability_priest_auspiciousspirits:20|tSpirits form in the shadows around the platform!', 41, 0, 100, 0, 0, 0, 103280, 0, 'Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 6, 0, 'My powers have grown since joining the Legion. Here, let me show you.', 14, 0, 100, 0, 0, 55462, 97700, 0, 'Cordana Felsong');
INSERT INTO `creature_text` VALUES (95888, 7, 0, 'Beaten? How! You are... nothing', 14, 0, 100, 0, 0, 55460, 97702, 0, 'Cordana Felsong to Player');
INSERT INTO `creature_text` VALUES (96015, 0, 0, 'Ooohh... New subjects.', 14, 0, 100, 0, 0, 64824, 97707, 0, 'Inquisitor Tormentorum');
INSERT INTO `creature_text` VALUES (96015, 1, 0, 'Your souls are mine.', 14, 0, 100, 0, 0, 64828, 97711, 0, 'Inquisitor Tormentorum');
INSERT INTO `creature_text` VALUES (96015, 2, 0, '|TInterface\\Icons\\Spell_Shadow_Teleport:20|t%s |cFFFF0000|Hspell:200898|h[Teleports]|h|r to nearby cells and begins releasing prisoners!', 41, 0, 100, 0, 0, 0, 105573, 0, 'Inquisitor Tormentorum');
INSERT INTO `creature_text` VALUES (96015, 3, 0, 'Let\'s see what\'s behind door number...', 14, 0, 100, 0, 0, 64827, 97710, 0, 'Inquisitor Tormentorum');
INSERT INTO `creature_text` VALUES (96015, 4, 0, 'How terribly disappointing.', 14, 0, 100, 0, 0, 64829, 97712, 0, 'Inquisitor Tormentorum to Player');
INSERT INTO `creature_text` VALUES (96657, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[Rekindles]|h|r a Fel Scorcher!', 16, 0, 100, 0, 0, 0, 96139, 0, 'Blade Dancer Illianna to Blade Dancer Illianna');
INSERT INTO `creature_text` VALUES (97081, 0, 0, 'Where they failed, we will succeed!', 14, 0, 100, 0, 0, 54570, 102590, 0, 'King Bjorn');
INSERT INTO `creature_text` VALUES (97081, 1, 0, 'You have... bested me.', 14, 0, 100, 0, 0, 54578, 102592, 0, 'King Bjorn to Player');
INSERT INTO `creature_text` VALUES (97083, 0, 0, 'Your cries of anguish will echo through eternity!', 14, 0, 100, 0, 0, 54574, 102586, 0, 'King Ranulf to King Ranulf');
INSERT INTO `creature_text` VALUES (97083, 1, 0, 'I embrace the abyss!', 14, 0, 100, 0, 0, 54572, 102588, 0, 'King Ranulf to Player');
INSERT INTO `creature_text` VALUES (97084, 0, 0, 'Seems I... underestimated you.', 14, 0, 100, 0, 0, 54578, 102582, 0, 'King Tor to Player');
INSERT INTO `creature_text` VALUES (97087, 0, 0, 'I was looking for a chance to brawl!', 14, 0, 100, 274, 0, 0, 104682, 0, 'Valarjar Champion to Valarjar Champion');
INSERT INTO `creature_text` VALUES (97140, 3, 0, 'Valtrois, can you think of any way to draw more power? Even briefly?', 12, 0, 100, 0, 0, 67320, 118618, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 4, 0, 'What could make the leyline flare up?', 12, 0, 100, 0, 0, 67323, 118622, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 5, 0, 'Spare us the details, just make it happen. I am counting on you.', 12, 0, 100, 0, 0, 67327, 118626, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 6, 0, 'Dismiss these memories so we can proceed.', 12, 0, 100, 0, 0, 67450, 111813, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 7, 0, 'This hunger... this fear...', 12, 0, 100, 0, 0, 67452, 111664, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 8, 0, 'I, too have felt it...', 12, 0, 100, 0, 0, 67453, 111662, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 9, 0, 'Let us move on.', 12, 0, 100, 0, 0, 67454, 111812, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 10, 0, 'Thala nar\'valas.', 12, 0, 100, 0, 0, 67455, 112341, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 11, 0, 'Such confusion... such rage...', 12, 0, 100, 0, 0, 67456, 111667, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 12, 0, 'I am losing control...', 12, 0, 100, 0, 0, 67457, 111814, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 13, 0, 'Agh!! Make them stop! Please make them stop!', 12, 0, 100, 0, 0, 67459, 111608, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 14, 0, 'AAIGHH!!', 12, 0, 100, 0, 0, 67460, 112387, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 15, 0, 'Nnngh- ENOUGH!!', 12, 0, 100, 0, 0, 67461, 112350, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 16, 0, 'There... you found something, didn\'t you?', 12, 0, 100, 0, 0, 67463, 118575, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 17, 0, 'Something which gave you peace...', 12, 0, 100, 0, 0, 67464, 118576, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 18, 0, 'A peace we so desperately need for ourselves.', 12, 0, 100, 0, 0, 67466, 118583, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97140, 19, 0, 'I... I am fine.', 12, 0, 100, 0, 0, 67465, 112398, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (97202, 0, 0, 'You will not deny Hyrja\'s ascendance!', 14, 0, 100, 0, 0, 54670, 102975, 0, 'Olmyr the Enlightened');
INSERT INTO `creature_text` VALUES (97202, 1, 0, 'The Light shines eternal in you, Hyrja!', 14, 0, 100, 0, 0, 54671, 102976, 0, 'Olmyr the Enlightened to Player');
INSERT INTO `creature_text` VALUES (97219, 0, 0, 'You sully this ritual with your presence, mortals!', 14, 0, 100, 0, 0, 54672, 102977, 0, 'Solsten');
INSERT INTO `creature_text` VALUES (97219, 1, 0, 'Hyrja... the fury of the storm is yours to command!', 14, 0, 100, 0, 0, 54673, 102978, 0, 'Solsten to Player');
INSERT INTO `creature_text` VALUES (97729, 0, 0, 'I will rest now.', 12, 0, 100, 0, 0, 66047, 102391, 0, 'Disturbed Apparition to Player');
INSERT INTO `creature_text` VALUES (97729, 1, 0, 'Our people... shu dalees-na.', 12, 0, 100, 0, 0, 66051, 102393, 0, 'Disturbed Apparition to Player');
INSERT INTO `creature_text` VALUES (97729, 2, 0, 'Thandae-alah...', 12, 0, 100, 0, 0, 66044, 102385, 0, 'Disturbed Apparition to Player');
INSERT INTO `creature_text` VALUES (99013, 0, 0, 'Thank you for your help. Your intervention saved my life.', 12, 0, 100, 0, 0, 55443, 103226, 0, 'Drelanim Whisperwind to Player');
INSERT INTO `creature_text` VALUES (99013, 1, 0, 'If you intend to venture deeper you will find far more terrifying things lurking in our Vault.', 12, 0, 100, 0, 0, 55442, 103227, 0, 'Drelanim Whisperwind to Player');
INSERT INTO `creature_text` VALUES (99240, 0, 0, 'The room\'s countermeasures are now armed.', 16, 0, 100, 0, 0, 55437, 106822, 0, 'Countermeasures to Ash\'Golm');
INSERT INTO `creature_text` VALUES (99859, 0, 0, 'I can control it no longer. You must drown!', 12, 0, 100, 0, 0, 0, 116503, 0, 'Azurefall Guardian to Player');
INSERT INTO `creature_text` VALUES (99859, 1, 0, 'Free at last! Let us defeat this evil!', 12, 0, 100, 0, 0, 0, 101639, 0, 'Azurefall Guardian to Player');
INSERT INTO `creature_text` VALUES (99859, 2, 0, 'Thank you, stranger. The rage... the corruption is gone. Now I must tend to the waters I have neglected for so long.', 12, 0, 100, 0, 0, 0, 119825, 0, 'Azurefall Guardian to Player');
INSERT INTO `creature_text` VALUES (102583, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|tThe $n |cFFFF0000|Hspell:202663|h[Detonates]|h|r as it dies!', 16, 0, 100, 0, 0, 0, 122413, 0, 'Fel Scorcher to Fel Scorcher');
INSERT INTO `creature_text` VALUES (102600, 0, 0, 'You there! Outlander! Be a dear and step on that other switch, will you?', 12, 0, 100, 5, 0, 67475, 117624, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 1, 0, 'Ahhh, I recognize that symbol. So Thalyssra has recruited an outlander to our cause... Are we truly so desperate?', 12, 0, 100, 5, 0, 67478, 117635, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 2, 0, '<Sigh.> Very well. I am Valtrois.', 12, 0, 100, 1, 0, 67479, 117672, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 3, 0, 'Out here, so far from the Nightwell, we must settle for any source of magic we can find.', 12, 0, 100, 1, 0, 67480, 118569, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 4, 0, 'It seems Thalyssra and I had the same idea - the old leyline feeds might keep us alive, if barely.', 12, 0, 100, 0, 0, 67481, 118570, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 5, 0, 'We just have to wake them up.', 12, 0, 100, 0, 0, 67482, 118571, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 6, 0, 'This coil projects a high-potency current. You will find a low-potency coil at the far end of the facility.', 12, 0, 100, 0, 0, 67427, 105571, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 7, 0, 'Both currents feed into the central chamber. Use the coils and switches to direct the currents and power all four seals in the central chamber!', 12, 0, 100, 0, 0, 67428, 105575, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (102600, 8, 0, 'You are dismissed. I will make my own way to Shal\'Aran.', 12, 0, 100, 0, 0, 67429, 105742, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103155, 0, 0, 'I shall not squander this gift.', 12, 0, 100, 0, 0, 67407, 105787, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103511, 0, 0, 'Lost Feathermane Kitten spots you and scampers away in fear!', 16, 0, 100, 0, 0, 0, 110347, 0, 'Lost Feathermane Kitten to Player');
INSERT INTO `creature_text` VALUES (103568, 0, 0, 'It is done!', 12, 0, 100, 4, 0, 0, 86779, 0, 'Mayruna Moonwing to Player');
INSERT INTO `creature_text` VALUES (103569, 0, 0, 'Fresh green food grows and your enemies are fewer. We seek only your blessing in return.', 12, 0, 100, 0, 0, 0, 108539, 0, 'Mythandros Irongrove to Player');
INSERT INTO `creature_text` VALUES (103570, 0, 0, 'Thank you.', 12, 0, 100, 2, 0, 0, 111252, 0, 'Mardranel Forestheart to Player');
INSERT INTO `creature_text` VALUES (103571, 1, 0, 'I will bring the blessing to Mythandros directly. See you there!', 12, 0, 100, 0, 0, 0, 120002, 0, 'Solendra Featherdown to Player');
INSERT INTO `creature_text` VALUES (103570, 1, 0, 'I will meet you on the other side.', 12, 0, 100, 0, 0, 0, 110286, 0, 'Mardranel Forestheart to Player');
INSERT INTO `creature_text` VALUES (103570, 2, 0, 'Your kittens are safe, Yowlon. Will you grant us your blessing so we may share this land with you?', 12, 0, 100, 1, 0, 0, 110918, 0, 'Mardranel Forestheart to Player');
INSERT INTO `creature_text` VALUES (103570, 3, 0, 'Thank you, your highness.', 12, 0, 100, 2, 0, 0, 110920, 0, 'Mardranel Forestheart to Player');
INSERT INTO `creature_text` VALUES (103571, 0, 0, 'Our efforts were not in vain. The Moonwhisper Matriarch watches from afar.', 12, 0, 100, 0, 0, 0, 108550, 0, 'Solendra Featherdown to Player');
INSERT INTO `creature_text` VALUES (103712, 0, 0, 'Yowlon purrs quietly.', 16, 0, 100, 0, 0, 0, 108520, 0, 'Yowlon to Player');
INSERT INTO `creature_text` VALUES (103712, 1, 0, 'Yowlon leans downward to pluck a feather from his ankle tufts.', 16, 0, 100, 0, 0, 0, 110919, 0, 'Yowlon to Player');
INSERT INTO `creature_text` VALUES (103712, 2, 0, 'Yowlon deposits the feather into Mardranel\'s palm.', 16, 0, 100, 0, 0, 0, 110921, 0, 'Yowlon to Player');
INSERT INTO `creature_text` VALUES (104234, 0, 0, 'There were more of us when we set out. I would have died, if it were not for Mayruna\'s arrival.', 12, 0, 100, 0, 0, 0, 119942, 0, 'Val\'sharah Refugee to Player');
INSERT INTO `creature_text` VALUES (104235, 0, 0, 'We owe our lives to Mythandros and his druid companions. We can call Suramar home, thanks to him.', 12, 0, 100, 0, 0, 0, 119943, 0, 'Val\'sharah Refugee to Player');
INSERT INTO `creature_text` VALUES (104478, 0, 0, 'Lost Owlkitten slowly raises its head and looks around.', 16, 0, 100, 0, 0, 0, 110283, 0, 'Lost Owlkitten to Player');
INSERT INTO `creature_text` VALUES (104478, 1, 0, 'Lost Owlkitten spots you and scampers away in fear!', 16, 0, 100, 0, 0, 0, 110284, 0, 'Lost Owlkitten to Player');
INSERT INTO `creature_text` VALUES (104478, 2, 0, 'Lost Owlkitten raises its nose to the air and chirps in recognition of its surroundings.', 16, 0, 100, 377, 0, 0, 106914, 0, 'Lost Owlkitten to Player');
INSERT INTO `creature_text` VALUES (104998, 0, 0, 'Thalyssra lives? Come, let us talk in private.', 12, 0, 100, 0, 0, 67474, 107605, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105071, 0, 0, 'We shall use the garden entrance.', 12, 0, 100, 0, 0, 67506, 108574, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105071, 1, 0, 'Enchanted masks. Simply don the mask, and a magical costume will envelop your body.', 12, 0, 100, 0, 0, 67507, 108575, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105071, 2, 0, 'It should be enough to fool the other guests, but stay clear of the guards.', 12, 0, 100, 0, 0, 67508, 108577, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105071, 3, 0, 'I will find Ly\'leth. You stay out of trouble.', 12, 0, 100, 0, 0, 67509, 108576, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105071, 4, 0, 'Just enjoy the party for a bit. You know... mingle.', 12, 0, 100, 0, 0, 67510, 108943, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (105342, 0, 0, 'This is not up for debate, Anarys. You are either with us, or against us. Choose wisely.', 12, 0, 100, 0, 0, 67504, 109087, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105342, 1, 0, '<sigh> Dear child... Do you not realize what he died for?', 12, 0, 100, 0, 0, 67505, 109089, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105342, 2, 0, 'Our father\'s memorial is just around the corner. I am sure you will find Anarys nearby.', 12, 0, 100, 0, 0, 67238, 114850, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105342, 3, 0, 'An\'anora!', 12, 0, 100, 0, 0, 67226, 109431, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105342, 4, 0, 'True. You will have to make an appearance.', 12, 0, 100, 0, 0, 67227, 109285, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (105351, 0, 0, 'Hm. Yes. Fascinating.', 12, 0, 100, 0, 0, 0, 108409, 0, 'Masqued Reveler to Player');
INSERT INTO `creature_text` VALUES (105351, 1, 0, 'Curious...', 12, 0, 100, 0, 0, 0, 108406, 0, 'Masqued Reveler to Player');
INSERT INTO `creature_text` VALUES (105351, 2, 0, 'You know, the Magistrix has spies everywhere.', 12, 0, 100, 0, 0, 0, 108407, 0, 'Masqued Reveler to Player');
INSERT INTO `creature_text` VALUES (105351, 3, 0, 'Who let you in here?', 12, 0, 100, 0, 0, 0, 108411, 0, 'Masqued Reveler to Player');
INSERT INTO `creature_text` VALUES (105351, 4, 0, 'Psst. Table behind the house. Lady Ly\'leth is waiting for you.', 12, 0, 100, 0, 0, 0, 108443, 0, 'Masqued Reveler to Player');
INSERT INTO `creature_text` VALUES (105633, 0, 0, 'AAAHHHhhhh!', 14, 0, 100, 0, 0, 0, 103052, 0, 'Understone Drudge to Understone Drudge');
INSERT INTO `creature_text` VALUES (105636, 0, 0, 'Intruders? Here?! Break them!', 14, 0, 100, 0, 0, 0, 102910, 0, 'Understone Drudge');
INSERT INTO `creature_text` VALUES (105759, 0, 0, 'Who goes there?!', 12, 0, 100, 0, 0, 65802, 105557, 0, 'High Mage of the Duskwatch to Player');
INSERT INTO `creature_text` VALUES (105759, 1, 0, 'An illusion! What are you hiding?', 12, 0, 100, 0, 0, 66003, 105559, 0, 'High Mage of the Duskwatch to Player');
INSERT INTO `creature_text` VALUES (106239, 0, 0, 'I will hear no more of your scheming! You endanger everything our father worked for!', 12, 0, 100, 274, 0, 67503, 116440, 0, 'Anarys Lunastre to Player');
INSERT INTO `creature_text` VALUES (106256, 0, 0, 'Free... finally free. How do I get out of here?', 12, 0, 100, 0, 0, 0, 122228, 0, 'Nightborne Captive to Webbed Victim');
INSERT INTO `creature_text` VALUES (106256, 1, 0, 'Please... kill every last one of these monsters.', 12, 0, 100, 0, 0, 0, 122229, 0, 'Nightborne Captive to Webbed Victim');
INSERT INTO `creature_text` VALUES (106256, 2, 0, 'I... I think I can make it. Thank you.', 12, 0, 100, 0, 0, 0, 122227, 0, 'Nightborne Captive to Webbed Victim');
INSERT INTO `creature_text` VALUES (106495, 0, 0, 'Dusk lily pods? I see...', 12, 0, 100, 0, 0, 67258, 109660, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (106495, 1, 0, 'Please excuse me. I must address this immediately.', 12, 0, 100, 0, 0, 67259, 109662, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (106696, 0, 0, 'It is done. I will dispose of the ashes and find Mayruna back at camp. Thank you, friend.', 12, 0, 100, 0, 0, 0, 110907, 0, 'Kalendros Irongrove to Player');
INSERT INTO `creature_text` VALUES (107114, 0, 0, 'The Tormenting Orb stares at all players inquisitively.', 16, 0, 100, 0, 0, 0, 113561, 0, 'Tormenting Orb to Tormenting Orb');
