INSERT INTO `creature_text` VALUES (114549, 1, 0, 'Mind is... slipping...', 12, 0, 100, 0, 0, 0, 120313, 0, 'Withering Suramar Citizen to Player');
INSERT INTO `creature_text` VALUES (114876, 0, 0, 'You flee at the cost of your life!', 12, 0, 100, 0, 0, 0, 124634, 0, 'Vengeful Wrathguard');
INSERT INTO `creature_text` VALUES (114876, 1, 0, 'Stay inside if you want to live!', 12, 0, 100, 0, 0, 0, 124633, 0, 'Vengeful Wrathguard');
INSERT INTO `creature_text` VALUES (115631, 0, 0, 'Hurry! We\'re not safe here!', 12, 0, 100, 0, 0, 0, 124196, 0, 'Desperate Refugee to Player');
INSERT INTO `creature_text` VALUES (115631, 1, 0, 'I am safe... thank you.', 12, 0, 100, 0, 0, 0, 124195, 0, 'Desperate Refugee');
INSERT INTO `creature_text` VALUES (115697, 0, 0, 'There are demons everywhere...', 12, 0, 100, 0, 0, 55370, 124198, 0, 'Desperate Refugee to Player');
INSERT INTO `creature_text` VALUES (115697, 1, 0, 'This city has devolved to madness...', 12, 0, 100, 0, 0, 0, 124194, 0, 'Desperate Refugee');
INSERT INTO `creature_text` VALUES (115703, 0, 0, 'We... we were almost killed!', 12, 0, 100, 0, 0, 0, 124193, 0, 'Desperate Refugee');
INSERT INTO `creature_text` VALUES (117412, 0, 0, 'This cannot be...', 12, 0, 100, 0, 0, 65692, 116434, 0, 'Felborne Trapper to Player');
INSERT INTO `creature_text` VALUES (97729, 3, 0, 'Desecrator...', 12, 0, 100, 0, 0, 66045, 102387, 0, 'Disturbed Apparition to Player');
INSERT INTO `creature_text` VALUES (97750, 0, 0, 'My mind is... cleared. I will sleep now. Give Thaedris my love.', 12, 0, 100, 0, 0, 65609, 102867, 0, 'Latara to Player');
INSERT INTO `creature_text` VALUES (98306, 0, 0, 'Curses upon you!', 12, 0, 100, 0, 0, 61255, 121847, 0, 'Cliffclutch Screecher to Player');
INSERT INTO `creature_text` VALUES (99065, 0, 0, 'Off with you, then. Find me some cliffthorn.', 12, 0, 100, 0, 0, 65625, 100992, 0, 'Thaedris Feathersong to Player');
INSERT INTO `creature_text` VALUES (99485, 0, 0, 'This world will... burn...', 12, 0, 100, 0, 0, 0, 101128, 0, 'Kozak the Afflictor to Player');
INSERT INTO `creature_text` VALUES (99581, 0, 0, 'We are Legion.', 12, 0, 100, 0, 0, 55369, 102139, 0, 'Wrathguard Fury to Player');
INSERT INTO `creature_text` VALUES (99581, 1, 0, 'Feel my wrath!', 12, 0, 100, 0, 0, 55368, 102138, 0, 'Wrathguard Fury to Player');
INSERT INTO `creature_text` VALUES (99593, 0, 0, 'You will pay for attacking my daughters!', 12, 0, 100, 0, 0, 0, 102857, 0, 'Cliffclutch Matriarch to Player');
INSERT INTO `creature_text` VALUES (99593, 1, 0, 'First the cave dwellers, now this...', 12, 0, 100, 0, 0, 0, 102861, 0, 'Cliffclutch Matriarch to Player');
INSERT INTO `creature_text` VALUES (99762, 0, 0, 'For the Legion!', 12, 0, 100, 0, 0, 55192, 96663, 0, 'Legion Endbringer to Player');
INSERT INTO `creature_text` VALUES (99762, 1, 0, 'You are easily dealt with.', 12, 0, 100, 0, 0, 55196, 102131, 0, 'Legion Endbringer to Player');
INSERT INTO `creature_text` VALUES (99762, 2, 0, 'You dare attack us here?!', 12, 0, 100, 0, 0, 55191, 96662, 0, 'Legion Endbringer to Player');
INSERT INTO `creature_text` VALUES (99851, 0, 0, 'My glaives have gone too long without tasting demon blood...', 12, 0, 100, 0, 0, 67593, 101595, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (99851, 1, 0, 'If you wish to find me when Grimwing is slain... follow the trail of demon corpses.', 12, 0, 100, 0, 0, 71821, 105159, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (99890, 0, 0, 'Speak now, demon. Lead me to your master and I may let you live.', 12, 0, 100, 0, 0, 71820, 105176, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100053, 0, 0, 'Now that the portal has been opened, the path to Azoran is clear.', 12, 0, 100, 0, 0, 67601, 105686, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100301, 0, 0, 'We must leave now. Soon we will be home.', 12, 0, 100, 0, 0, 65731, 102081, 0, 'Astoril to Player');
INSERT INTO `creature_text` VALUES (100301, 1, 0, 'Do not be afraid. Soon all will be set right.', 12, 0, 100, 0, 0, 65754, 102082, 0, 'Astoril to Player');
INSERT INTO `creature_text` VALUES (100301, 2, 0, 'We are here.', 12, 0, 100, 0, 0, 65729, 102100, 0, 'Astoril to Player');
INSERT INTO `creature_text` VALUES (100301, 3, 0, 'Silence. I tire of your prattling. Do not worry, it will all be over soon.', 12, 0, 100, 0, 0, 65730, 102102, 0, 'Astoril to Player');
INSERT INTO `creature_text` VALUES (100301, 4, 0, 'FEL MASTERS, I BRING YOU YOUR FINAL SACRIFICE! Nine times nine souls I have brought you, my own kith and kin!', 12, 0, 100, 0, 0, 65740, 102103, 0, 'Astoril to Player');
INSERT INTO `creature_text` VALUES (100331, 0, 0, 'Yes, I am ready. Let us make haste. I worry that we will be found by our enemies.', 12, 0, 100, 0, 0, 65932, 102083, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (100331, 1, 0, 'I don\'t understand. This isn\'t the city. Wher-', 12, 0, 100, 0, 0, 65933, 102101, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (100595, 0, 0, 'Begone, filthy $c! Baelbug find the glaive and keeps it!', 12, 0, 100, 0, 0, 0, 106040, 0, 'Baelbug to Player');
INSERT INTO `creature_text` VALUES (100595, 1, 0, 'You no take warglaive!', 12, 0, 100, 0, 0, 0, 102723, 0, 'Baelbug to Player');
INSERT INTO `creature_text` VALUES (100779, 0, 0, 'I thank you for giving me newfound purpose. I will meet you at Shal\'Aran. I think I will be needing a source of magic after all.', 12, 0, 100, 0, 0, 65622, 103155, 0, 'Thaedris Feathersong to Player');
INSERT INTO `creature_text` VALUES (100823, 0, 0, 'Enter the portal and fight your way to Azoran.', 12, 0, 100, 0, 0, 67599, 101993, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100823, 1, 0, 'Do not worry about reinforcements. I will guard this portal with my life.', 12, 0, 100, 0, 0, 71817, 105725, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100823, 2, 0, 'Time is short... Go swiftly!', 12, 0, 100, 0, 0, 71818, 105726, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (100878, 0, 0, 'The Burning Legion has been dealt a great blow this day, but our work is far from over.', 12, 0, 100, 0, 0, 67600, 105972, 0, 'Lyana Darksorrow to Player');
INSERT INTO `creature_text` VALUES (101878, 0, 0, 'You cannot hide from me.', 12, 0, 100, 0, 0, 55374, 102155, 0, 'Felsoul Inquisitor to Player');
INSERT INTO `creature_text` VALUES (102127, 0, 0, 'What do you think you\'re doing?', 12, 0, 100, 0, 0, 68252, 119572, 0, 'Menagerie Keeper to Player');
INSERT INTO `creature_text` VALUES (102127, 1, 0, 'Stop that at once!', 12, 0, 100, 0, 0, 68251, 119571, 0, 'Menagerie Keeper to Player');
INSERT INTO `creature_text` VALUES (102365, 0, 0, 'Your human friend was taken by the demons. Grant me my freedom and I will tell you where he was taken!', 12, 0, 100, 0, 0, 0, 106088, 0, 'Selthaes Starsong to Player');
INSERT INTO `creature_text` VALUES (102365, 1, 0, 'Your friend was taken to the felstalker pits beneath the great Burning Legion command ship.', 12, 0, 100, 0, 0, 0, 106191, 0, 'Selthaes Starsong to Player');
INSERT INTO `creature_text` VALUES (102365, 2, 0, 'Sadly, it\'s probably too late for him... don\'t foolishly throw your lives away for a dead man!', 12, 0, 100, 0, 0, 0, 106192, 0, 'Selthaes Starsong to Player');
INSERT INTO `creature_text` VALUES (103155, 1, 0, 'Hmm... The main conduit beneath Suramar city pulls ten times what we have here.', 12, 0, 100, 0, 0, 67321, 118619, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103155, 2, 0, 'If that leyline were to flare up, the surge could theoretically reach all the way back to Shal\'Aran.', 12, 0, 100, 0, 0, 67322, 118620, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103437, 0, 0, 'We gotta get to Brambley, before it\'s too late!', 12, 0, 100, 0, 0, 0, 105916, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 1, 0, 'Which fist do ye want? How about both!', 12, 0, 100, 0, 0, 0, 106072, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 2, 0, 'Who ordered a knuckle sandwich?!', 12, 0, 100, 0, 0, 0, 106067, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 3, 0, 'Och, what happened to yer face? MAH FISTS!', 12, 0, 100, 0, 0, 0, 106071, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 4, 0, 'Aye, let\'s get him the key... but be on your guard. This place is crawling with demons.', 12, 0, 100, 0, 0, 0, 105917, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 5, 0, 'Don\'t worry, I\'ll go easy on ye!', 12, 0, 100, 0, 0, 0, 106069, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 6, 0, 'Ye wanna fight, eh? I\'ll give ye a fight!', 12, 0, 100, 0, 0, 0, 106064, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 7, 0, 'Now, THAT\'S a face worth punching!', 12, 0, 100, 0, 0, 0, 106068, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 8, 0, 'Who needs an axe when ye\'ve got fists o\' steel!', 12, 0, 100, 0, 0, 0, 106065, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 9, 0, 'Och! This one fell off the ugly tree an\' hit my fists on the way down!', 12, 0, 100, 0, 0, 0, 106066, 0, 'Angus Stormbrew');
INSERT INTO `creature_text` VALUES (103437, 10, 0, 'Look! That\'s Brambley\'s helm, I\'d recognize it anywhere!', 12, 0, 100, 0, 0, 0, 105919, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 11, 0, 'Brambley! No... no it canna\' be true!', 12, 0, 100, 0, 0, 0, 105918, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 12, 0, 'Those demons musta\' ate ol\' Brambley... look at him! He\'s just bones, now!', 12, 0, 100, 18, 0, 0, 105920, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 13, 0, 'Curses upon ye, demons! CURSES UPON YE!', 12, 0, 100, 20, 0, 0, 105924, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 14, 0, 'Don\'t worry Brambley, I\'ll carry yer wee head to safety!', 12, 0, 100, 381, 0, 0, 106438, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 15, 0, 'Here lies his mighty arm that defended the realm in so many battles!', 12, 0, 100, 381, 0, 0, 106442, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 16, 0, 'I\'ll take ye home Brambley! Piece by piece, I swear it!', 12, 0, 100, 381, 0, 0, 106445, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 17, 0, 'Och, looks like the demons chewed on this one. Aye, it\'s still moist...', 12, 0, 100, 381, 0, 0, 106446, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 18, 0, 'Good eye! That\'s his alright... I\'d know it a mile away!', 12, 0, 100, 381, 0, 0, 106444, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 19, 0, 'It\'s Brambley\'s Dagger! We\'ll take it home an\' bury it with the rest o\' him.', 12, 0, 100, 381, 0, 0, 106447, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 20, 0, 'I can still hear Brambley\'s voice in my head, tellin\' me to stay alive.', 12, 0, 100, 0, 0, 0, 106514, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103437, 21, 0, 'I will laddy! I\'ll stay alive for the both of us!', 12, 0, 100, 0, 0, 0, 106515, 0, 'Angus Stormbrew to Player');
INSERT INTO `creature_text` VALUES (103634, 0, 0, 'Su\'esh eyes the wounds on Graedis.', 16, 0, 100, 0, 0, 0, 106272, 0, 'Su\'esh to Graedis Oenthar');
INSERT INTO `creature_text` VALUES (103634, 1, 0, 'Su\'esh looks hungrily at Graedis.', 16, 0, 100, 0, 0, 0, 106273, 0, 'Su\'esh to Graedis Oenthar');
INSERT INTO `creature_text` VALUES (103670, 0, 0, 'Angus, I\'m alive! I\'m up here!', 14, 0, 100, 0, 0, 0, 106452, 0, 'Brambley Morrison to Player');
INSERT INTO `creature_text` VALUES (103670, 1, 0, 'It IS my voice you daft dwarf! I\'m ALIVE!', 14, 0, 100, 0, 0, 0, 106453, 0, 'Brambley Morrison to Player');
INSERT INTO `creature_text` VALUES (103677, 0, 0, 'Enough of this! To me, Su\'esh! Destroy these interlopers!', 14, 0, 100, 0, 0, 65641, 106261, 0, 'Graedis Oenthar');
INSERT INTO `creature_text` VALUES (103677, 1, 0, 'What\'s this? Listen to me! I am the alpha!', 12, 0, 100, 0, 0, 65642, 106262, 0, 'Graedis Oenthar to Su\'esh');
INSERT INTO `creature_text` VALUES (103677, 2, 0, 'Is that how it is? Clever girl.', 12, 0, 100, 0, 0, 65643, 106263, 0, 'Graedis Oenthar to Su\'esh');
INSERT INTO `creature_text` VALUES (104161, 0, 0, 'We should get out of here before it\'s too late.', 12, 0, 100, 0, 0, 0, 123298, 0, 'Brambley Morrison to Player');
INSERT INTO `creature_text` VALUES (105640, 0, 0, 'The creature shakes its head, coming to its senses!', 16, 0, 100, 0, 0, 0, 107211, 0, 'Freed Goat');
INSERT INTO `creature_text` VALUES (105759, 2, 0, 'An illusion! What are you hiding?', 12, 0, 100, 0, 0, 66003, 124629, 0, 'High Mage of the Duskwatch to Player');
INSERT INTO `creature_text` VALUES (106375, 0, 0, 'Quickly, grab them!', 12, 0, 100, 0, 0, 55202, 96653, 0, 'Felsoul Trickster to Player');
INSERT INTO `creature_text` VALUES (106375, 1, 0, 'Help! Baddies here!', 12, 0, 100, 0, 0, 55202, 96655, 0, 'Felsoul Trickster to Player');
INSERT INTO `creature_text` VALUES (106375, 2, 0, 'Hmm... medium or well-done?', 12, 0, 100, 0, 0, 55202, 102137, 0, 'Felsoul Trickster to Player');
INSERT INTO `creature_text` VALUES (106375, 3, 0, 'Tasty meats for me to eats.', 12, 0, 100, 0, 0, 55207, 96658, 0, 'Felsoul Trickster to Player');
INSERT INTO `creature_text` VALUES (106375, 4, 0, 'Hey! You\'re not supposed to be here.', 12, 0, 100, 0, 0, 55206, 96657, 0, 'Felsoul Trickster to Player');
INSERT INTO `creature_text` VALUES (107134, 0, 0, 'Well well, the little elf returns. You are most... persistent.', 12, 0, 100, 0, 0, 0, 111300, 0, 'Projection of Aargoss to Player');
INSERT INTO `creature_text` VALUES (107134, 1, 0, 'You have fulfilled your end of the bargain. As promised, I give you a taste of the Legion\'s power. Earn more by proving your strength!', 12, 0, 100, 25, 0, 0, 111301, 0, 'Projection of Aargoss to Player');
INSERT INTO `creature_text` VALUES (107481, 0, 0, 'It will be easier for me to travel on the road.', 12, 0, 100, 0, 0, 65938, 116542, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 1, 0, 'You must protect me!', 12, 0, 100, 0, 0, 66396, 118677, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 2, 0, 'Look at them... they are not even people any more. I cannot turn into one of them. We must reach Astoril quickly. Clear the path faster!', 12, 0, 100, 0, 0, 65951, 104334, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 3, 0, 'We must hurry.', 12, 0, 100, 0, 0, 66391, 118672, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 4, 0, 'I am too weak to defend myself. Help!', 12, 0, 100, 0, 0, 66393, 118674, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 5, 0, 'More exiles... like me. All I did was punish those servants... You would have done the same!', 12, 0, 100, 0, 0, 65931, 104335, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107481, 6, 0, 'You must be Astoril! Finally! The first thing I shall do when I get back in the city is drink a cool, tall glass of arcwine.', 12, 0, 100, 0, 0, 65934, 104336, 0, 'Iadreth to Player');
INSERT INTO `creature_text` VALUES (107603, 3, 0, 'Please help me... please...', 12, 0, 100, 0, 0, 0, 114606, 0, 'Shal\'dorei Civilian to Vineyard Enforcer');
INSERT INTO `creature_text` VALUES (108190, 0, 0, 'Your death is certain!', 12, 0, 100, 0, 0, 65904, 116682, 0, 'Magisterial Sleuth to Player');
INSERT INTO `creature_text` VALUES (108870, 0, 0, 'Oh no...', 12, 0, 100, 0, 0, 66343, 115290, 0, 'Sylverin to Player');
INSERT INTO `creature_text` VALUES (108871, 0, 0, 'I shall dispose of you, lowborn!', 12, 0, 100, 0, 0, 66024, 116678, 0, 'Vineyard Warden to Player');
INSERT INTO `creature_text` VALUES (108872, 0, 0, 'Margaux glances to each side, silently assessing the situation.', 16, 0, 100, 0, 0, 0, 114972, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108872, 1, 0, 'A tour? Of course! I will show you the grounds myself.', 12, 0, 100, 0, 0, 66287, 114973, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108872, 2, 0, 'The courtyard you just left is open to visitors. From here forward, one of my staff must accompany you.', 12, 0, 100, 0, 0, 66288, 114978, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108872, 3, 0, 'You can purchase souvenirs and gifts from Sylverin, my assistant. Perhaps you could do that on your way out?', 12, 0, 100, 0, 0, 66289, 114979, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108872, 4, 0, 'This is our tasting pavilion. We serve only the finest varietals here - many of them enjoyed in the palace!', 12, 0, 100, 0, 0, 66290, 114980, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108872, 5, 0, 'Feel free to try the samples we have available today. We will continue the tour when you are finished.', 12, 0, 100, 0, 0, 66291, 114982, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (108875, 0, 0, 'The Vineyards are open to special guests only. You must leave immediately.', 12, 0, 100, 0, 0, 0, 114600, 0, 'Vineyard Enforcer');
INSERT INTO `creature_text` VALUES (108875, 1, 0, 'You are not authorized to enter the Vineyard.', 12, 0, 100, 0, 0, 0, 114598, 0, 'Vineyard Enforcer');
INSERT INTO `creature_text` VALUES (108875, 2, 0, 'Stand back!', 12, 0, 100, 0, 0, 0, 114763, 0, 'Vineyard Enforcer to Shal\'dorei Civilian');
INSERT INTO `creature_text` VALUES (108875, 3, 0, 'Very good.', 12, 0, 100, 0, 0, 0, 121508, 0, 'Vineyard Enforcer to Vineyard Laborer');
INSERT INTO `creature_text` VALUES (108875, 4, 0, 'You there! Back to work!', 12, 0, 100, 6, 0, 0, 115111, 0, 'Vineyard Enforcer to Player');
INSERT INTO `creature_text` VALUES (108875, 5, 0, 'I will hear none of your excuses. Back to work!', 12, 0, 100, 0, 0, 0, 115113, 0, 'Vineyard Enforcer to Player');
INSERT INTO `creature_text` VALUES (108875, 6, 0, 'The Overseer will hear of this. Mark my words, Margaux.', 12, 0, 100, 0, 0, 0, 115115, 0, 'Vineyard Enforcer to Player');
INSERT INTO `creature_text` VALUES (108877, 0, 0, 'This is a heavy blend of barely-ripe shadowberry and manafruit, triple infused with Nightwell essence. It is best appreciated in small sips.', 12, 0, 100, 0, 0, 66178, 114934, 0, 'Sacheverell Leburque to Player');
INSERT INTO `creature_text` VALUES (108877, 1, 0, 'This three year Infusion incorporates delightful berry elements with a scintillating melon bouquet. Refreshing, yes?', 12, 0, 100, 0, 0, 66176, 114932, 0, 'Sacheverell Leburque to Player');
INSERT INTO `creature_text` VALUES (108877, 2, 0, 'Ah, this is a fine vintage. Seventh generation manafruit entangled with slippery nightsap flavors and a delicate walnut finish.', 12, 0, 100, 0, 0, 66177, 114933, 0, 'Sacheverell Leburque to Player');
INSERT INTO `creature_text` VALUES (108931, 0, 0, 'I was just resting. I-', 12, 0, 100, 0, 0, 0, 115112, 0, 'Vineyard Laborer to Player');
INSERT INTO `creature_text` VALUES (108931, 1, 0, 'Agh!', 12, 0, 100, 0, 0, 66279, 115114, 0, 'Vineyard Laborer to Player');
INSERT INTO `creature_text` VALUES (109144, 0, 0, 'I\'ve procured a palanquin for you. Try to look imperious- it helps with the illusion.', 12, 0, 100, 0, 0, 67565, 114863, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (109144, 1, 0, 'The woman you are looking for is named Margaux. She has shown interest in our cause.', 12, 0, 100, 0, 0, 67566, 114864, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (109223, 0, 0, 'The building we approach now is called the Aging Chamber.', 12, 0, 100, 1, 0, 66268, 115021, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 1, 0, 'If you are at all familiar with the wine crafting process, you know that it is necessary to allow the juice to ferment.', 12, 0, 100, 1, 0, 66271, 115025, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 2, 0, 'We prefer to shorten the process using time magic. Such power is our birthright!', 12, 0, 100, 603, 0, 66269, 115022, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 3, 0, 'To my left you will see one of our orchards.', 12, 0, 100, 1, 0, 66270, 115023, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 4, 0, 'This one houses a species of tree that produces extra sweet fruit, which you tasted in the Infusion.', 12, 0, 100, 1, 0, 66272, 115026, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 5, 0, 'The buildings before you house our bottling facilities.', 12, 0, 100, 1, 0, 66273, 115028, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 6, 0, 'Each bottle is sealed with magic to prevent any of the Nightwell\'s essence from fading.', 12, 0, 100, 1, 0, 66274, 115029, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 7, 0, 'A cask of arcwine from our vineyard can be safely stored for hundreds of years!', 12, 0, 100, 603, 0, 66275, 115030, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 8, 0, 'Ahead you will see our distribution facility.', 12, 0, 100, 1, 0, 66276, 115031, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 9, 0, 'Here we arrange to send hundreds of barrels and casks of arcwine out to the city.', 12, 0, 100, 1, 0, 66277, 115032, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 10, 0, 'The Magistrix monitors these activities very-', 12, 0, 100, 1, 0, 66278, 115033, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 11, 0, 'Don\'t hurt him!', 12, 0, 100, 603, 0, 66279, 115034, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 12, 0, 'I am so sorry- please forgive this mistake. I will personally ensure it does not happen again.', 12, 0, 100, 20, 0, 66280, 115035, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 13, 0, 'Where were we?', 12, 0, 100, 6, 0, 66281, 115036, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 14, 0, 'Across this bridge is the most crucial part of our winemaking process.', 12, 0, 100, 1, 0, 66282, 115038, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 15, 0, 'It is here that we instill our wine with Nightwell essence, which gives it the restorative properties our people enjoy.', 12, 0, 100, 1, 0, 66283, 115039, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 16, 0, 'And this is where I leave you. I must prepare the next activity.', 12, 0, 100, 1, 0, 66284, 115040, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (109223, 17, 0, 'I will meet you at the next station. My assistant Sylverin will be happy to show you how the infusion process works.', 12, 0, 100, 273, 0, 66285, 115042, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (110931, 0, 0, 'Come to Furog, tiny elf-monsters!', 12, 0, 100, 0, 0, 0, 119271, 0, 'Furog the Elfbreaker');
INSERT INTO `creature_text` VALUES (110931, 1, 0, 'I break your elf-thing!', 12, 0, 100, 0, 0, 0, 119284, 0, 'Furog the Elfbreaker to Withered Exile');
INSERT INTO `creature_text` VALUES (110955, 0, 0, 'Die, beast!', 12, 0, 100, 0, 0, 0, 119459, 0, 'Hulking Shocktrooper to Su\'esh');
INSERT INTO `creature_text` VALUES (111002, 0, 0, 'The enemy may be using magic to deceive us! Do not let your guard down!', 14, 0, 100, 0, 0, 0, 119473, 0, 'Doombringer Sinarus');
INSERT INTO `creature_text` VALUES (111002, 1, 0, 'Move quickly, and crush any who resist the Legion!', 14, 0, 100, 0, 0, 0, 119469, 0, 'Doombringer Sinarus');
INSERT INTO `creature_text` VALUES (111013, 0, 0, 'Kill it! Kill it now!', 12, 0, 100, 0, 0, 0, 119462, 0, 'Felsoul Champion to Su\'esh');
INSERT INTO `creature_text` VALUES (111013, 1, 0, 'Fools! Su\'esh has escaped the menagerie!', 12, 0, 100, 0, 0, 0, 119461, 0, 'Felsoul Champion to Su\'esh');
INSERT INTO `creature_text` VALUES (111013, 2, 0, 'Su\'esh is loose! To arms!', 12, 0, 100, 0, 0, 0, 119460, 0, 'Felsoul Champion to Su\'esh');
INSERT INTO `creature_text` VALUES (112055, 0, 0, 'Take your place near the table. Once we begin, it is important that we keep up the pace. These delicate ingredients spoil quickly.', 12, 0, 100, 1, 0, 66296, 115231, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 1, 0, 'The juices need a little massaging- step into the vat and jump to squish the mixture around!', 12, 0, 100, 25, 0, 66303, 115242, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 2, 0, 'The arcwine has soured... you must start over!', 42, 0, 100, 0, 0, 66293, 123135, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 3, 0, 'Are you certain you are ready to participate? This batch will have to be destroyed.', 12, 0, 100, 0, 0, 66293, 115228, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 4, 0, 'I feel as though you are losing focus. Is something else on your mind? Step back for a moment and we will try again.', 12, 0, 100, 0, 0, 66294, 115229, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 5, 0, 'Well done. Now let me see...', 12, 0, 100, 1, 0, 66298, 115236, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 6, 0, 'Let us add a little moisture. Just enough to keep the taps flowing.', 12, 0, 100, 0, 0, 66301, 115240, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 7, 0, 'Exactly what it needed. But there is still something missing...', 12, 0, 100, 0, 0, 66299, 115238, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 8, 0, 'You, my friend, are a natural. The blend needs something more, though. Let me think...', 12, 0, 100, 0, 0, 66297, 115237, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 9, 0, 'Ah! It needs something floral, to add a little brightness.', 12, 0, 100, 0, 0, 66300, 115239, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (112055, 10, 0, 'Excellent! You did very well- are you certain you have never made wine before? I will have this bottled immediately.', 12, 0, 100, 1, 0, 66297, 115230, 0, 'Margaux to Player');
INSERT INTO `creature_text` VALUES (113573, 0, 0, 'Grawk... the Mother will get you...', 12, 0, 100, 0, 0, 0, 102726, 0, 'Cliffclutch Thornwitch to Player');
INSERT INTO `creature_text` VALUES (114926, 0, 0, 'HALT. Inspection required.', 12, 0, 100, 0, 0, 0, 119793, 0, 'Arcane Sentinel to Player');
INSERT INTO `creature_text` VALUES (114998, 0, 0, 'Let there be no slack in their chains!', 14, 0, 100, 0, 0, 53329, 123540, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (115691, 0, 0, 'Your death is fated!', 12, 0, 100, 0, 0, 65814, 116703, 0, 'Felborne Magus to Player');
INSERT INTO `creature_text` VALUES (115691, 1, 0, 'I shall dispose of you, lowborn!', 12, 0, 100, 0, 0, 66024, 116692, 0, 'Felborne Magus to Player');
INSERT INTO `creature_text` VALUES (116050, 0, 0, 'The shal\'dorei will rule this world!', 12, 0, 100, 0, 0, 66019, 116430, 0, 'Duskwatch Conjurer to Player');
INSERT INTO `creature_text` VALUES (98208, 2, 0, 'I have not survived for ten thousand years just to fall to a swarm of rats.', 14, 0, 100, 0, 0, 58646, 0, 0, 'VANDROS - AGGRO');
INSERT INTO `creature_text` VALUES (98208, 3, 0, 'Have you vermin ever witnessed such raw power? I think not.', 14, 0, 100, 0, 0, 58639, 0, 0, 'VANDROS - CHRONO_SHARDS');
INSERT INTO `creature_text` VALUES (98208, 4, 0, 'I am a conduit of the Nightwell\'s glorious power! I am NIGHTBORNE!', 14, 0, 100, 0, 0, 58638, 0, 0, 'VANDROS - BANISH_IN_TIME');
INSERT INTO `creature_text` VALUES (98206, 0, 0, 'Form up, maggots! Secure the breach until we receive orders from Gul\'dan!', 14, 0, 100, 0, 0, 58644, 0, 0, 'XAKAL - INTRO');
INSERT INTO `creature_text` VALUES (98206, 1, 0, 'Good! Some entertainment! I was getting restless!', 14, 0, 100, 0, 0, 58645, 0, 0, 'XAKAL - AGGRO');
INSERT INTO `creature_text` VALUES (98206, 2, 0, 'All who rise against the Legion are doomed!', 14, 0, 100, 0, 0, 58646, 0, 0, 'XAKAL - FEL_FISSURE');
INSERT INTO `creature_text` VALUES (98203, 0, 0, 'Stay back! It\'s mine!', 14, 0, 100, 0, 0, 58644, 0, 0, 'IVANYR - INTRO');
INSERT INTO `creature_text` VALUES (98206, 3, 0, 'All in my shadow will crumble and fall!', 14, 0, 100, 0, 0, 58639, 0, 0, 'XAKAL - SHADOW_SLASH');
INSERT INTO `creature_text` VALUES (98203, 1, 0, 'Thieves! The mana is MINE! ', 14, 0, 100, 0, 0, 58645, 0, 0, 'IVANYR - AGGRO');
INSERT INTO `creature_text` VALUES (98203, 2, 0, 'Go away!', 14, 0, 100, 0, 0, 58646, 0, 0, 'IVANYR - VOLATILE_MAGIC');
INSERT INTO `creature_text` VALUES (98548, 1, 0, 'Now this is important - once that manastorm gets going, there will be too much interference for me to teleport anyone. You must get out before the storm!', 12, 0, 100, 5, 0, 67334, 118647, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (98548, 2, 0, 'Alright everybody, gather in close. Do not be shy now. Group hug.', 12, 0, 100, 0, 0, 67337, 118644, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (98548, 3, 0, 'Ahh, thank you.', 12, 0, 100, 0, 0, 71457, 121587, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (104615, 0, 0, 'Lemme hear your bones crunch!', 12, 0, 100, 0, 0, 54860, 96412, 0, 'Greystone Flanker to Player');
INSERT INTO `creature_text` VALUES (103155, 3, 0, 'A manastorm, are you insane?', 12, 0, 100, 0, 0, 67325, 118624, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103155, 4, 0, 'Right away.', 12, 0, 100, 0, 0, 67329, 118628, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (103769, 0, 0, 'The Nightmare has corrupted dragons, gods, and legends. Your downfall will be simple and swift!', 14, 0, 100, 0, 0, 58726, 109326, 0, 'Xavius');
INSERT INTO `creature_text` VALUES (103769, 1, 0, 'I\'ll slice into the heart of you!', 14, 0, 100, 0, 0, 58737, 109348, 0, 'Xavius to Player');
INSERT INTO `creature_text` VALUES (103769, 2, 0, 'Time to crush your dreams!', 14, 0, 100, 0, 0, 58751, 109353, 0, 'Xavius');
INSERT INTO `creature_text` VALUES (103769, 3, 0, 'Nightmares are... never... forgotten...', 14, 0, 100, 0, 0, 58736, 109347, 0, 'Xavius to Player');
INSERT INTO `creature_text` VALUES (104245, 0, 0, '|TInterface\\Icons\\INV_Misc_Bell_01:20|tAn alarm has been sounded! A reinforcement has entered the fray!', 41, 0, 100, 0, 0, 0, 109270, 0, 'Arcane Beacon to Duskwatch Sentry');
INSERT INTO `creature_text` VALUES (104278, 2, 0, 'The Emissary must be notified!', 14, 0, 100, 0, 0, 71112, 109579, 0, 'Felbound Enforcer to Player');
INSERT INTO `creature_text` VALUES (104695, 1, 0, '%s runs away in terror!', 16, 0, 100, 11, 0, 0, 121586, 0, 'Suramar Merchant');
INSERT INTO `creature_text` VALUES (104696, 1, 0, 'How did you get in here?', 12, 0, 100, 0, 0, 0, 121051, 0, 'Suramar Civilian');
INSERT INTO `creature_text` VALUES (105759, 3, 0, 'None may challenge us!', 12, 0, 100, 0, 0, 66026, 116701, 0, 'High Mage of the Duskwatch to Player');
INSERT INTO `creature_text` VALUES (108930, 0, 0, 'Legion, come to our aid!', 12, 0, 100, 0, 0, 65978, 114783, 0, 'Nightborne Envoy');
INSERT INTO `creature_text` VALUES (107126, 4, 0, 'The arcan\'dor is at a critical stage.', 12, 0, 100, 0, 0, 67313, 118611, 0, 'Valewalker Farodin to Player');
INSERT INTO `creature_text` VALUES (107126, 5, 0, 'Without enough power to mature into its final form, it will die.', 12, 0, 100, 0, 0, 67314, 118612, 0, 'Valewalker Farodin to Player');
INSERT INTO `creature_text` VALUES (107126, 6, 0, 'It is not enough...', 12, 0, 100, 0, 0, 67319, 118617, 0, 'Valewalker Farodin to Player');
INSERT INTO `creature_text` VALUES (107253, 0, 0, 'You\'ll find Lorin in the square. Sylessa frequents the canals. Just search the bazaar for Cyrille. They\'ll catch your eye.', 12, 0, 100, 0, 0, 66207, 117194, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107253, 1, 0, 'Is that what she\'s up to? Brave girl. I\'d be delighted to help, but...', 12, 0, 100, 0, 0, 68043, 119243, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107253, 2, 0, 'Always a pleasure. Do things my way and she\'ll be advisor before dinner.', 12, 0, 100, 0, 0, 66192, 116295, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107253, 3, 0, 'Arluin beckons you over.', 16, 0, 100, 0, 0, 0, 121788, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107253, 4, 0, 'I... will go home. I have no business among such esteemed individuals.', 12, 0, 100, 1, 0, 66188, 116511, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107253, 5, 0, 'I accept your gratitude, by the way!', 12, 0, 100, 603, 0, 66189, 116512, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (107333, 0, 0, 'Intruder! Face my blade!', 12, 0, 100, 0, 0, 69824, 115553, 0, 'Overseer Durant');
INSERT INTO `creature_text` VALUES (107333, 1, 0, 'She sealed her fate, as you have sealed yours!', 12, 0, 100, 0, 0, 69825, 115554, 0, 'Overseer Durant');
INSERT INTO `creature_text` VALUES (107333, 2, 0, 'You cannot defy me!', 12, 0, 100, 0, 0, 69827, 115597, 0, 'Overseer Durant');
INSERT INTO `creature_text` VALUES (107333, 3, 0, 'Elisande will have your heads for this!', 12, 0, 100, 0, 0, 69828, 115598, 0, 'Overseer Durant');
INSERT INTO `creature_text` VALUES (107333, 4, 0, 'T-traitors...', 12, 0, 100, 0, 0, 69829, 111870, 0, 'Overseer Durant to Player');
INSERT INTO `creature_text` VALUES (107342, 0, 0, 'You will bow to us!', 12, 0, 100, 0, 0, 65667, 116700, 0, 'Duskwatch Spell-Fencer to Player');
INSERT INTO `creature_text` VALUES (107348, 0, 0, 'Understood.', 12, 0, 100, 0, 0, 0, 132592, 0, 'Sylessa to Player');
INSERT INTO `creature_text` VALUES (107349, 0, 0, 'Very well.', 12, 0, 100, 0, 0, 0, 114217, 0, 'Lorin to Player');
INSERT INTO `creature_text` VALUES (107350, 0, 0, 'Say no more.', 12, 0, 100, 0, 0, 0, 111644, 0, 'Cyrille to Player');
INSERT INTO `creature_text` VALUES (107350, 1, 0, 'Vengeance for Margaux!', 12, 0, 100, 0, 0, 69825, 115599, 0, 'Cyrille to Overseer Durant');
INSERT INTO `creature_text` VALUES (107350, 2, 0, 'I can see why Arluin put his faith in you. Give him our regards.', 12, 0, 100, 0, 0, 0, 111927, 0, 'Cyrille to Player');
INSERT INTO `creature_text` VALUES (107350, 3, 0, 'Cyrille places a Dusk Lily atop Overseer Durant\'s corpse.', 16, 0, 100, 0, 0, 0, 120682, 0, 'Cyrille to Player');
INSERT INTO `creature_text` VALUES (107632, 0, 0, 'There is a reason we live on the opposite side of the city, my friend.', 12, 0, 100, 274, 0, 67567, 116281, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 1, 0, 'I could change much for our people... yes...', 12, 0, 100, 1, 0, 67568, 116282, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 2, 0, 'Meet me within the Sanctum of Order. I must prepare for the ceremony.', 12, 0, 100, 1, 0, 68065, 119291, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 3, 0, 'I humbly accept this appointment. It is an honor beyond words.', 12, 0, 100, 0, 0, 66267, 116515, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 4, 0, 'My lord, you have lost your sense of decorum. It is already decided.', 12, 0, 100, 1, 0, 66265, 116519, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 5, 0, 'Tal\'ashar it is. I name my friend here as my champion.', 12, 0, 100, 25, 0, 68147, 119381, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 6, 0, 'I am modifying your disguise to allow you to fight like one of us.', 15, 0, 100, 0, 0, 68150, 119388, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 7, 0, 'Win me a title, won\'t you?', 12, 0, 100, 1, 0, 68151, 119389, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 8, 0, 'I wish this had not been necessary, but you have done well.', 12, 0, 100, 0, 0, 68152, 119395, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (107632, 9, 0, 'I will send you back to the tavern when you are ready.', 12, 0, 100, 0, 0, 68153, 119396, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (108871, 1, 0, 'The shal\'dorei are... chosen...', 12, 0, 100, 0, 0, 66014, 116624, 0, 'Vineyard Warden to Player');
INSERT INTO `creature_text` VALUES (108871, 2, 0, 'The shal\'dorei will rule!', 12, 0, 100, 0, 0, 65801, 116707, 0, 'Vineyard Warden to Player');
INSERT INTO `creature_text` VALUES (109048, 0, 0, 'To me, servant!', 12, 0, 100, 0, 0, 0, 114785, 0, 'Arcane Engineer to Player');
INSERT INTO `creature_text` VALUES (109461, 0, 0, 'My hand slipped! Forgive me!', 12, 0, 100, 430, 0, 0, 121692, 0, 'Vineyard Porter to Player');
INSERT INTO `creature_text` VALUES (109461, 1, 0, 'Flee! The Magistrix will kill us for failing her!', 12, 0, 100, 430, 0, 0, 115419, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT INTO `creature_text` VALUES (109461, 2, 0, 'Thank you.', 12, 0, 100, 0, 0, 0, 115479, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT INTO `creature_text` VALUES (109461, 3, 0, 'Take the arcwine. I am getting out of here.', 12, 0, 100, 1, 0, 0, 115481, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT INTO `creature_text` VALUES (109509, 0, 0, 'This shipment is worth more than you are! How dare you treat it so carelessly!', 12, 0, 100, 0, 0, 0, 121691, 0, 'Spellsinger Quiris to Player');
INSERT INTO `creature_text` VALUES (109509, 1, 0, 'You will pay for this delay!', 12, 0, 100, 0, 0, 0, 115487, 0, 'Spellsinger Quiris to Player');
INSERT INTO `creature_text` VALUES (109509, 2, 0, 'Elisande... will hear... of this...', 12, 0, 100, 0, 0, 0, 115488, 0, 'Spellsinger Quiris to Player');
INSERT INTO `creature_text` VALUES (109670, 0, 0, 'Show your identification. COMPLY.', 12, 0, 100, 0, 0, 0, 119796, 0, 'Chronarch Defender to Player');
INSERT INTO `creature_text` VALUES (109670, 1, 0, 'Scanning arcane essence...', 12, 0, 100, 0, 0, 65819, 119797, 0, 'Chronarch Defender to Player');
INSERT INTO `creature_text` VALUES (109670, 2, 0, 'Stand by for screening.', 12, 0, 100, 0, 0, 66004, 119794, 0, 'Chronarch Defender to Player');
INSERT INTO `creature_text` VALUES (110354, 0, 0, 'I will wrestle my title from your champion\'s lifeless hands, Ly\'leth!', 12, 0, 100, 0, 0, 66216, 116532, 0, 'Coryn');
INSERT INTO `creature_text` VALUES (110354, 1, 0, 'You cannot match me! I am your better!', 12, 0, 100, 0, 0, 66217, 116533, 0, 'Coryn');
INSERT INTO `creature_text` VALUES (110354, 2, 0, 'I will have what is owed me!', 12, 0, 100, 0, 0, 66220, 116534, 0, 'Coryn');
INSERT INTO `creature_text` VALUES (110354, 3, 0, 'I was... framed...', 12, 0, 100, 0, 0, 66221, 116535, 0, 'Coryn to Enhanced Illusion');
INSERT INTO `creature_text` VALUES (110365, 0, 0, 'A challenger? Don\'t make me laugh!', 12, 0, 100, 0, 0, 66332, 116536, 0, 'Ruven');
INSERT INTO `creature_text` VALUES (110365, 1, 0, 'The Legion brings us power unending!', 12, 0, 100, 0, 0, 66333, 116537, 0, 'Ruven');
INSERT INTO `creature_text` VALUES (110365, 2, 0, 'I will not be defeated by the likes of you!', 12, 0, 100, 0, 0, 66335, 116539, 0, 'Ruven');
INSERT INTO `creature_text` VALUES (110365, 3, 0, 'They... promised...', 12, 0, 100, 0, 0, 66336, 116540, 0, 'Ruven to Player');
INSERT INTO `creature_text` VALUES (110538, 0, 0, 'We must learn why the old arcan\'dor failed. Its shattered bones may have the answers.', 12, 0, 100, 0, 0, 66383, 118555, 0, 'Valewalker Farodin to Player');
INSERT INTO `creature_text` VALUES (110654, 0, 0, 'Run!', 12, 0, 100, 0, 0, 66336, 118743, 0, 'Frightened Laborer to Player');
INSERT INTO `creature_text` VALUES (110655, 0, 0, 'Get moving!', 12, 0, 100, 0, 0, 0, 118738, 0, 'Duskmere Smuggler to Player');
INSERT INTO `creature_text` VALUES (110656, 0, 0, 'You will not interfere with my research!', 12, 0, 100, 0, 0, 0, 118752, 0, 'Arcanist Lylandre to Lylandre\'s Fire Crystal');
INSERT INTO `creature_text` VALUES (110679, 0, 0, 'You are not leaving my sight until an advisor is appointed. Promise me!', 12, 0, 100, 0, 0, 0, 119494, 0, 'Aurore to Player');
INSERT INTO `creature_text` VALUES (110679, 1, 0, 'Thank you. Do let Ly\'leth know that I stand with her.', 12, 0, 100, 0, 0, 65866, 119496, 0, 'Aurore to Player');
INSERT INTO `creature_text` VALUES (110680, 0, 0, 'I think they meant to kill me!', 12, 0, 100, 603, 0, 0, 118819, 0, 'Ailen Astravar to Player');
INSERT INTO `creature_text` VALUES (110680, 1, 0, 'I am getting off this infernal ship! I will return to my mother at once.', 12, 0, 100, 603, 0, 0, 119490, 0, 'Ailen Astravar to Player');
INSERT INTO `creature_text` VALUES (110804, 0, 0, 'Yes, mother.', 12, 0, 100, 0, 0, 0, 119495, 0, 'Ailen to Player');
INSERT INTO `creature_text` VALUES (110874, 0, 0, 'That is the symbol of House Stelleris... but that could not be. Coryn was Ruven\'s dearest friend...', 12, 0, 100, 1, 0, 0, 119179, 0, 'Distraught Noble to Player');
INSERT INTO `creature_text` VALUES (110874, 1, 0, 'No, that\'s simply impossible...', 12, 0, 100, 274, 0, 0, 119180, 0, 'Distraught Noble to Player');
INSERT INTO `creature_text` VALUES (110875, 0, 0, 'Preposterous! A joke at my lord\'s expense? Only a madman would attempt it!', 12, 0, 100, 5, 0, 0, 119181, 0, 'Shamed Noble to Player');
INSERT INTO `creature_text` VALUES (110875, 1, 0, '... But if anyone would laugh, it would be THEM.', 12, 0, 100, 1, 0, 0, 119182, 0, 'Shamed Noble to Player');
INSERT INTO `creature_text` VALUES (110876, 0, 0, 'Absolutely NOT! Why would he do such a thing? Has he no honor?!', 12, 0, 100, 5, 0, 0, 119177, 0, 'Disgruntled Servant to Player');
INSERT INTO `creature_text` VALUES (110876, 1, 0, '... Why WOULD he do such a thing...?', 12, 0, 100, 1, 0, 0, 119178, 0, 'Disgruntled Servant to Player');
INSERT INTO `creature_text` VALUES (110967, 0, 0, 'Filthy backstabbing cretin! That title should have been mine!', 12, 0, 100, 603, 0, 66003, 116517, 0, 'Coryn to Player');
INSERT INTO `creature_text` VALUES (110967, 1, 0, 'You impinge upon my honor! You besmirch my name with baseless accusations!', 12, 0, 100, 603, 0, 66214, 116518, 0, 'Coryn to Player');
INSERT INTO `creature_text` VALUES (110967, 2, 0, 'I will reclaim my honor! I invoke the ancient rite- Tal\'ashar!', 12, 0, 100, 603, 0, 66219, 116524, 0, 'Coryn to Player');
INSERT INTO `creature_text` VALUES (110967, 3, 0, 'I have no need to hide behind a champion. I will defend my own honor.', 12, 0, 100, 1, 0, 68148, 119382, 0, 'Coryn to Player');
INSERT INTO `creature_text` VALUES (110967, 4, 0, 'Meet me atop the Sanctum when you are ready to die, whelp.', 12, 0, 100, 603, 0, 68149, 119383, 0, 'Coryn to Player');
INSERT INTO `creature_text` VALUES (111484, 0, 0, 'Now is the age of the shal\'dorei!', 12, 0, 100, 0, 0, 65707, 116677, 0, 'Duskwatch Spellshield to Hungering Withered');
INSERT INTO `creature_text` VALUES (111485, 0, 0, 'Lowborn scum!', 12, 0, 100, 0, 0, 65907, 116685, 0, 'Duskwatch Spellshield to Player');
INSERT INTO `creature_text` VALUES (111485, 1, 0, 'Anath\'ashar!', 12, 0, 100, 0, 0, 65910, 116688, 0, 'Duskwatch Spellshield to Player');
INSERT INTO `creature_text` VALUES (111485, 2, 0, 'Die, outlander!', 12, 0, 100, 0, 0, 65909, 116708, 0, 'Duskwatch Spellshield to Player');
