INSERT INTO `creature_text` VALUES (96759, 2, 0, 'Your fate rains down!', 14, 0, 100, 0, 0, 54378, 0, 0, 'HELYA - TAIN_OF_SEA');
INSERT INTO `creature_text` VALUES (96759, 2, 1, 'Embrace your fate, fools!', 14, 0, 100, 0, 0, 54388, 0, 0, 'HELYA - TAIN_OF_SEA');
INSERT INTO `creature_text` VALUES (96759, 3, 0, 'You will pay for that.', 14, 0, 100, 0, 0, 54385, 0, 0, 'HELYA - TENTACLE DIE');
