INSERT INTO `creature_text` VALUES (96756, 0, 0, 'Mortals... Your wretched kind murdered MY people... destroyed MY kingdom! Long have I waited for a chance to repay what was done to me!', 14, 0, 100, 0, 0, 54370, 0, 0, 'YMIRON - AGGRO_1');
INSERT INTO `creature_text` VALUES (96756, 0, 1, 'Mortals... Your wretched kind murdered MY people... destroyed MY kingdom! Now you, too, will know what it feels like to lose EVERYTHING!', 14, 0, 100, 0, 0, 54371, 0, 0, 'YMIRON - AGGRO_2');
INSERT INTO `creature_text` VALUES (96756, 0, 2, 'Mortals... I fell to your wretched kind once before. It WILL NOT happen again!', 14, 0, 100, 0, 0, 54403, 0, 0, 'YMIRON - AGGRO_3');
INSERT INTO `creature_text` VALUES (96756, 1, 0, 'CRAVEN MAGGOTS!', 14, 0, 100, 0, 0, 54361, 0, 0, 'YMIRON - SCREAM OF DEAD');
INSERT INTO `creature_text` VALUES (96756, 1, 1, 'FLEE, COWARDS!', 14, 0, 100, 0, 0, 54360, 0, 0, 'YMIRON - SCREAM OF DEAD');
INSERT INTO `creature_text` VALUES (96756, 1, 2, 'COWER BEFORE ME!', 14, 0, 100, 0, 0, 54359, 0, 0, 'YMIRON - SCREAM OF DEAD');
INSERT INTO `creature_text` VALUES (96754, 0, 0, 'The spirits grow hungry from the scent of blood', 14, 0, 100, 0, 0, 54387, 0, 0, 'HARBARON - INTRO');
