INSERT INTO `creature_text` VALUES (96754, 2, 0, 'Arise, servitor! Claim our blood debt', 14, 0, 100, 0, 0, 54388, 0, 0, 'HARBARON - SCREAM OF DEAD');
INSERT INTO `creature_text` VALUES (96754, 2, 1, 'Spirits! Sweep these mortals into the tides!', 14, 0, 100, 0, 0, 54385, 0, 0, 'HARBARON - SCREAM OF DEAD');
INSERT INTO `creature_text` VALUES (96754, 2, 2, 'If I cannot bleed the payment for you, the spirits will!', 14, 0, 100, 0, 0, 54386, 0, 0, 'HARBARON - SCREAM OF DEAD');
