INSERT INTO `creature_text` VALUES (110698, 2, 0, 'I understand... He never could stay out of trouble...', 12, 0, 100, 0, 0, 67308, 118776, 0, 'Arluelle to Player');
INSERT INTO `creature_text` VALUES (110698, 3, 0, 'Thank you... I will remember this.', 12, 0, 100, 0, 0, 67309, 118777, 0, 'Arluelle to Player');
INSERT INTO `creature_text` VALUES (111523, 0, 0, 'Something\'s not quite right...', 12, 0, 100, 1, 0, 65803, 118519, 0, 'Duskwatch Warpcaster to Player');
INSERT INTO `creature_text` VALUES (111620, 0, 0, 'Die, outlander!', 12, 0, 100, 0, 0, 65820, 116708, 0, 'Duskwatch Executor to Player');
INSERT INTO `creature_text` VALUES (111901, 0, 0, 'Now, as before, we do what we must to carry on.', 12, 0, 100, 11, 0, 0, 125324, 0, 'Suramar Refugee to Player');
INSERT INTO `creature_text` VALUES (111902, 0, 0, 'This fruit actually cured Thalyssra? Amazing.', 12, 0, 100, 7, 0, 0, 125323, 0, 'Suramar Refugee to Player');
INSERT INTO `creature_text` VALUES (112145, 0, 0, 'Perhaps there is a new path for us after all...', 12, 0, 100, 0, 0, 67374, 118753, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (112145, 1, 0, 'You have my thanks, outlander.', 12, 0, 100, 0, 0, 67375, 118756, 0, 'Arcanist Valtrois to Player');
INSERT INTO `creature_text` VALUES (112146, 0, 0, 'This tree came at a high cost... You are sure it can sustain us?', 12, 0, 100, 0, 0, 67331, 118739, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (112146, 1, 0, 'We are on the brink of a new age for the Nightborne.', 12, 0, 100, 0, 0, 67376, 118771, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (112146, 2, 0, 'Without you, none of this would have been possible.', 12, 0, 100, 0, 0, 67377, 118772, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (112146, 3, 0, 'For now, we shall gather our strength. When the time comes, I trust we can call upon you to help us liberate Suramar.', 12, 0, 100, 0, 0, 67378, 118773, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (112147, 0, 0, 'Hmm... a little tart, with a hint of- oh, my... there it is.', 12, 0, 100, 0, 0, 67372, 118645, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (112147, 1, 0, 'Astounding.', 12, 0, 100, 0, 0, 67373, 118742, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (113061, 0, 0, 'Yes. We are close.', 12, 0, 100, 0, 0, 67339, 118660, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 1, 0, 'There! Place the manastorm generator near the conduit.', 12, 0, 100, 0, 0, 67342, 118665, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 2, 0, 'Something is wrong... The temporal warp sequencer-', 12, 0, 100, 0, 0, 67344, 118669, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 3, 0, 'The fuse is broken. We must trigger the manastorm manually.', 12, 0, 100, 0, 0, 67346, 118670, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 4, 0, 'We have no choice. This must be done.', 12, 0, 100, 0, 0, 67348, 118681, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 5, 0, 'Rath, anu\'telar!', 12, 0, 100, 463, 0, 67349, 118682, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 6, 0, 'We just need to hold out until the storm is over!', 12, 0, 100, 0, 0, 67350, 118683, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 7, 0, 'Erath-omnas!', 12, 0, 100, 0, 0, 67353, 118704, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 8, 0, 'It is too dangerous! The arcane torrents would tear us apart!', 12, 0, 100, 0, 0, 67355, 118705, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 9, 0, 'What are you doing?!', 12, 0, 100, 0, 0, 67358, 81078, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 10, 0, 'Arluin! NO!!', 12, 0, 100, 0, 0, 67359, 118709, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 11, 0, 'Oculeth, get us out of here!', 12, 0, 100, 0, 0, 67361, 118711, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113061, 12, 0, 'Ugh... we made it...', 12, 0, 100, 0, 0, 67362, 118712, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 13, 0, 'Where is Arluin?!', 12, 0, 100, 0, 0, 67363, 118713, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 14, 0, 'Oh no...', 12, 0, 100, 0, 0, 67364, 115290, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 15, 0, 'Do not speak! You are wounded-', 12, 0, 100, 0, 0, 67366, 118716, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 16, 0, 'Yes. You saved us. Please... try to rest.', 12, 0, 100, 0, 0, 67368, 118719, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 17, 0, 'He is gone.', 12, 0, 100, 0, 0, 67370, 118720, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113061, 18, 0, 'Come... Let us check on the others.', 12, 0, 100, 0, 0, 67371, 118721, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (113062, 0, 0, 'This is not looking real conduit-y. You sure the old man knows his stuff?', 12, 0, 100, 0, 0, 67338, 118661, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 1, 0, 'Whatever you gotta do, do it quick! Lots of guards heading our way.', 12, 0, 100, 0, 0, 67343, 118668, 0, 'Arluin to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113062, 2, 0, 'Small words, please.', 12, 0, 100, 0, 0, 67345, 118679, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 3, 0, 'Wait, aren\'t we supposed to leave BEFORE the storm starts?', 12, 0, 100, 0, 0, 67347, 118680, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 4, 0, 'Easier said than done...', 12, 0, 100, 0, 0, 67351, 118684, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 5, 0, 'The manastorm is only getting stronger! We need to shut that thing down!', 12, 0, 100, 0, 0, 67354, 118700, 0, 'Arluin to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113062, 6, 0, 'Damn it...', 12, 0, 100, 0, 0, 67356, 118706, 0, 'Arluin to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113062, 7, 0, 'You guys owe me...', 12, 0, 100, 0, 0, 67357, 118707, 0, 'Arluin to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113062, 8, 0, 'Hhghh-AAAHHHH!!!', 14, 0, 100, 16, 0, 67360, 118710, 0, 'Arluin to First Mate Snellig');
INSERT INTO `creature_text` VALUES (113062, 9, 0, '<cough> Nghh...', 12, 0, 100, 0, 0, 67365, 118715, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 10, 0, 'We pulled it off... <wheeze> didn\'t we?', 12, 0, 100, 0, 0, 67367, 118718, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113062, 11, 0, 'Hey... <cough> Tell Vanthir I said... thanks... for every...', 12, 0, 100, 0, 0, 67369, 118717, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113114, 0, 0, 'I heard about this crazy job you guys are planning. You weren\'t thinking of leaving me out, were you?', 12, 0, 100, 0, 0, 67301, 118629, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113114, 1, 0, 'Look, I am not blind. I see what is happening in Suramar.', 12, 0, 100, 1, 0, 67303, 118630, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113114, 2, 0, 'I am not sitting this one out. The stakes are too high. So I am in, you got that?', 12, 0, 100, 0, 0, 67304, 118631, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113114, 3, 0, 'Oh and don\'t worry about my usual fee - you can pay me when the job is done.', 12, 0, 100, 1, 0, 67305, 118632, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113114, 4, 0, 'Get in, plant the device, get out before it goes off. Not the most complicated job I have been on.', 12, 0, 100, 1, 0, 67335, 118648, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113166, 0, 0, 'You will bow to us!', 12, 0, 100, 0, 0, 65667, 116700, 0, 'Duskwatch Arcblade to First Arcanist Thalyssra');
INSERT INTO `creature_text` VALUES (113166, 1, 0, 'Scum like you should obey your betters.', 12, 0, 100, 0, 0, 65867, 116706, 0, 'Duskwatch Arcblade to Player');
INSERT INTO `creature_text` VALUES (113207, 0, 0, 'I never met a lock I could not open.', 12, 0, 100, 0, 0, 67340, 118663, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113207, 1, 0, 'Just give me one moment.', 12, 0, 100, 0, 0, 67341, 118664, 0, 'Arluin to Player');
INSERT INTO `creature_text` VALUES (113208, 0, 0, 'Look out!', 12, 0, 100, 0, 0, 67341, 114489, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (114472, 0, 0, 'Arash-falas...', 12, 0, 100, 0, 0, 65900, 116431, 0, 'Duskwatch Highblade to Player');
INSERT INTO `creature_text` VALUES (114474, 1, 0, 'Im... impossible.', 12, 0, 100, 0, 0, 65688, 116429, 0, 'Duskwatch Highblade to Player');
INSERT INTO `creature_text` VALUES (114868, 0, 0, 'It is hopeless to resist...', 12, 0, 100, 0, 0, 66020, 116783, 0, 'Felborne Enforcer to Player');
INSERT INTO `creature_text` VALUES (114904, 0, 0, 'Line them up!', 14, 0, 100, 36, 0, 0, 123594, 0, 'Soul Engine Jailer to Player');
INSERT INTO `creature_text` VALUES (114904, 1, 0, 'Ah, look, another prisoner!', 12, 0, 100, 0, 0, 0, 123595, 0, 'Soul Engine Jailer to Player');
INSERT INTO `creature_text` VALUES (114904, 2, 0, 'Just in time for the next ferry. It\'s off to the Soul Engine with you!', 12, 0, 100, 0, 0, 0, 123746, 0, 'Soul Engine Jailer to Player');
INSERT INTO `creature_text` VALUES (114948, 0, 0, 'There you are! Over here!', 12, 0, 100, 0, 0, 78391, 124353, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 1, 0, 'Elisande knows about Vanthir\'s involvement in the rebellion. He is gone.', 12, 0, 100, 0, 0, 78392, 123461, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 2, 0, 'I will move ahead and clear out a safe haven for the survivors. Stay strong, my friend.', 12, 0, 100, 0, 0, 78360, 124361, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 3, 0, 'Now all we need is a to make contact with--', 12, 0, 100, 0, 0, 78554, 123716, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 4, 0, 'Thalyssra?!', 12, 0, 100, 0, 0, 65777, 124165, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 5, 0, 'What manner of spell is this?', 12, 0, 100, 0, 0, 78524, 124166, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 6, 0, 'Fear not, First Arcanist. I will see to it that our friend here comes to no harm.', 12, 0, 100, 0, 0, 78594, 124160, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114948, 7, 0, 'I am sadly familiar with Vanthir\'s captor.', 12, 0, 100, 0, 0, 78595, 124161, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (114958, 0, 0, 'Elisande must be stopped!', 12, 0, 100, 0, 0, 79245, 123450, 0, 'Duskwatch Subjugator to Player');
INSERT INTO `creature_text` VALUES (114958, 1, 0, 'This is too much... if I must be labeled a traitor then so be it!', 12, 0, 100, 397, 0, 79245, 123451, 0, 'Duskwatch Subjugator to Player');
INSERT INTO `creature_text` VALUES (114958, 2, 0, 'I knew there were others! I will no longer bloody my hands for Elisande!', 12, 0, 100, 0, 0, 79245, 123449, 0, 'Duskwatch Subjugator to Player');
INSERT INTO `creature_text` VALUES (114978, 0, 0, 'Oh, thank you! This masterpiece is worth my life a hundred times over.', 12, 0, 100, 603, 0, 0, 123472, 0, 'Master Devlyn to Player');
INSERT INTO `creature_text` VALUES (114983, 0, 0, 'Behind you!', 12, 0, 100, 0, 0, 0, 124769, 0, 'Thorvos to Player');
INSERT INTO `creature_text` VALUES (114983, 1, 0, 'I am right behind you! Go!', 12, 0, 100, 603, 0, 0, 124770, 0, 'Thorvos to Player');
INSERT INTO `creature_text` VALUES (114984, 0, 0, 'F-father... Run...', 12, 0, 100, 396, 0, 0, 123479, 0, 'Valanth to Player');
INSERT INTO `creature_text` VALUES (114985, 0, 0, '%s spits on the corpse of the demon.', 16, 0, 100, 0, 0, 0, 123473, 0, 'Scarleth to Player');
INSERT INTO `creature_text` VALUES (114985, 1, 0, 'I know where to find Shal\'Aran. Come, ladies. We are no longer safe in the city.', 12, 0, 100, 396, 0, 0, 123476, 0, 'Scarleth to Player');
INSERT INTO `creature_text` VALUES (114998, 1, 0, 'More come from Suramar every hour! Move out! Make room!', 14, 0, 100, 0, 0, 0, 123543, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (114998, 2, 0, 'We must not leave the Soul Engine waiting!', 14, 0, 100, 0, 0, 78451, 123542, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (114998, 3, 0, 'Ha! You walk willingly to your death, mortal!', 12, 0, 100, 0, 0, 78444, 124519, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (114998, 4, 0, 'I will take great pleasure in grinding your soul to nothing!', 12, 0, 100, 0, 0, 78445, 124520, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (114998, 5, 0, 'Shaza-kiel!', 12, 0, 100, 0, 0, 78446, 124521, 0, 'Aargoss to Player');
INSERT INTO `creature_text` VALUES (114998, 6, 0, 'You are nothing! Insignificant gnat!', 12, 0, 100, 0, 0, 78447, 124522, 0, 'Aargoss');
INSERT INTO `creature_text` VALUES (114998, 7, 0, 'No! ... I will be... fuel...', 12, 0, 100, 0, 0, 78448, 124523, 0, 'Aargoss to Player');
INSERT INTO `creature_text` VALUES (115014, 0, 0, 'Hmm... medium or well-done?', 12, 0, 100, 0, 0, 53329, 102137, 0, 'Felsoul Imp to Player');
INSERT INTO `creature_text` VALUES (115014, 1, 0, 'Tasty meats for me to eats.', 12, 0, 100, 0, 0, 55207, 96658, 0, 'Felsoul Imp to Player');
INSERT INTO `creature_text` VALUES (115018, 0, 0, 'I hope she sees this...', 12, 0, 100, 0, 0, 78550, 124501, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115018, 1, 0, 'This way! Everyone into the boat!', 12, 0, 100, 0, 0, 78551, 124502, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115018, 2, 0, 'Excellent timing as usual, Deline.', 12, 0, 100, 0, 0, 78552, 124503, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115049, 0, 0, 'See that these people escape the city, Deline. $n, stay with me.', 12, 0, 100, 0, 0, 78557, 123713, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115049, 1, 0, 'This way!', 12, 0, 100, 397, 0, 78553, 123714, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115078, 0, 0, 'He will be fine. The fruit will bring him back.', 12, 0, 100, 0, 0, 78582, 124539, 0, 'Arluelle to Player');
INSERT INTO `creature_text` VALUES (115243, 0, 0, 'I have urgent news, Thalyssra.', 12, 0, 100, 0, 0, 78611, 124022, 0, 'Image of Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115243, 1, 0, 'The portal to my estate is secure. You will want to hear this in person.', 12, 0, 100, 0, 0, 78612, 124023, 0, 'Image of Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115247, 0, 0, 'Welcome home, First Arcanist. Thank you for coming on such short notice.', 12, 0, 100, 0, 0, 0, 124055, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115247, 1, 0, 'I have heard terrible rumors about our friend Vanthir.', 12, 0, 100, 0, 0, 78616, 124063, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115247, 2, 0, 'He is to be made an example for all to see.', 12, 0, 100, 0, 0, 78617, 124535, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115247, 3, 0, 'I do not know. I only hope we are not too late.', 12, 0, 100, 0, 0, 78619, 124537, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115247, 4, 0, 'Be safe, Thalyssra. I must return to the palace.', 12, 0, 100, 0, 0, 78406, 124075, 0, 'Ly\'leth Lunastre to Player');
INSERT INTO `creature_text` VALUES (115248, 0, 0, 'Thank you, Advisor. What news?', 12, 0, 100, 0, 0, 78615, 124056, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115248, 1, 0, 'How much time do we have?', 12, 0, 100, 0, 0, 78618, 124536, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115248, 2, 0, 'Keep your voice down, Silgryn. It is good to see you too.', 12, 0, 100, 0, 0, 78408, 124164, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115248, 3, 0, 'One that will find our friend.', 12, 0, 100, 0, 0, 78525, 124167, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 0, 0, 'I will find him. Preferably without being seen.', 12, 0, 100, 0, 0, 78416, 123637, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 1, 0, 'This must be where he took many meals...', 12, 0, 100, 0, 0, 78409, 123698, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 2, 0, 'There it is. We are coming, Vanthir.', 12, 0, 100, 0, 0, 78410, 123699, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 3, 0, 'This garden likely gave him many hours of respite. His essence is strong and bright, here.', 12, 0, 100, 0, 0, 78411, 123700, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 4, 0, 'I can feel him! He is still alive, at least.', 12, 0, 100, 0, 0, 78412, 123701, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 5, 0, 'One of his suppliers must have served him here. Reminders of a simpler time.', 12, 0, 100, 0, 0, 78413, 123702, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115277, 6, 0, 'Perfect. This will be enough to find him.', 12, 0, 100, 0, 0, 78414, 123703, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 0, 0, 'Talk to me, Vanthir...', 12, 0, 100, 0, 0, 78526, 124129, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 1, 0, 'The moment he was taken from the Waning Crescent he felt such terrible fear... but not for himself.', 12, 0, 100, 0, 0, 78527, 124130, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 2, 0, 'Fear for those that he sheltered, those he sacrificed for.', 12, 0, 100, 0, 0, 78528, 124131, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 3, 0, 'He was taken somewhere dark and isolated. He began to feel hunger settling its roots in his gut.', 12, 0, 100, 0, 0, 78529, 124132, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 4, 0, 'Hunger that did not pass, but grew...', 12, 0, 100, 0, 0, 78530, 124133, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 5, 0, '... Until he could think of nothing else!', 12, 0, 100, 0, 0, 78531, 124134, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 6, 0, 'He is near to withering. His thoughts are jumbled, frantic... I just need to...', 12, 0, 100, 0, 0, 78532, 124135, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 7, 0, 'Focus! There!', 12, 0, 100, 0, 0, 78533, 124136, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 8, 0, 'He is being held at the Terrace of Order! It must be time for the display...', 12, 0, 100, 0, 0, 78534, 124137, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 9, 0, 'That took more out of me than I thought it would. You will have to free Vanthir without me. I am sorry.', 12, 0, 100, 0, 0, 78580, 124154, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115557, 10, 0, 'Thank you, Silgryn. Be careful.', 12, 0, 100, 0, 0, 78581, 124162, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (115604, 0, 0, 'Vanthir struggles to his feet.', 16, 0, 100, 0, 0, 0, 125238, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115604, 1, 0, 'Relief... at last...', 12, 0, 100, 0, 0, 78596, 124540, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115604, 2, 0, 'Aran\'ethil... The nightmare is over!', 12, 0, 100, 0, 0, 78597, 124541, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115604, 3, 0, 'They wanted to make an example. We have stolen their message and made it ours.', 12, 0, 100, 0, 0, 78568, 124543, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115604, 4, 0, 'All will see... hope... in the Dusk Lily.', 12, 0, 100, 0, 0, 78569, 124544, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115604, 5, 0, '<Cough.> I need... to rest now. Thank you. Thank you for everything.', 12, 0, 100, 0, 0, 78566, 124545, 0, 'Vanthir to Player');
INSERT INTO `creature_text` VALUES (115607, 0, 0, 'You think to stop this demonstration? Very well. I will make an example of you first!', 12, 0, 100, 0, 0, 78599, 124260, 0, 'Thoramir');
INSERT INTO `creature_text` VALUES (115607, 1, 0, 'She... was right... about you...', 12, 0, 100, 0, 0, 78607, 124269, 0, 'Thoramir to Arluelle');
INSERT INTO `creature_text` VALUES (115608, 0, 0, 'I know.', 12, 0, 100, 0, 0, 78608, 124270, 0, 'Silgryn to Arluelle');
INSERT INTO `creature_text` VALUES (115608, 1, 0, 'Lay still, my friend. We will get you out.', 12, 0, 100, 0, 0, 78610, 124538, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115608, 2, 0, 'Welcome back, old friend.', 12, 0, 100, 0, 0, 78598, 124542, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115608, 3, 0, 'We will see him back to safety. Go inform Thalyssra of our success.', 12, 0, 100, 0, 0, 78567, 124546, 0, 'Silgryn to Player');
INSERT INTO `creature_text` VALUES (115710, 0, 0, 'No need to delay. Onto the pad with you.', 12, 0, 100, 25, 0, 78389, 123457, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (115710, 1, 0, 'I will get you as close to the Crescent as I can. We will be in touch.', 12, 0, 100, 0, 0, 78390, 124348, 0, 'Chief Telemancer Oculeth to Player');
INSERT INTO `creature_text` VALUES (115736, 0, 0, 'She is waiting for you just outside.', 12, 0, 100, 1, 0, 78535, 124504, 0, 'First Arcanist Thalyssra to Player');
