INSERT INTO `creature_text` VALUES (17424, 0, 0, 'Now, let\'s see....', 12, 0, 100, 69, 0, 0, 14281, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17424, 1, 0, 'In the process of translating this, I believe I\'ve learned more about the drunken exploits of a certain \'Andrew Clementine\' than about the journal\'s author.', 12, 0, 100, 1, 0, 0, 14282, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17424, 2, 0, 'But, no matter. Mister Clementine\'s... err, \'contribution\' notwithstanding, this seems to be the journal of a man named Nolkai. The entries toward the end describe a brutal battle.', 12, 0, 100, 11, 0, 0, 14283, 0, 'Anchorite Paetheus');
