INSERT INTO `creature_text` VALUES (20913, 1, 0, 'It\'s working! Ghabar will be pleased to hear the news.', 12, 0, 100, 0, 0, 0, 18652, 0, 'Tashar');
INSERT INTO `creature_text` VALUES (17433, 0, 0, 'These pictographs tell a story. This figure at the beginning, almost looks like one of the creatures we encountered at Azure Watch...', 12, 0, 100, 0, 0, 0, 14030, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (17433, 1, 0, 'No, this can\'t be... It says this creature willingly became a servant of the Legion. He transforms into a satyr and receives the Legion\'s \"blessing.', 12, 0, 100, 274, 0, 0, 14031, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (17433, 2, 0, 'We must defeat these satyr, $n. We will not suffer the Legion\'s presence in these lands.', 12, 0, 100, 53, 0, 0, 14032, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (794, 0, 0, 'Dang! Fish arent biting here either. I am gonna go back to my ol fishin hole!', 12, 7, 100, 0, 0, 0, 894, 0, 'Matt');
