INSERT INTO `creature_text` VALUES (20281, 3, 0, 'Alright, pylon two down. Now for the heat manifold.', 12, 0, 100, 0, 0, 0, 17944, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (20281, 4, 0, 'That should do it. The teleporter should blow any second now!', 12, 0, 100, 0, 0, 0, 17945, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (20281, 5, 0, 'Ok, let\'s get out of here!', 12, 0, 100, 0, 0, 0, 17946, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (20281, 6, 0, 'Thank you, $N! I couldn\'t have done it without you. You\'ll let Gahruj know?', 12, 0, 100, 0, 0, 0, 17949, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (20281, 7, 0, 'Keep them off me!', 12, 0, 100, 0, 0, 0, 17950, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (20281, 8, 0, 'Very well.  Before we head down there, take a moment to prepare yourself.', 12, 0, 100, 0, 0, 0, 17940, 0, 'Drijya');
INSERT INTO `creature_text` VALUES (23843, 0, 0, 'I brought you a housewarming gift, Jarl.', 12, 0, 100, 1, 0, 0, 22348, 0, 'Mordant Grimsby');
INSERT INTO `creature_text` VALUES (23843, 1, 0, 'Go on, open it. I think you\'ll like it.', 12, 0, 100, 25, 0, 0, 22349, 0, 'Mordant Grimsby');
INSERT INTO `creature_text` VALUES (23843, 2, 0, 'Jarl?', 12, 0, 100, 0, 0, 0, 22350, 0, 'Mordant Grimsby');
