INSERT INTO `creature_text` VALUES (45966, 0, 0, 'We have to break through those mindless leper gnomes if we want to get out of here alive!', 12, 0, 100, 25, 0, 0, 46382, 0, 'Nevin Twistwrench');
INSERT INTO `creature_text` VALUES (42489, 0, 0, 'We can\'t afford to let our enemy, Thermaplugg, continue to tighten his grip on the rest of Gnomeregan.', 12, 0, 100, 1, 0, 0, 43103, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42489, 1, 0, 'He has ordered his crony, Razlo Crushcog, to keep us on the defensive. Hinkles, what\'s the latest intelligence on Razlo and his men?', 12, 0, 100, 1, 0, 0, 43104, 0, 'Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42491, 0, 0, 'It\'s been hard for our scouts to get information on what Crushcog is planning.', 12, 0, 100, 1, 0, 0, 43105, 0, 'Hinkles Fastblast to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42491, 1, 0, 'What we do know is that Crushcog is using an advanced mechano-tank prototype and his irradiated gnomes are trying to repair more at the old arsenal.', 12, 0, 100, 1, 0, 0, 43110, 0, 'Hinkles Fastblast to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42366, 0, 0, 'We\'re going to have a hard time handling this one alone. I sent a message to the dwarven capital of Ironforge asking for their support.', 12, 0, 100, 1, 0, 0, 43111, 0, 'Kelsey Steelspark to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42490, 0, 0, 'A messenger reached us from Brewnall Village this morning. The dwarves are sending a squad of mountaineers to help us.', 12, 0, 100, 1, 0, 0, 43117, 0, 'Elgin Clickspring to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42489, 2, 0, 'We have to be in position to strike against Crushcog when the mountaineers arrive.', 12, 0, 100, 1, 0, 0, 43118, 0, 'Captain Tread Sparknozzle to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42489, 3, 0, 'With Crushcog defeated, Thermaplugg is sure to be quaking in his mechano-tank, and rightly so. You\'re next, Thermaplugg. You\'re next!', 12, 0, 100, 1, 0, 0, 43119, 0, 'Captain Tread Sparknozzle to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42317, 0, 0, 'We assembled a mighty force and defeated Thermaplugg\'s servants, fighting our way into the city. But, the usurper Thermaplugg had one last trick up his sleeve...', 12, 0, 100, 1, 0, 20895, 42408, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42317, 1, 0, 'Take a look at this holotable. I want to show you a bit about Operation: Gnomeregan.', 12, 0, 100, 25, 0, 12148, 48277, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42317, 2, 0, 'We were forced to retreat. Thermaplugg continues to hide behind the radiation, sending his crony, Crushcog, to harass us here on the surface.', 12, 0, 100, 1, 0, 20896, 42411, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42419, 0, 0, 'It\'s too quiet. Where are Thermaplugg\'s defense forces?', 12, 0, 100, 1, 0, 17552, 39883, 0, 'Image of High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42419, 1, 0, 'IT\'S A TRAP! That\'s a fully functional irradiator!', 12, 0, 100, 5, 0, 17554, 39869, 0, 'Image of High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42419, 2, 0, 'Ten minutes? Plenty of time to disarm the device.', 12, 0, 100, 1, 0, 17556, 39893, 0, 'Image of High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42419, 3, 0, 'Hinkles, hand me that Whirring Bronze Gizmo and some Fresh Spring Water. Cogspin, toss me that Dirty Trogg Cloth and a Handful of Copper Bolts...', 12, 0, 100, 1, 0, 0, 39894, 0, 'Image of High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42419, 5, 0, 'Whuh-oh! We\'ve got to get out of here! Lapforge, beam us back, quickly!', 12, 0, 100, 5, 0, 17557, 39895, 0, 'Image of High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42423, 0, 0, 'No! No, no, NO!!! I won\'t allow you into my kingdom, usurper! I WON\'T ALLOW IT! ', 12, 0, 100, 0, 0, 17570, 39885, 0, 'Thermaplugg\'s Brag-bot');
INSERT INTO `creature_text` VALUES (42423, 1, 0, 'Activate the Ultimate Atomic Protection System.', 12, 0, 100, 0, 0, 0, 39886, 0, 'Thermaplugg\'s Brag-bot');
INSERT INTO `creature_text` VALUES (42423, 2, 0, '...DISINTEGRATE THEM ALL!', 12, 0, 100, 0, 0, 0, 39887, 0, 'Thermaplugg\'s Brag-bot');
