INSERT INTO `creature_text` VALUES (37173, 2, 0, 'Soon enough we take over dis valley.  Soon enough.', 12, 0, 100, 0, 0, 0, 36961, 0, 'Soothsayer Rikkari to Player');
INSERT INTO `creature_text` VALUES (37174, 0, 0, 'Our land be a land of ice an\' snow.', 12, 0, 100, 0, 0, 0, 36963, 0, 'Soothsayer Mirim\'koa to Player');
INSERT INTO `creature_text` VALUES (37174, 1, 0, 'But beneath the earth, child, there always be fire.', 12, 0, 100, 0, 0, 0, 36964, 0, 'Soothsayer Mirim\'koa to Player');
INSERT INTO `creature_text` VALUES (37174, 2, 0, 'De spirit come from deep down to talk with Grik\'nir.', 12, 0, 100, 0, 0, 0, 36965, 0, 'Soothsayer Mirim\'koa to Player');
INSERT INTO `creature_text` VALUES (37507, 0, 0, 'I gonna make you into mojo!', 12, 0, 100, 0, 0, 0, 10435, 0, 'Frostmane Blade to Player');
INSERT INTO `creature_text` VALUES (37518, 0, 0, '<Static>$bIs this thing on?  Hello?', 12, 7, 100, 0, 0, 0, 37115, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 1, 0, 'Ahh, there you are.  Hello!  I figured that, while you\'re flying to Kharanos, I\'d give you some data about what\'s going on outside of Coldridge Valley.', 12, 7, 100, 0, 0, 0, 37117, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 2, 0, 'It seems you Coldridge folks are dreadfully under-informed about the events of the last few days.', 12, 7, 100, 0, 0, 0, 37118, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 3, 0, 'If you thought you had problems with trolls in Coldridge, wait \'til you get to Kharanos!  That big cataclysm got the trolls all stirred up out here.', 12, 7, 100, 0, 0, 0, 37119, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 4, 0, 'And if you\'ll look to your left, you\'ll see that the gnomes have retaken Gnomeregan.  Hooray for us!', 12, 7, 100, 0, 0, 0, 43112, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 5, 0, 'There\'s plenty else going on out here, too.  We\'ve opened up the path to the airport, there\'s rumors of Dark Irons in the east... oh, and I think something weird is going on with that dwarf king, Magni.  Might want to check it out.', 12, 7, 100, 0, 0, 0, 43113, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (37518, 6, 0, 'And here we are!  Welcome to Kharanos!  Watch your step on the way out.', 12, 7, 100, 0, 0, 0, 43115, 0, 'Milo Geartwinge to Milo\'s Gyro');
INSERT INTO `creature_text` VALUES (40950, 0, 0, 'We\'re getting bogged down in the fight against the Frostmane trolls.', 12, 0, 100, 5, 0, 0, 43235, 0, 'Captain Tharran');
INSERT INTO `creature_text` VALUES (40950, 1, 0, 'Most of our steam tanks are out of commission and we\'re losing mountaineers quickly.', 12, 0, 100, 5, 0, 0, 43236, 0, 'Captain Tharran');
INSERT INTO `creature_text` VALUES (40950, 2, 0, 'We can\'t keep up the operation much longer. Delber and his team must launch their attack from Steelgrill\'s Depot as soon as possible!', 12, 0, 100, 6, 0, 0, 43243, 0, 'Captain Tharran');
INSERT INTO `creature_text` VALUES (40950, 3, 0, 'Glad to hear it. I\'ll send Delber any forces I can spare.', 12, 0, 100, 273, 0, 0, 43253, 0, 'Captain Tharran');
INSERT INTO `creature_text` VALUES (40950, 4, 0, 'There\'s no substitute for live observers, but I need those mountaineers here. We\'ll have to rely on those little gnomish observer bots for now...', 12, 0, 100, 6, 0, 0, 43522, 0, 'Captain Tharran to Player');
INSERT INTO `creature_text` VALUES (40951, 0, 0, 'It won\'t be long before we\'re out of supplies too, Captain. Between the trolls and the yeti raids, we\'ll be down to half rations within a week.', 12, 0, 100, 1, 0, 0, 43242, 0, 'Quartermaster Glynna to Captain Tharran');
INSERT INTO `creature_text` VALUES (40991, 0, 0, 'Tell Captain Tharran that I\'ll be back in Kharanos as soon as I\'ve verified that the bot is working correctly.', 12, 0, 100, 66, 0, 0, 41005, 0, 'Mountaineer Dunstan to Player');
INSERT INTO `creature_text` VALUES (40994, 0, 0, 'I can\'t wait to get out there and help in the fight against those trolls.', 12, 0, 100, 66, 0, 0, 41006, 0, 'Mountaineer Lewin to Player');
INSERT INTO `creature_text` VALUES (41056, 0, 0, 'We\'ve been fighting nonstop since the cataclysm. It\'ll be nice to get a rest, if a brief one.', 12, 0, 100, 66, 0, 0, 41008, 0, 'Mountaineer Valgrum to Player');
