INSERT INTO `creature_text` VALUES (41335, 0, 0, 'Delber and his men are working overtime, captain. He has devised a means of delivering an assault team to the rear flank of the battle.', 12, 0, 100, 1, 0, 0, 43245, 0, 'Covert Operative to Captain Tharran');
INSERT INTO `creature_text` VALUES (41335, 1, 0, 'We\'ve also secured air support for the operation and will be ready to deploy soon. If any of your men want to join the assault team, just send them to Delber.', 12, 0, 100, 1, 0, 0, 43248, 0, 'Covert Operative to Captain Tharran');
INSERT INTO `creature_text` VALUES (41578, 0, 0, 'The Frostmane trolls have been raiding the ranch continuously.', 12, 0, 100, 6, 0, 0, 41525, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` VALUES (41578, 1, 0, 'As it is, there are barely enough of us to keep them from taking everything.', 12, 0, 100, 5, 0, 0, 41526, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` VALUES (41578, 2, 0, 'They just take the rams and go. The herd can\'t take any more.', 12, 0, 100, 1, 0, 0, 41527, 0, 'Sergeant Flinthammer to Player');
INSERT INTO `creature_text` VALUES (41578, 3, 0, 'One of the mountaineers has seen something. Keep your eyes open!', 14, 0, 100, 25, 0, 0, 41528, 0, 'Sergeant Flinthammer to Player');
