INSERT INTO `creature_text` VALUES (42129, 1, 0, 'I will not let you and this treasonous Commander Stonebreaker steal my son\'s birthright!', 12, 0, 100, 274, 0, 0, 42006, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` VALUES (42129, 2, 0, 'Both of you know I would never order such a thing! Why would I set one half my son\'s inheritance against the other?', 12, 7, 100, 5, 0, 0, 42022, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` VALUES (42129, 3, 0, 'No, this is the work of a traitor to both our peoples. Despite my best efforts, some Dark Irons remain in the grip of Twilight\'s Hammer.', 12, 0, 100, 1, 0, 0, 42026, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` VALUES (42129, 4, 0, 'The cult must have someone in the city to coordinate this kind of attack. That only leaves one suspect, Ambassador Slaghammer!', 12, 0, 100, 1, 0, 0, 42028, 0, 'Moira Thaurissan to Player');
INSERT INTO `creature_text` VALUES (42129, 5, 0, 'If we were in Shadowforge City, I would have you tossed into the Ring of Law for your transgressions, Ambassador. The darkest dungeons of Ironforge will have to do for now.', 12, 0, 100, 274, 0, 0, 42044, 0, 'Moira Thaurissan to Arrest the Ambassador Credit');
INSERT INTO `creature_text` VALUES (42131, 0, 0, 'Muradin has a point. If you cannot govern your own people, you have no place at this table.', 12, 7, 100, 273, 0, 0, 42017, 0, 'Falstad Wildhammer to Player');
INSERT INTO `creature_text` VALUES (42153, 0, 0, 'I go willingly, $c. My purpose in my master\'s plan is fulfilled.', 12, 0, 100, 1, 0, 0, 42046, 0, 'Ambassador Slaghammer to Player');
INSERT INTO `creature_text` VALUES (42153, 1, 0, 'Do as you will with me. I am not afraid. That halfbreed runt will never be my emperor! Long live Twilight\'s Hammer!', 12, 0, 100, 5, 0, 0, 42043, 0, 'Ambassador Slaghammer to Arrest the Ambassador Credit');
INSERT INTO `creature_text` VALUES (42928, 0, 0, 'That is quite an accusation, Moira. It\'s hardly your throne room in the first place.', 12, 0, 100, 6, 0, 0, 42007, 0, 'Muradin Bronzebeard to Player');
