INSERT INTO `creature_text` VALUES (38120, 4, 0, 'Shut it down, shut it down! The eggs are bad!!!', 14, 0, 100, 0, 0, 0, 38170, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 5, 0, 'The marketing research Megs has conducted suggests that a naga invasion is imminent!', 12, 0, 100, 5, 0, 0, 49934, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 6, 0, 'Take your time, my $g boy : girl;. Now what can I modify this shredder boot to be? Hmm.', 12, 0, 100, 6, 0, 0, 50086, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 7, 0, 'I\'ve set the Bootzooka to fire several rockets per shot. I\'m fairly certain that I perfectly calibrated the targeting.', 12, 0, 100, 273, 0, 0, 50087, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 8, 0, 'Booster rockets for cages. What will I think of next? I am a genius!', 12, 0, 100, 11, 0, 0, 50112, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38122, 0, 0, 'Hey, $n, come over here, would ya? I\'ve got something for you to do.', 12, 0, 100, 70, 0, 6820, 41261, 0, 'Bamm Megabomb to Player');
INSERT INTO `creature_text` VALUES (38122, 1, 0, 'Now get us some real eggs.', 12, 0, 100, 273, 0, 0, 49899, 0, 'Bamm Megabomb to Player');
INSERT INTO `creature_text` VALUES (38124, 0, 0, 'Genius idea, sir. Your brain power never ceases to amaze me!', 14, 0, 100, 1, 0, 0, 38119, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 1, 0, 'This thing is huge! Reverse the polarity on the Poultryizer. We\'ll have to shove it in through the outtake.', 14, 0, 100, 5, 0, 0, 38141, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 2, 0, 'There it goes.  It should only be a moment now....', 14, 0, 100, 1, 0, 0, 38142, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 3, 0, '$n, get to the Mechashark X-Steam Controller. Rid us of the Hammer menace!', 12, 0, 100, 1, 0, 0, 49911, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 4, 0, 'Look for the mounds of glowing stuff on the ground.', 12, 0, 100, 1, 0, 1473, 50082, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 5, 0, 'Greely drools a little as she continues her mindless labor.', 16, 0, 100, 0, 0, 0, 50101, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 6, 0, 'Get rid of those Steamwheedle Sharks before they trounce us!', 12, 0, 100, 1, 0, 0, 50105, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 7, 0, 'It\'s time for a little sabotage!', 12, 0, 100, 11, 0, 0, 50109, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 8, 0, 'Deliver the footbomb uniform to the Slave Pits. Hurry, $n!', 12, 0, 100, 25, 0, 0, 50111, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38359, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 38630, 0, 'Vashj\'elan Warrior');
INSERT INTO `creature_text` VALUES (38381, 0, 0, 'We can discuss the cut after you come back with the hides. I\'m thinking ninety-ten.', 12, 0, 100, 6, 0, 0, 49948, 0, 'Brett \"Coins\" McQuid to Player');
INSERT INTO `creature_text` VALUES (38387, 0, 0, '$n, hop into one of those B.C. Eliminators and wipe out the pygmies!', 14, 0, 100, 25, 0, 0, 49973, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 1, 0, 'You\'re going to have to take the fight to them, $n. Look for Izzy just outside their village.', 12, 0, 100, 273, 0, 0, 49974, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 2, 0, 'Those zombies are slow. You don\'t have to run right through them. Don\'t let them eat your brains, $n!', 12, 0, 100, 1, 0, 2304, 50013, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 3, 0, 'Goblin grenades versus gnomish land mines. Show them who\'s better, $n!', 12, 0, 100, 1, 0, 0, 50095, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 4, 0, 'I forget... did you take flying lessons back on Kezan?', 12, 0, 100, 6, 0, 0, 50097, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 5, 0, 'Better hurry up, $n. I think that Thrall has news on where our people are being held.', 12, 0, 100, 273, 0, 0, 50099, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 6, 0, 'First she left you for Chip, and now the Trade Prince! She was never good enough for you, $n.', 12, 0, 100, 274, 0, 0, 50114, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 7, 0, '$n, get into the Ultimate Footbomb Uniform behind you and get down to the docks. Help Thrall kick the Trade Prince\'s butt!', 12, 0, 100, 1, 0, 0, 43948, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38409, 0, 0, 'That\'s right. Look at the pretty flags.', 14, 0, 100, 5, 0, 0, 38652, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` VALUES (38409, 1, 0, 'Have you heard of Azshara? There are no zombies there.', 14, 0, 100, 273, 0, 1473, 38647, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` VALUES (38409, 2, 0, 'Smoker!', 14, 0, 100, 6, 0, 0, 38649, 0, 'Goblin Survivor to B.C. Eliminator');
INSERT INTO `creature_text` VALUES (38409, 3, 0, 'We put a whole town... in a tiny little box!', 14, 0, 100, 0, 0, 0, 37356, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 4, 0, 'Recycling! But, for stuff that\'s never been used before.', 14, 0, 100, 0, 0, 0, 37416, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 5, 0, 'Stairways... for horizontal surfaces!', 14, 0, 100, 0, 0, 0, 37365, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 6, 0, 'Tauren Paladins!', 14, 0, 100, 0, 0, 0, 37329, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 7, 0, 'Mouse-sized beartraps... no... Bear-sized mousetraps!!', 14, 0, 100, 0, 0, 0, 37423, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 8, 0, 'Flerberts. I\'m not sure what they do, but I\'d better lock down the name with a trademark.', 14, 0, 100, 0, 0, 0, 37417, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 9, 0, 'Nose-stenders. For smelling things in different rooms.', 14, 0, 100, 0, 0, 0, 37353, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38409, 10, 0, 'Giant gnomes. No, wait... tiny giants!', 14, 0, 100, 0, 0, 0, 37359, 0, 'Goblin Survivor');
INSERT INTO `creature_text` VALUES (38432, 0, 0, 'You hear that, naga? $n is on $g his : her; way to settle our \"negotiations\"!', 14, 0, 100, 15, 0, 0, 49938, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38432, 1, 0, 'Everybody loves a pool pony. It\'s our fastest selling toy behind the Kablooey Bombs: Infant Edition.', 12, 0, 100, 1, 0, 0, 49951, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38432, 2, 0, 'That pygmy helm should fool them, at least for a little while. Get back to town!', 12, 0, 100, 25, 0, 0, 44571, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38441, 0, 0, 'Little help here!', 14, 0, 100, 0, 0, 0, 38767, 0, 'Ace');
INSERT INTO `creature_text` VALUES (38441, 1, 0, 'I never liked that guy. He stole your girlfriend. I say, good riddance!', 12, 0, 100, 273, 0, 0, 50108, 0, 'Ace to Player');
INSERT INTO `creature_text` VALUES (38448, 0, 0, 'WHO DARES?!', 14, 0, 100, 15, 0, 15136, 38338, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38448, 1, 0, 'Little goblins? I remember when your race was created.', 14, 0, 100, 1, 0, 15139, 38346, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38448, 2, 0, 'I care nothing for your threats or these naga. Prepare to be removed from existence.', 14, 0, 100, 25, 0, 15140, 38349, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38448, 3, 0, 'Now, young $n, you will die!', 14, 0, 100, 0, 0, 15140, 38359, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38455, 0, 0, 'I got these little hellions on a short leash, $n. The naga won\'t attack us while we have their hatchlings.', 12, 0, 100, 1, 0, 0, 38356, 0, 'Ace to Player');
INSERT INTO `creature_text` VALUES (38455, 1, 0, 'You ready to make their leader surrender, $g buddy : lady;? Okay, here we go.', 12, 0, 100, 1, 0, 0, 38357, 0, 'Ace to Player');
INSERT INTO `creature_text` VALUES (38455, 2, 0, 'We\'ve got your hatchlings. You better not attack us if you know what\'s good for you!', 14, 0, 100, 5, 0, 0, 38375, 0, 'Ace');
INSERT INTO `creature_text` VALUES (38455, 3, 0, 'We know you were getting ready to attack us. You brought this on yourselves.', 14, 0, 100, 5, 0, 3434, 38377, 0, 'Ace');
INSERT INTO `creature_text` VALUES (38455, 4, 0, 'You naga keep your distance. We\'re not kidding around here!', 14, 0, 100, 5, 0, 3434, 38374, 0, 'Ace');
INSERT INTO `creature_text` VALUES (38455, 5, 0, 'Alright, naga leader, come on out from hiding and surrender in the name of $n and the Bilgewater Cartel!', 14, 0, 100, 5, 0, 0, 38381, 0, 'Ace to Player');
INSERT INTO `creature_text` VALUES (38455, 6, 0, 'Um, dude, this does not look good. I\'m out of here!', 12, 0, 100, 5, 0, 0, 38358, 0, 'Ace to Faceless of the Deep');
INSERT INTO `creature_text` VALUES (38517, 0, 0, 'Okay. We\'re going to move away from the exploding volcano and try to hook up with the orcs for the final assault on the Trade Prince. See you on the other side... I hope.', 12, 0, 100, 1, 0, 0, 39240, 0, 'Slinky Sharpshiv to Player');
INSERT INTO `creature_text` VALUES (38643, 0, 0, 'I gotta get out of here before they catch me again!', 12, 0, 100, 5, 0, 0, 38569, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38643, 1, 0, 'I really can\'t thank you enough!', 12, 0, 100, 18, 0, 0, 38570, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38643, 2, 0, 'They took the others up into the volcano. Some to turn into zombies, some to sacrifice to their turtle god, Volcanoth!', 12, 0, 100, 5, 0, 0, 38571, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38643, 3, 0, 'The other captives are up in the volcano... they\'re being sacrificed, or worse, zombiefied!', 12, 0, 100, 5, 0, 0, 38572, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38643, 4, 0, 'I\'m free! I owe you my life!', 12, 0, 100, 4, 0, 0, 38565, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38643, 5, 0, 'I felt my life slipping out of me.', 12, 0, 100, 1, 0, 0, 38567, 0, 'Goblin Captive to Oomlot Shaman');
INSERT INTO `creature_text` VALUES (38647, 0, 0, '$n, you gotta get in there and rescue our people!', 12, 0, 100, 5, 0, 0, 50007, 0, 'Izzy to Player');
INSERT INTO `creature_text` VALUES (38647, 1, 0, 'Goblin zombies? The Town-In-A-Box has to be warned!', 12, 0, 100, 6, 0, 0, 50008, 0, 'Izzy to Player');
INSERT INTO `creature_text` VALUES (38696, 0, 0, 'Echuta, gobin!', 14, 0, 100, 0, 0, 0, 38555, 0, 'Yngwie');
INSERT INTO `creature_text` VALUES (38696, 1, 0, 'Tay\'kn gobins!', 14, 0, 100, 15, 0, 0, 38560, 0, 'Yngwie');
INSERT INTO `creature_text` VALUES (38738, 0, 0, 'Get in there and show those zombies what the Bilgewater Buccaneers are made of, kid!', 12, 0, 100, 25, 0, 0, 50072, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` VALUES (38738, 1, 0, 'You\'re going to love this!', 12, 0, 100, 273, 0, 0, 50083, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` VALUES (38738, 2, 0, 'We\'re gonna make you the ultimate footbomb uniform, kid. But first, we need parts. Lots of them.', 12, 0, 100, 25, 0, 0, 50106, 0, 'Coach Crosscheck to Player');
INSERT INTO `creature_text` VALUES (38745, 0, 0, 'Ooh, that\'s gotta hurt!', 14, 0, 100, 25, 0, 1473, 38645, 0, 'Kezan Citizen to B.C. Eliminator');
INSERT INTO `creature_text` VALUES (38745, 1, 0, 'Help me, help me!', 14, 0, 100, 0, 0, 0, 38768, 0, 'Kezan Citizen');
INSERT INTO `creature_text` VALUES (38745, 2, 0, 'Portable gasoline squirt-guns!', 14, 0, 100, 0, 0, 2304, 37337, 0, 'Kezan Citizen');
INSERT INTO `creature_text` VALUES (38855, 0, 0, 'Stay out of the way of Volcanoth\'s breath!', 41, 0, 100, 0, 0, 0, 38847, 0, 'Volcanoth to Player');
INSERT INTO `creature_text` VALUES (38869, 0, 0, 'Hang on, $n! We have to get out of here, NOW!', 14, 0, 100, 0, 0, 0, 38874, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38928, 0, 0, '$n, come on! Get over here!!!', 14, 0, 100, 0, 0, 0, 38908, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (39065, 0, 0, 'Deliver justice for our dead, $n.', 12, 0, 100, 5, 0, 0, 50094, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (39065, 1, 0, 'You honor us with your actions on behalf of our dead, $n.', 12, 0, 100, 1, 0, 0, 50096, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (39066, 0, 0, 'Thrall Hall!', 14, 1, 100, 15, 0, 0, 39096, 0, 'Kilag Gorefang');
INSERT INTO `creature_text` VALUES (39066, 1, 0, 'Run, Bastia! Get $n to Sky Falls and the labor mine.', 12, 0, 100, 1, 0, 0, 50100, 0, 'Kilag Gorefang to Player');
INSERT INTO `creature_text` VALUES (39193, 0, 0, 'Trade Prince say hurt you!', 12, 0, 100, 0, 0, 0, 39318, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` VALUES (39193, 1, 0, 'Break time over!', 12, 0, 100, 0, 0, 0, 39322, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` VALUES (39193, 2, 0, 'Me like beat you up!', 12, 0, 100, 0, 0, 0, 39320, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` VALUES (39193, 3, 0, 'Me overseer, you slave!', 12, 0, 100, 0, 0, 0, 39321, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` VALUES (39193, 4, 0, 'You no mine ore, you get beating!', 12, 0, 100, 0, 0, 0, 39319, 0, 'Brute Overseer to Player');
INSERT INTO `creature_text` VALUES (39194, 0, 0, 'What? You\'re not supposed to be here. You\'re dead!', 12, 0, 100, 0, 0, 0, 39340, 0, 'Blastshadow the Brutemaster to Player');
INSERT INTO `creature_text` VALUES (39195, 0, 0, 'Kill that goblin, quickly!', 12, 0, 100, 0, 0, 0, 39341, 0, 'Delicia Whipsnaps to Player');
INSERT INTO `creature_text` VALUES (39195, 1, 0, 'NOOOOOOO!', 14, 0, 100, 0, 0, 0, 39297, 0, 'Delicia Whipsnaps to Blastshadow the Brutemaster');
INSERT INTO `creature_text` VALUES (39198, 0, 0, 'Feed pigs rubber, \'til they bounce. There\'s got to be an application for that.', 14, 0, 100, 0, 0, 0, 37343, 0, 'Ace to Player');
INSERT INTO `creature_text` VALUES (39199, 0, 0, 'I\'VE GOT AN IDEA!', 14, 0, 100, 0, 0, 0, 39259, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (39199, 1, 0, 'We\'ve got to get our people out of here, $n! The key is to get them to drink plenty of Kaja\'Cola Zero-One!', 12, 0, 100, 5, 0, 0, 50102, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (39199, 2, 0, 'We\'re not a part of your system!', 14, 0, 100, 15, 0, 0, 39312, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (39199, 3, 0, '$n, we need to escape through the northern tunnel!', 12, 0, 100, 1, 0, 0, 50103, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (39201, 0, 0, 'A binary number system. It uses only two numbers. 6 and 11.', 14, 0, 100, 0, 0, 0, 37358, 0, 'Gobber to Player');
INSERT INTO `creature_text` VALUES (39426, 0, 0, 'I spent most of your money on getting a decent massage, goodness knows you could never do it right!', 14, 0, 100, 0, 0, 0, 40620, 0, 'Candy Cane to Player');
INSERT INTO `creature_text` VALUES (39456, 0, 0, '$n, you and Hobart are going to be the death of me!', 12, 0, 100, 0, 0, 0, 39502, 0, 'Captured Goblin to Player');
INSERT INTO `creature_text` VALUES (39456, 1, 0, 'What are those rockets for?!', 12, 0, 100, 0, 0, 0, 39501, 0, 'Captured Goblin to Player');
INSERT INTO `creature_text` VALUES (39456, 2, 0, 'The pirates have keys!', 12, 0, 100, 0, 0, 0, 39503, 0, 'Captured Goblin to Player');
INSERT INTO `creature_text` VALUES (39456, 3, 0, 'Tell my husband I love him very much!', 12, 0, 100, 0, 0, 0, 39499, 0, 'Captured Goblin to Player');
INSERT INTO `creature_text` VALUES (39456, 4, 0, 'Um... you do realize there\'s a bottom to this cage?!', 12, 0, 100, 0, 0, 0, 39496, 0, 'Captured Goblin to Player');
INSERT INTO `creature_text` VALUES (39582, 0, 0, 'You\'re fired!', 14, 0, 100, 0, 0, 19574, 39561, 0, 'Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39582, 1, 0, 'I SEE THE TRAITOR IS HERE TO RESCUE YOU, WARCHIEF. HOW CONVENIENT. YOU WILL BOTH BOW TO ME OR FALL TOGETHER!', 14, 0, 100, 0, 0, 19567, 39530, 0, 'Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39582, 2, 0, 'I\'m downsizing the cartel, starting with you!', 14, 0, 100, 0, 0, 19571, 39556, 0, 'Trade Prince Gallywix to Thrall');
INSERT INTO `creature_text` VALUES (39582, 3, 0, 'I like you. Here\'s a raise!', 14, 0, 100, 0, 0, 19578, 39573, 0, 'Trade Prince Gallywix to Thrall');
INSERT INTO `creature_text` VALUES (39582, 4, 0, 'Excuse me while I dispose of these toxic assets all over you!', 14, 0, 100, 0, 0, 19569, 39559, 0, 'Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39582, 5, 0, 'The Bilgewater Cartel has grown too large. Time to downsize!', 14, 0, 100, 0, 0, 19572, 39557, 0, 'Trade Prince Gallywix to Ultimate Footbomb Uniform');
INSERT INTO `creature_text` VALUES (39582, 6, 0, 'I need to move these toxic assets onto a sucker... like you!', 14, 0, 100, 0, 0, 19570, 39560, 0, 'Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39582, 7, 0, 'Eat it!', 14, 0, 100, 0, 0, 19566, 47397, 0, 'Trade Prince Gallywix to Ultimate Footbomb Uniform');
INSERT INTO `creature_text` VALUES (39594, 0, 0, 'For now, you will remain the Trade Prince of the Bilgewater Cartel.', 14, 0, 100, 0, 0, 0, 39586, 0, 'Thrall to Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39594, 1, 0, 'I will send a representative from amongst your people to the new warchief, Garrosh Hellscream in Orgrimmar.', 14, 0, 100, 0, 0, 0, 39587, 0, 'Thrall to Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (39594, 2, 0, 'You will have a new home in Azshara and the Bilgewater Cartel will become a part of the Horde!', 14, 0, 100, 0, 0, 0, 39588, 0, 'Thrall to Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (315, 2, 2, 'It\'s all true, brother. Every word. You doubted it?', 12, 0, 100, 11, 0, 0, 43965, 0, 'Stalvan Mistmantle to Player');
INSERT INTO `creature_text` VALUES (315, 3, 3, 'You know why!', 12, 0, 100, 25, 0, 0, 43968, 0, 'Stalvan Mistmantle to Player');
INSERT INTO `creature_text` VALUES (315, 4, 4, 'Surely you\'ve felt anger. Anger so foul and vicious that it makes you want to tear someone to shreds...', 12, 0, 100, 1, 0, 0, 43978, 0, 'Stalvan Mistmantle to Player');
INSERT INTO `creature_text` VALUES (315, 5, 5, 'Aren\'t you feeling it right now?', 12, 0, 100, 6, 0, 0, 43979, 0, 'Stalvan Mistmantle to Player');
INSERT INTO `creature_text` VALUES (315, 6, 6, 'You see, brother... we\'re not so different...', 12, 0, 100, 6, 0, 0, 43981, 0, 'Stalvan Mistmantle to Tobias Mistmantle');
INSERT INTO `creature_text` VALUES (43453, 0, 0, 'Brother!', 12, 0, 100, 0, 0, 0, 43966, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (43453, 1, 1, 'Tell me it\'s not true, brother. Tell me you didn\'t die a murderer!', 12, 0, 100, 25, 0, 0, 43967, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (43453, 2, 2, 'But why?! How could you?', 12, 0, 100, 5, 0, 0, 43973, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (43797, 3, 3, 'No... NO! STOP IT!', 14, 0, 100, 15, 0, 0, 43980, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (43797, 4, 4, 'No...', 12, 0, 100, 0, 0, 0, 43982, 0, 'Tobias Mistmantle to Stalvan Mistmantle');
INSERT INTO `creature_text` VALUES (33304, 6, 0, 'Show yourself, elf-coward! Face your death with honor!', 14, 0, 100, 0, 0, 0, 0, 0, 'Gorat Yells');
INSERT INTO `creature_text` VALUES (33304, 5, 0, 'He must fall!', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33304, 3, 0, 'Follow me closely, and prepare for battle!', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33304, 4, 0, 'Captain Elendilad conceals himself on the island ahead, cowardly issuing orders to his underlings.', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33304, 0, 0, 'Thank you, friend. I could not rest knowing that my mission had not been completed.', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33304, 1, 0, '...and that my mens\' sacrifice had not been avenged.', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33304, 2, 0, 'We must hurry, now. I sense that my time is short.', 12, 0, 100, 0, 0, 0, 0, 0, 'Gorat Says');
INSERT INTO `creature_text` VALUES (33440, 0, 1, 'You stay close, $c. And don\'t let elfs near me!', 12, 0, 0, 0, 0, 0, 0, 0, 'Demoralized Peon 1');
INSERT INTO `creature_text` VALUES (33440, 0, 2, 'You keep a lookout, $c! The elfs are everywhere!', 12, 0, 0, 0, 0, 0, 0, 0, 'Demoralized Peon 2');
INSERT INTO `creature_text` VALUES (33440, 0, 3, 'Hmm. You don\'t look very tough... but okay.', 12, 0, 0, 0, 0, 0, 0, 0, 'Demoralized Peon 3');
