INSERT INTO `creature_text` VALUES (37873, 0, 0, 'Just as Daral\'nir soothes the cursed druids who gave into the beast and abandoned balance, let Tal\'doren soothe $n.', 12, 0, 100, 1, 0, 16419, 0, 0, 'Vassandra Stormclaw to Player');
INSERT INTO `creature_text` VALUES (37876, 0, 0, 'It\'s over Godfrey.  You have no support left among the eastern lords.', 12, 0, 100, 1, 0, 23506, 0, 0, 'King Genn Greymane to Player');
