INSERT INTO `creature_text` VALUES (38051, 1, 0, 'It will be done!', 12, 0, 100, 0, 0, 19605, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38469, 0, 0, 'Enough!', 14, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38469, 1, 0, 'Let\'s see how brave Gilneas gets on without its stubborn leader!', 14, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38469, 2, 0, 'Such a waste.  That arrow\'s poison was not meant to be wasted on your whelp.  We\'ll meet again, Genn!', 12, 0, 100, 0, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (38470, 0, 0, 'Block their retreat, Liam!  We\'ve got them right where we want them!', 14, 0, 100, 0, 0, 19606, 0, 0, 'King Genn Greymane to Player');
