INSERT INTO `creature_text` VALUES (38507, 1, 0, 'Follow Tobias Mistmantle into the Cathedral District.', 42, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 2, 0, 'Let\'s do our best not to be seen, $n.', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Did you hear that, $n?  Sylvanas is headed to the cathedral.  Let\'s hurry!', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 4, 0, 'Quick!  Hide in the water!', 12, 0, 100, 15, 0, 19606, 0, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38530, 0, 0, 'You can assure Garrosh that this is a minor setback.  Our victory in Gilneas will be absolute.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 1, 0, 'You\'d do well to watch your tone, General.  Neither you nor Garrosh have anything to worry about.  We\'ve ceased all production of the Plague, as he ordered.  We\'d never deploy it without his permission.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 2, 0, 'Go with honor, General.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'What kind of question is that?  Of course we\'re deploying the Plague as planned!  Let the Gilneans enjoy their small victory.  Not even their bones will remain by tomorrow.', 12, 0, 100, 1, 0, 19606, 0, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38533, 0, 0, 'It appears you\'re losing control of Gilneas, Sylvanas.  Garrosh fears he\'s going to have to carry out this invasion himself.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38533, 1, 0, 'You sound very confident, your Majesty.  I seriously hope you do not plan to use the Plague.  Garrosh has explicitly forbidden it.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38533, 2, 0, 'I will deliver my report to our leader, then.  By your leave, my lady.', 12, 0, 100, 66, 0, 19606, 0, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38537, 0, 0, 'My Lady!  Should I order my men to stop the deployment of the Plague?  Or are we to continue as planned?', 12, 0, 100, 66, 0, 19606, 0, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38537, 1, 0, 'As you wish!', 12, 0, 100, 66, 0, 19606, 0, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38617, 0, 0, 'Make sure everything is ready.  The orcs\' emissary is ready to meet Sylvanas inside the cathedral.', 14, 0, 100, 1, 0, 19606, 0, 0, 'Forsaken General to Player');
INSERT INTO `creature_text` VALUES (38766, 0, 0, 'Crowley!  You and your elven allies are hereby ordered to serve along the king\'s army.  Cursed or not, you are still bound by Gilnean law!', 12, 0, 100, 25, 0, 16419, 0, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (38767, 0, 0, 'It is decided, then.  We will unite all Gilneans and drive the Forsaken from our lands.', 12, 0, 100, 0, 0, 16419, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (38768, 0, 0, 'Father!', 14, 0, 100, 0, 0, 19605, 0, 0, 'Lorna Crowley to Player');
