INSERT INTO `creature_text` VALUES (124882, 1, 0, 'Ah! More meat to grind into sausage!', 12, 0, 100, 0, 0, 0, 134439, 0, 'Roland Hacksaw to Player');
INSERT INTO `creature_text` VALUES (124922, 0, 0, 'Insipid $c! This village is mine. The Mother has willed it!', 12, 0, 100, 0, 0, 97669, 133715, 0, 'Helena Gentle to Player');
