INSERT INTO `creature_text` VALUES (125342, 2, 0, 'Careful \'bout stickin\' yer nose into Ashvane business. They\'re as filthy as they come.', 12, 0, 100, 0, 0, 109584, 136094, 0, 'Captain Keelson to Player');
INSERT INTO `creature_text` VALUES (125380, 0, 0, 'That\'s better. Now, let\'s--', 12, 0, 100, 1, 0, 97605, 138518, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (125380, 1, 0, 'It\'s an ambush! Defend the villagers!', 14, 0, 100, 5, 0, 97607, 138553, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (125382, 0, 0, 'He\'s okay, he\'s okay! There\'s no need to-- AAH!', 12, 0, 100, 1, 0, 729, 136800, 0, 'Eddie Norwington to Player');
