INSERT INTO `creature_text` VALUES (125385, 2, 0, 'What in creation is that?', 12, 0, 100, 5, 0, 97609, 138528, 0, 'Marshal Everit Reade to Deathbound Shambler');
INSERT INTO `creature_text` VALUES (125922, 0, 0, 'I... please, calm down, calm down everyone! We are doing the best we can. We shall find a cure, and the cause!', 12, 0, 100, 1, 0, 109024, 138691, 0, 'Brother Therold to Player');
INSERT INTO `creature_text` VALUES (125922, 1, 0, 'I grant you the blessings of the sea! May you experience its gentle embrace, as it accepts you as an ally.', 12, 0, 100, 0, 0, 109012, 136292, 0, 'Brother Therold to Player');
INSERT INTO `creature_text` VALUES (125922, 2, 0, 'O sea, provide relief to the sick and downtrodden of Anglepoint Wharf. May we discover the source of the malady that plagues us!', 12, 0, 100, 1, 0, 109026, 138822, 0, 'Brother Therold to Player');
INSERT INTO `creature_text` VALUES (125922, 3, 0, 'Be calm, children of the sea. We must put our faith in the waters to find a cure.', 12, 0, 100, 1, 0, 109027, 138831, 0, 'Brother Therold to Player');
INSERT INTO `creature_text` VALUES (125922, 4, 0, 'I will move to scout further in town. Pray that the people there are safe!', 12, 0, 100, 1, 0, 109028, 138853, 0, 'Brother Therold to Player');
INSERT INTO `creature_text` VALUES (125924, 0, 0, 'Them Outriggers are so brave! I could never go out and face them monsters! I\'m good on cargo duty.', 12, 0, 100, 273, 0, 0, 140700, 0, 'Anglepoint Dockworker to Player');
INSERT INTO `creature_text` VALUES (125945, 0, 0, 'I actually just received a shipment a few minutes ago! Want to take a look?', 12, 0, 100, 1, 0, 0, 136221, 0, 'Aaron Gutier to Player');
INSERT INTO `creature_text` VALUES (125945, 1, 0, 'AAH! GET THIS THING OFF ME!', 14, 0, 100, 0, 0, 0, 136225, 0, 'Aaron Gutier to Player');
INSERT INTO `creature_text` VALUES (125945, 2, 0, 'I can\'t... help... AAAAAAAUUURGH!', 14, 0, 100, 0, 0, 0, 143054, 0, 'Aaron Gutier to Player');
INSERT INTO `creature_text` VALUES (125947, 0, 0, 'You know, I haven\'t seen that Stormsong kid in a few days. I wish they would put somebody more competent in charge of this place, but who am I to say?', 12, 0, 100, 1, 0, 0, 136220, 0, 'Ginger Freelin to Player');
