INSERT INTO `creature_text` VALUES (125955, 0, 0, 'How can management let this happen? That\'s it, we need to unionize.', 12, 0, 100, 1, 0, 0, 136254, 0, 'Anglepoint Fishpacker to Player');
INSERT INTO `creature_text` VALUES (125955, 1, 0, 'I\'m used to monster parts in the shipments, but living monsters? Count me out.', 12, 0, 100, 1, 0, 0, 136251, 0, 'Anglepoint Fishpacker to Player');
INSERT INTO `creature_text` VALUES (125955, 2, 0, 'Worms that eat faces? I\'m getting out of here!', 12, 0, 100, 1, 0, 0, 136255, 0, 'Anglepoint Fishpacker to Player');
INSERT INTO `creature_text` VALUES (125955, 3, 0, 'It\'s time for my break anyway. I\'m outta here.', 12, 0, 100, 1, 0, 0, 136253, 0, 'Anglepoint Fishpacker to Player');
INSERT INTO `creature_text` VALUES (125957, 0, 0, 'Wh... what? Who are you? What is that?', 12, 0, 100, 1, 0, 0, 136371, 0, 'Possessed Dockworker');
INSERT INTO `creature_text` VALUES (125957, 1, 0, 'Wh... what happened?', 12, 0, 100, 6, 0, 0, 136368, 0, 'Possessed Dockworker');
INSERT INTO `creature_text` VALUES (125957, 2, 0, 'That thing was controlling my brain?!', 12, 0, 100, 274, 0, 0, 136370, 0, 'Possessed Dockworker');
INSERT INTO `creature_text` VALUES (125960, 0, 0, 'Brannon Stormsong is a weak boy. The true power belongs to the sea!', 12, 0, 100, 0, 0, 0, 136275, 0, 'Overseer Bates to Player');
INSERT INTO `creature_text` VALUES (125960, 1, 0, 'Ifis shuul qi\'nuq yyqzz...', 12, 0, 100, 0, 0, 0, 136276, 0, 'Overseer Bates to Player');
INSERT INTO `creature_text` VALUES (125961, 0, 0, 'Brannon, you are naught but a foolish child. I have made a deal, and will lead this village to success without you!', 12, 0, 100, 1, 0, 109171, 139307, 0, 'Brother Conway to Player');
INSERT INTO `creature_text` VALUES (125961, 1, 0, 'It is too late! You cannot stop our progress!', 12, 0, 100, 0, 0, 109172, 136277, 0, 'Brother Conway to Player');
INSERT INTO `creature_text` VALUES (125961, 2, 0, 'Enough with this charade!', 12, 0, 100, 0, 0, 109173, 136095, 0, 'Brother Conway');
INSERT INTO `creature_text` VALUES (125961, 3, 0, 'The world... shall drown... ak yar qi\'lwhuk h\'iwn...', 12, 0, 100, 0, 0, 109174, 136278, 0, 'Brother Conway to Player');
INSERT INTO `creature_text` VALUES (126020, 0, 0, 'Ifis yahf an\'zig...', 12, 0, 100, 0, 0, 0, 140722, 0, 'K\'thir Harbinger to Player');
INSERT INTO `creature_text` VALUES (126020, 1, 0, 'Glory! Glory to the sea!', 12, 0, 100, 0, 0, 0, 140724, 0, 'K\'thir Harbinger to Player');
INSERT INTO `creature_text` VALUES (126020, 2, 0, 'You cannot stop the tides...', 12, 0, 100, 0, 0, 0, 140723, 0, 'K\'thir Harbinger to Player');
