INSERT INTO `creature_text` VALUES (126159, 1, 0, 'Thank you! I was nearly a goner.', 12, 0, 100, 5, 0, 0, 154072, 0, 'Frightened Woodsman to Player');
INSERT INTO `creature_text` VALUES (126159, 2, 0, 'I\'m saved! I was about a moment away from being monster food!', 12, 0, 100, 4, 0, 0, 154070, 0, 'Frightened Woodsman to Player');
INSERT INTO `creature_text` VALUES (126170, 0, 0, 'You\'ll regret that!', 12, 0, 100, 0, 0, 0, 139072, 0, 'Mind-addled Enforcer');
INSERT INTO `creature_text` VALUES (126170, 1, 0, 'I\'ve got more power than you ever will!', 12, 0, 100, 0, 0, 0, 139071, 0, 'Mind-addled Enforcer');
INSERT INTO `creature_text` VALUES (126170, 2, 0, 'Oi! What\'re you doin\' here?', 12, 0, 100, 0, 0, 0, 136509, 0, 'Mind-addled Enforcer to Player');
