INSERT INTO `creature_text` VALUES (126441, 4, 0, 'My sisters are many... Kul Tiras will fall...', 12, 0, 100, 0, 0, 97650, 136576, 0, 'Matron Levae to Curseformed Ravager');
INSERT INTO `creature_text` VALUES (126490, 0, 0, 'Ugh, still a bit foggy. Just need a little drink to clear my head.', 12, 0, 100, 0, 0, 105450, 136364, 0, 'Flynn Fairwind to Player');
