INSERT INTO `creature_text` VALUES (126490, 2, 0, '<hic> Now, which way was it... left?', 12, 0, 100, 0, 0, 105452, 136362, 0, 'Flynn Fairwind to Player');
INSERT INTO `creature_text` VALUES (126490, 3, 0, 'No, no. It was DEFINITELY right.', 12, 0, 100, 0, 0, 105453, 136363, 0, 'Flynn Fairwind to Player');
INSERT INTO `creature_text` VALUES (126490, 4, 0, 'Watch yourself. Some nasty critters out here.', 12, 0, 100, 0, 0, 105454, 136489, 0, 'Flynn Fairwind to Player');
INSERT INTO `creature_text` VALUES (126490, 5, 0, 'Blimey, what a hike. Just need another little sip...', 12, 0, 100, 0, 0, 105456, 136372, 0, 'Flynn Fairwind to Flynn Fairwind');
INSERT INTO `creature_text` VALUES (126490, 6, 0, 'Ahh. We\'re gettin\' close. I can feel it.', 12, 0, 100, 0, 0, 105457, 136373, 0, 'Flynn Fairwind to Flynn Fairwind');
INSERT INTO `creature_text` VALUES (126490, 7, 0, '<hic> Hey, lizard.', 12, 0, 100, 3, 0, 105461, 136382, 0, 'Flynn Fairwind to Venomspine Saurolisk');
