INSERT INTO `creature_text` VALUES (126629, 2, 0, 'Get out! Save yourself!', 12, 0, 100, 5, 0, 0, 154075, 0, 'Captured Villager to Player');
INSERT INTO `creature_text` VALUES (126629, 3, 0, 'Get me out of here!', 12, 0, 100, 5, 0, 0, 149509, 0, 'Captured Villager to Player');
