INSERT INTO `creature_text` VALUES (126719, 2, 0, 'Heh. Fresh meat.', 12, 0, 100, 0, 0, 0, 138756, 0, 'Irontide Cutthroat to Player');
INSERT INTO `creature_text` VALUES (126729, 0, 0, 'Free at last!', 12, 0, 100, 0, 0, 0, 127527, 0, 'Bridget Fairwater to Player');
