INSERT INTO `creature_text` VALUES (127144, 0, 0, 'Alright Eddie. I think riding lessons will continue on the estate for now.', 12, 0, 100, 1, 0, 0, 136958, 0, 'Melissa Kenny to Player');
INSERT INTO `creature_text` VALUES (127156, 0, 0, 'Come on! Let\'s get out of here!', 12, 0, 100, 0, 0, 0, 137096, 0, 'Nigel Rifthold to Player');
INSERT INTO `creature_text` VALUES (127157, 0, 0, 'I think I\'m gonna stand back over there. The, uh, light\'s better for reading. Good luck!', 12, 0, 100, 0, 0, 0, 137999, 0, 'Marcus Howlingdale to Player');
INSERT INTO `creature_text` VALUES (127158, 0, 0, 'Hey, thanks! Who knew troggs could use magic?', 12, 0, 100, 0, 0, 0, 137020, 0, 'Jonathan \"Johnny\" Presterby to Player');
INSERT INTO `creature_text` VALUES (127158, 1, 0, 'I\'m going to find Marcus and wait with him. See ya there!', 12, 0, 100, 0, 0, 0, 137021, 0, 'Jonathan \"Johnny\" Presterby to Player');
INSERT INTO `creature_text` VALUES (127159, 0, 0, 'Thank you! I was gonna run out of rocks up here!', 12, 0, 100, 0, 0, 0, 137037, 0, 'Amelia Tidecrest to Player');
INSERT INTO `creature_text` VALUES (127159, 1, 0, 'I\'m going to hide with Marcus!', 12, 0, 100, 0, 0, 0, 137038, 0, 'Amelia Tidecrest to Player');
INSERT INTO `creature_text` VALUES (127160, 0, 0, 'Oh, thank you! It was quite uncomfortable in here.', 12, 0, 100, 0, 0, 0, 137027, 0, 'Euphemia Batten-Chase to Player');
INSERT INTO `creature_text` VALUES (127160, 1, 0, 'I\'ll go find Marcus! See you there!', 12, 0, 100, 0, 0, 0, 137028, 0, 'Euphemia Batten-Chase to Player');
INSERT INTO `creature_text` VALUES (127393, 0, 0, 'Hail to the Wicker Man!', 12, 0, 100, 274, 0, 0, 136992, 0, 'Wickerite Initiate to Player');
INSERT INTO `creature_text` VALUES (127396, 0, 0, 'This is all a bit too much for me. I\'m out of here!', 12, 0, 100, 1, 0, 0, 137026, 0, 'Initiate Peony to Player');
INSERT INTO `creature_text` VALUES (127418, 0, 0, 'These people are crazy! Help me, $c!', 14, 0, 100, 5, 0, 0, 137001, 0, 'Edwin Maldus to Player');
INSERT INTO `creature_text` VALUES (127418, 1, 0, 'Thank you again, $c. You arrived in the nick of time!', 12, 0, 100, 5, 0, 0, 137005, 0, 'Edwin Maldus to Player');
INSERT INTO `creature_text` VALUES (127418, 2, 0, 'Come on, let\'s put an end to this cult!', 12, 0, 100, 5, 0, 0, 137049, 0, 'Edwin Maldus to Player');
INSERT INTO `creature_text` VALUES (127419, 0, 0, 'You should not interfere with our works, lest you too become a sacrifice!', 12, 0, 100, 1, 0, 0, 137002, 0, 'Lord Autumnvale to Player');
INSERT INTO `creature_text` VALUES (127419, 1, 0, 'Die, interloper!', 14, 0, 100, 1, 0, 0, 137036, 0, 'Lord Autumnvale to Player');
INSERT INTO `creature_text` VALUES (127537, 0, 0, 'Thanks for the help, stranger. Remember to keep the salt outta yer britches.', 12, 0, 100, 0, 0, 0, 137075, 0, 'Geraldine to Player');
INSERT INTO `creature_text` VALUES (127548, 0, 0, 'Oops! You snagged an angry crab!', 42, 0, 100, 0, 0, 0, 137109, 0, 'Fishing Rod to Player');
INSERT INTO `creature_text` VALUES (127548, 1, 0, 'The line broke!', 42, 0, 100, 0, 0, 0, 137108, 0, 'Fishing Rod to Player');
INSERT INTO `creature_text` VALUES (127559, 0, 0, 'Capital timing, my friend. We\'ve got a trogg attack on our hands!', 12, 0, 100, 1, 0, 108412, 155533, 0, 'Lord Aldrius Norwington to Player');
INSERT INTO `creature_text` VALUES (127559, 1, 0, 'This is going to be the most exciting festival in years! Hop onto Cooper and show the troggs what for. You will find him most useful in combat!', 12, 0, 100, 4, 0, 108435, 138025, 0, 'Lord Aldrius Norwington to Player');
INSERT INTO `creature_text` VALUES (127613, 0, 0, 'Before we begin, let me get prepared. Just a moment...', 12, 0, 100, 1, 0, 108384, 137087, 0, 'Majo to Player');
INSERT INTO `creature_text` VALUES (127613, 1, 0, 'Well, enough dawdling, then. It is time to identify the perfect horse!', 12, 0, 100, 1, 0, 108402, 137088, 0, 'Majo to Player');
INSERT INTO `creature_text` VALUES (127709, 0, 0, 'What peculiar wounds. They almost remind me of a bear. Do they look like bear wounds to you?', 12, 0, 100, 0, 0, 0, 137270, 0, 'Lord Kennings to Player');
INSERT INTO `creature_text` VALUES (127709, 1, 0, 'Hold it still! I can\'t get a good shot in if it keeps moving!', 12, 0, 100, 0, 0, 0, 137752, 0, 'Lord Kennings');
INSERT INTO `creature_text` VALUES (127709, 2, 0, 'Or maybe not a bear. She looks like she ran afoul with an outlaw, or something.', 12, 0, 100, 0, 0, 0, 137271, 0, 'Lord Kennings to Player');
INSERT INTO `creature_text` VALUES (127709, 3, 0, 'Face my trusty crossbow, vile fiend!', 12, 0, 100, 0, 0, 0, 137753, 0, 'Lord Kennings');
INSERT INTO `creature_text` VALUES (127709, 4, 0, 'More bear wounds. An outlaw and his pet bear, perhaps?', 12, 0, 100, 0, 0, 0, 137274, 0, 'Lord Kennings to Player');
INSERT INTO `creature_text` VALUES (127709, 5, 0, 'Could it be a combination of the two? Man-bear, or is it bear-man?', 12, 0, 100, 0, 0, 0, 137275, 0, 'Lord Kennings to Player');
INSERT INTO `creature_text` VALUES (127709, 6, 0, 'A yeti! Well, I was mostly correct. It is man-shaped and hairy like a bear.', 12, 0, 100, 0, 0, 0, 141456, 0, 'Lord Kennings to Player');
INSERT INTO `creature_text` VALUES (127715, 0, 0, 'I can\'t believe we\'re here! My father used to tell me stories about this place.', 12, 0, 100, 5, 0, 97562, 137379, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 1, 0, 'It contains an ancient secret of our house, from back when the Waycrests first settled this land.', 12, 0, 100, 1, 0, 97563, 137380, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 2, 0, 'We just need to find the entrance!', 12, 0, 100, 6, 0, 97564, 137381, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 3, 0, 'That\'s it! The way is opening!', 12, 0, 100, 5, 0, 97565, 137382, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 4, 0, 'Thus is reborn the Order of Embers.', 12, 0, 100, 1, 0, 97590, 137411, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 5, 0, 'Now then, I believe we have some witches to kill.', 12, 0, 100, 273, 0, 97591, 137412, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127715, 6, 0, 'Our work here is done, $n. I\'ll meet you at Arom\'s Stand.', 12, 0, 100, 1, 0, 97592, 137718, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (127976, 0, 0, 'Watch yourself, $c.', 12, 0, 100, 0, 0, 0, 137789, 0, 'Veteran Hunter to Player');
INSERT INTO `creature_text` VALUES (128113, 0, 0, 'Wait... what was that?', 12, 0, 100, 6, 0, 97569, 146310, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 1, 0, 'Look out!', 14, 0, 100, 22, 0, 97570, 141848, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 2, 0, 'Wait... look out!', 14, 0, 100, 6, 0, 97575, 137391, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 3, 0, 'Hmm... It looks like some rubble blocks our path ahead. Can you clear the way?', 12, 0, 100, 25, 0, 97581, 137396, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 4, 0, 'Wait... something\'s not right.', 12, 0, 100, 6, 0, 97582, 137397, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 5, 0, '$C, behind you!', 14, 0, 100, 25, 0, 97583, 137400, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 6, 0, 'I\'ve never seen a construct that big before! Are you alright?', 12, 0, 100, 5, 0, 97584, 137405, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 7, 0, 'What am I saying... of course you\'re alright. I can see it\'ll take more than a few stones to bring you down!', 12, 0, 100, 6, 0, 97585, 137406, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 8, 0, 'Come along, the book should be in here!', 12, 0, 100, 25, 0, 97586, 137407, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128113, 9, 0, 'Here it is... the last resting place of the Order of Embers. Are you ready to revive ancient history?', 12, 0, 100, 1, 0, 97587, 137408, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (128233, 0, 0, '%s continues rambling about taking responsibility for your own actions for several minutes.', 16, 0, 100, 0, 0, 0, 137907, 0, 'Friendly Pete to Player');
INSERT INTO `creature_text` VALUES (128286, 0, 0, 'Save.... yourself....', 12, 0, 100, 0, 0, 0, 141343, 0, 'Cursed Raider to Player');
INSERT INTO `creature_text` VALUES (128345, 0, 0, 'Fool! You will die screaming!', 14, 0, 100, 0, 0, 104620, 154748, 0, 'Deathcursed Terror');
INSERT INTO `creature_text` VALUES (128345, 1, 0, 'All of Kul Tiras will suffer. You are only the first!', 14, 0, 100, 0, 0, 104621, 154749, 0, 'Deathcursed Terror');
INSERT INTO `creature_text` VALUES (128345, 2, 0, 'Gorak Tul... comes...', 12, 0, 100, 0, 0, 104622, 154750, 0, 'Deathcursed Terror to Player');
INSERT INTO `creature_text` VALUES (128349, 0, 0, 'Some of our most valuable finds were left behind in this crate. Watch my back, $c, while I secure as much as possible.', 12, 0, 100, 0, 0, 0, 138618, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 1, 0, 'A \"genuine Cranklefuse Prototype\". What a waste of metal, no space for trash though.', 12, 0, 100, 0, 0, 0, 138619, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 2, 0, 'And this collection of bones must be courtesy of Birch, given the teeth marks on them.', 12, 0, 100, 0, 0, 0, 138620, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 3, 0, 'I\'ve finally reached the actual relics, now that all of the additions are out o\' the way. Shouldn\'t be too long now.', 12, 0, 100, 0, 0, 0, 138621, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 4, 0, 'Almost finished....', 12, 0, 100, 0, 0, 0, 138622, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 5, 0, 'An\' done! Thanks for the the assistance.', 12, 0, 100, 0, 0, 0, 138623, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128349, 6, 0, 'I can make me own way out. Be careful.', 12, 0, 100, 0, 0, 0, 138666, 0, 'Hilde Firebreaker to Player');
INSERT INTO `creature_text` VALUES (128409, 0, 0, 'Thank you, $c. I\'ll speak with you somewhere safer.', 12, 0, 100, 66, 0, 0, 138316, 0, 'Officer Redfen to Player');
INSERT INTO `creature_text` VALUES (128417, 0, 0, 'May I announce the arrival of $gLord:Lady; $n!', 12, 0, 100, 0, 0, 0, 138017, 0, 'Norwington Herald to Player');
INSERT INTO `creature_text` VALUES (128582, 0, 0, 'You are fools to resist. The Heartsbane will claim Kul Tiras!', 14, 0, 100, 0, 0, 97612, 138531, 0, 'Sister Widdowson');
INSERT INTO `creature_text` VALUES (128582, 1, 0, 'The Mother... will take you...', 12, 0, 100, 0, 0, 97613, 138532, 0, 'Sister Widdowson to Player');
INSERT INTO `creature_text` VALUES (128679, 0, 0, 'We\'re not waiting for your blessing any longer, Therold. We\'ve got monsters to kill.', 12, 0, 100, 1, 0, 108363, 140698, 0, 'Rosaline Madison to Player');
INSERT INTO `creature_text` VALUES (128679, 1, 0, 'Let\'s go, Okri. The Outriggers are waiting.', 12, 0, 100, 1, 0, 108350, 140699, 0, 'Rosaline Madison to Player');
INSERT INTO `creature_text` VALUES (128679, 2, 0, 'Get ready, Outriggers! These creatures are unlike anything we\'ve faced before!', 14, 0, 100, 0, 0, 108344, 139383, 0, 'Rosaline Madison to Player');
INSERT INTO `creature_text` VALUES (128680, 0, 0, 'Inform me when you are prepared to go! Time is fleeting, and the monster shall not hesitate!', 12, 0, 100, 1, 0, 108334, 140635, 0, 'Okri Putterwrench to Player');
INSERT INTO `creature_text` VALUES (128742, 0, 0, 'Thank you for saving me.', 12, 0, 100, 4, 0, 0, 138770, 0, 'Injured Ranger to Player');
INSERT INTO `creature_text` VALUES (128742, 1, 0, 'Thank you, stranger. I thought I was done for.', 12, 0, 100, 4, 0, 0, 138769, 0, 'Injured Ranger to Player');
INSERT INTO `creature_text` VALUES (128742, 2, 0, 'Thank you. Once I find my pet I\'m leaving this cursed place!', 12, 0, 100, 4, 0, 0, 138773, 0, 'Injured Ranger to Player');
INSERT INTO `creature_text` VALUES (128805, 0, 0, 'Our children are falling ill!', 12, 0, 100, 5, 0, 0, 138688, 0, 'Anxious Villager to Player');
INSERT INTO `creature_text` VALUES (128805, 1, 0, 'You must help us, Therold! We need answers! We need a cure!', 12, 0, 100, 5, 0, 0, 138690, 0, 'Anxious Villager to Player');
INSERT INTO `creature_text` VALUES (128806, 0, 0, 'What is causing this illness?!', 12, 0, 100, 5, 0, 0, 138689, 0, 'Anxious Villager to Player');
INSERT INTO `creature_text` VALUES (128903, 0, 0, 'It\'s a pity the turtle fellow didn\'t come here himself. His shell would have fetched a high price.', 12, 0, 100, 1, 0, 0, 141177, 0, 'Carentan to Player');
INSERT INTO `creature_text` VALUES (128903, 1, 0, 'I suppose I should lay low for awhile...', 12, 0, 100, 1, 0, 0, 141178, 0, 'Carentan to Player');
INSERT INTO `creature_text` VALUES (128903, 2, 0, 'Farewell, fool!', 12, 0, 100, 3, 0, 0, 141180, 0, 'Carentan to Player');
INSERT INTO `creature_text` VALUES (128913, 0, 0, 'Put the hat on. You can\'t be a scurvy sea dog without a hat!', 12, 0, 100, 1, 0, 105439, 138743, 0, 'Flynn Fairwind to Player');
INSERT INTO `creature_text` VALUES (128913, 1, 0, 'Looking good. Let\'s tell the recruiter we\'ve come to volunteer.', 12, 0, 100, 1, 0, 105440, 138744, 0, 'Flynn Fairwind to Player');
INSERT INTO `creature_text` VALUES (128927, 0, 0, 'There you go. Now, a toast to better days!', 12, 0, 100, 1, 0, 106017, 140461, 0, 'Venrik to Player');
INSERT INTO `creature_text` VALUES (128941, 0, 0, 'W...were those inside of me?', 12, 0, 100, 1, 0, 0, 138779, 0, 'Nauseated Villager to Nauseated Villager');
INSERT INTO `creature_text` VALUES (128941, 1, 0, 'My stomach hasn\'t felt this good in days!', 12, 0, 100, 1, 0, 0, 138781, 0, 'Nauseated Villager to Nauseated Villager');
INSERT INTO `creature_text` VALUES (128941, 2, 0, 'How did those things even get in my stomach?!', 12, 0, 100, 1, 0, 0, 138783, 0, 'Nauseated Villager to Nauseated Villager');
INSERT INTO `creature_text` VALUES (129082, 0, 0, 'We did not expect the dead to walk!', 12, 0, 100, 4, 0, 0, 157361, 0, 'Injured Thornspeaker to Player');
INSERT INTO `creature_text` VALUES (129082, 1, 0, 'Ancient things which should not be, are stirring. Be on your guard.', 12, 0, 100, 4, 0, 0, 157362, 0, 'Injured Thornspeaker to Player');
INSERT INTO `creature_text` VALUES (129082, 2, 0, 'The constructs must be destroyed. Please help!', 12, 0, 100, 4, 0, 0, 157359, 0, 'Injured Thornspeaker to Player');
INSERT INTO `creature_text` VALUES (126581, 0, 0, 'The nobles may be a bit too uppity for my taste, but you can\'t help but like Norwington!', 12, 0, 100, 1, 0, 0, 0, 0, 'Roughneck Rider to Player');
INSERT INTO `creature_text` VALUES (129098, 0, 0, 'Aye-aye! Letter will get there fast fast! Full speed ahead, pretties!', 12, 0, 100, 113, 0, 0, 141207, 0, 'Rodrigo to Player');
INSERT INTO `creature_text` VALUES (129122, 0, 0, 'That\'s enough singing from you two!', 14, 0, 100, 0, 0, 0, 148419, 0, 'Stabby Jane to Nolpe');
INSERT INTO `creature_text` VALUES (129122, 1, 0, 'Good work. Maybe some day these waters will be safe again.', 12, 0, 100, 0, 0, 0, 148420, 0, 'Stabby Jane to Nolpe');
INSERT INTO `creature_text` VALUES (129158, 0, 0, 'My friend, lend me your aid!', 12, 0, 100, 0, 0, 108461, 143222, 0, 'Brannon Stormsong to Player');
INSERT INTO `creature_text` VALUES (129158, 1, 0, 'My workers... I failed them.', 12, 0, 100, 0, 0, 108464, 143221, 0, 'Brannon Stormsong to Player');
INSERT INTO `creature_text` VALUES (129170, 0, 0, 'Good girl. It\'s almost time for a treat!', 12, 0, 100, 1, 0, 0, 143040, 0, 'Rulf to Player');
INSERT INTO `creature_text` VALUES (129170, 1, 0, 'Ho, ho! Look at what the house cat dragged in! Unbelievable!', 12, 0, 100, 1, 0, 0, 143226, 0, 'Rulf to Player');
INSERT INTO `creature_text` VALUES (129170, 2, 0, 'Quite a catch, my friend. Let\'s see how he flies. Hyah!', 12, 0, 100, 0, 0, 0, 143227, 0, 'Rulf to Player');
INSERT INTO `creature_text` VALUES (129170, 3, 0, 'I\'ll get to work on the training. You go see Tagart, he\'s got more for you.', 12, 0, 100, 0, 0, 0, 143230, 0, 'Rulf to Player');
INSERT INTO `creature_text` VALUES (129181, 0, 0, 'Looks like you\'ve had enough. You\'re outta here!', 12, 0, 100, 0, 0, 0, 145723, 0, 'Barman Bill to Player');
INSERT INTO `creature_text` VALUES (129392, 0, 0, 'You\'re a life saver! I wonder where the Roughnecks will send me next...', 12, 0, 100, 3, 0, 0, 141580, 0, '\"Helpless\" Henry to Player');
