INSERT INTO `creature_text` VALUES (129575, 1, 0, 'Bloody mainlanders...', 12, 0, 100, 661, 0, 101553, 140799, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129575, 2, 0, 'Hey! Watch where you\'re pointing that thing!', 12, 0, 100, 396, 0, 101555, 140800, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129575, 3, 0, 'Ow!', 12, 0, 100, 33, 0, 101559, 140802, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129575, 4, 0, 'Clumsy oaf.', 12, 0, 100, 33, 0, 101559, 140801, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129575, 5, 0, 'The bloodflies are really biting these days, aren\'t they?', 12, 0, 100, 6, 0, 101561, 140803, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129590, 0, 0, 'All will melt.', 12, 0, 100, 0, 0, 0, 152756, 0, 'Prince Rathaw to Player');
INSERT INTO `creature_text` VALUES (129590, 1, 0, 'Life burns to ash...', 12, 0, 100, 0, 0, 0, 152773, 0, 'Prince Rathaw to Boss Tak');
INSERT INTO `creature_text` VALUES (129625, 0, 0, 'Ah, so Maynard sends his goons to solve his problems, as he always does. Well come, goon. I will take care of you as I did the others!', 12, 0, 100, 1, 0, 0, 148365, 0, 'Witch of the Woods to Player');
INSERT INTO `creature_text` VALUES (129625, 1, 0, 'You are a fool to get in my way!', 12, 0, 100, 0, 0, 0, 141406, 0, 'Witch of the Woods to Player');
INSERT INTO `creature_text` VALUES (129625, 2, 0, 'My husband\'s exploitation of his workers will never end...', 12, 0, 100, 0, 0, 0, 141414, 0, 'Witch of the Woods to Player');
INSERT INTO `creature_text` VALUES (129627, 0, 0, 'Give me an axe and I will chop!', 12, 0, 100, 1, 0, 0, 152463, 0, 'Algerson Lumberjack');
INSERT INTO `creature_text` VALUES (129627, 1, 0, 'Show me wicker, show me beasts... I will fight them all!', 12, 0, 100, 1, 0, 0, 152462, 0, 'Algerson Lumberjack');
INSERT INTO `creature_text` VALUES (129627, 2, 0, 'Kul Tirans never go down without a fight!', 12, 0, 100, 1, 0, 0, 152461, 0, 'Algerson Lumberjack');
INSERT INTO `creature_text` VALUES (129641, 0, 0, 'Ugh, clumsy oaf.', 12, 0, 100, 6, 0, 101562, 140801, 0, 'Corlain Refugee to Player');
INSERT INTO `creature_text` VALUES (129641, 1, 0, 'The coven will take your little town!', 14, 0, 100, 274, 0, 97735, 140838, 0, 'Unmasked Witch to Player');
INSERT INTO `creature_text` VALUES (129643, 0, 0, 'The word is given. Let us ride to Corlain!', 12, 0, 100, 0, 0, 97797, 143565, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (129655, 0, 0, 'This guy don\'t seem too bright.', 12, 0, 100, 1, 0, 101039, 141695, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129655, 1, 0, 'Seems they got dragged thataway, up the glacier ta the northwest.', 12, 0, 100, 25, 0, 101041, 141769, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129655, 2, 0, 'One o\' our axes. We\'re on the right track.', 12, 0, 100, 1, 0, 101036, 141594, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129655, 3, 0, 'One o\' our rucksacks. Looks like we\'ve found the right place.', 12, 0, 100, 1, 0, 101037, 141595, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129655, 4, 0, 'This guy really ruffles m\' feathers!', 12, 0, 100, 1, 0, 101038, 143217, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129655, 5, 0, 'Welcome ta the Roughnecks!', 12, 0, 100, 1, 0, 101040, 143201, 0, 'Boss Tak to Player');
INSERT INTO `creature_text` VALUES (129669, 0, 0, 'I\'m going to go check on the workers in the woods. If we\'re in danger here, I worry about their safety out there!', 12, 0, 100, 1, 0, 0, 148188, 0, 'Benjamin Algerson to Player');
INSERT INTO `creature_text` VALUES (129669, 1, 0, 'A witch... why is a witch targeting our operation?! I need to get out of here. I\'ll tell my father!', 12, 0, 100, 1, 0, 0, 148364, 0, 'Benjamin Algerson to Player');
