INSERT INTO `creature_text` VALUES (129749, 0, 0, 'For the... Horde...', 12, 0, 100, 0, 0, 103738, 150947, 0, 'Rampaging Grunt to Player');
INSERT INTO `creature_text` VALUES (129749, 1, 0, 'Your death will bring me honor!', 12, 0, 100, 0, 0, 103736, 150950, 0, 'Rampaging Grunt to Player');
INSERT INTO `creature_text` VALUES (129753, 0, 0, 'Bad juju comin\' for ya!', 12, 0, 100, 153, 0, 104237, 151024, 0, 'Bloodthirsty Headhunter to Player');
INSERT INTO `creature_text` VALUES (129753, 1, 0, 'I see... da Other Side...', 12, 0, 100, 0, 0, 104239, 151025, 0, 'Bloodthirsty Headhunter to Player');
