INSERT INTO `creature_text` VALUES (131636, 0, 0, 'Alright men, let us take this tower and drive the coven from Corlain!', 14, 0, 100, 5, 0, 99178, 146288, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 1, 0, 'Glory to House Waycrest!', 14, 0, 100, 0, 0, 99179, 146289, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 2, 0, 'Excellent work, Inquisitor! This tower will provide us a vantage over Corlain and all of its holdings.', 12, 0, 100, 5, 0, 99181, 146291, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 3, 0, 'The barrier is down! Waycrest Manor lies ahead!', 14, 0, 100, 5, 0, 102093, 147862, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 4, 0, 'Inquisitors! This is our chance to rescue Lord and Lady Waycrest... and put an end to this insufferable coven!', 12, 0, 100, 5, 0, 102972, 149007, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 5, 0, 'Stick close, and do not stop your advance until we\'ve breached the manor gates!', 12, 0, 100, 1, 0, 102973, 149009, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131636, 6, 0, 'For House Waycrest!', 12, 0, 100, 22, 0, 102974, 149010, 0, 'Marshal Everit Reade to Player');
INSERT INTO `creature_text` VALUES (131638, 0, 0, 'Oh, Alexander... I wish you were here now...', 12, 0, 100, 1, 0, 103806, 149850, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 1, 0, 'Fight it, Marshal!', 12, 0, 100, 5, 0, 103807, 149017, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 2, 0, 'Marshal, no...', 12, 0, 100, 661, 0, 103808, 149020, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 3, 0, 'Mother... what have you done?', 12, 0, 100, 6, 0, 103809, 149021, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 4, 0, 'Inquisitors, the final battle is upon you. May the winds be at your back.', 12, 0, 100, 5, 0, 108232, 149022, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 5, 0, 'I will join you inside, Inquisitor.', 12, 0, 100, 1, 0, 108233, 149025, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 6, 0, 'I may not be of much use in combat, but my mother must be brought to justice... and I must be there to see it through.', 12, 0, 100, 1, 0, 108234, 149027, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131638, 7, 0, 'For my father, and for Drustvar!', 12, 0, 100, 6, 0, 108235, 149026, 0, 'Lucille Waycrest to Player');
INSERT INTO `creature_text` VALUES (131639, 0, 0, 'I\'ll leave you to the mystery, then... while I see about cracking more coven skulls!', 12, 0, 100, 1, 0, 101048, 147950, 0, 'Inquisitor Mace to Player');
INSERT INTO `creature_text` VALUES (131639, 1, 0, 'You\'ve screeched at the wrong woman, witch!', 12, 0, 100, 0, 0, 104624, 149013, 0, 'Inquisitor Mace to Player');
INSERT INTO `creature_text` VALUES (131639, 2, 0, 'Aye ma\'am. We\'ll put the coven in the ground.', 12, 0, 100, 66, 0, 104625, 149023, 0, 'Inquisitor Mace to Player');
INSERT INTO `creature_text` VALUES (131639, 3, 0, 'Inquisitors, on me! Glory to House Waycrest!', 12, 0, 100, 22, 0, 104626, 149024, 0, 'Inquisitor Mace to Player');
INSERT INTO `creature_text` VALUES (131640, 0, 0, 'Thank you, $c! I was not looking forward to meeting the end of a pike.', 12, 0, 100, 5, 0, 104206, 149097, 0, 'Inquisitor Notley to Player');
