INSERT INTO `creature_text` VALUES (131641, 2, 0, 'We\'ll cover the flanks. $n, stay with Lucille and the Marshal!', 12, 0, 100, 0, 0, 104214, 149014, 0, 'Inquisitor Yorrick to Player');
INSERT INTO `creature_text` VALUES (131642, 0, 0, 'Now if you don\'t mind, I\'ve got a few more scores to settle.', 12, 0, 100, 1, 0, 104225, 147923, 0, 'Inquisitor Sterntide to Player');
INSERT INTO `creature_text` VALUES (131642, 1, 0, 'Thanks for the assist.', 12, 0, 100, 1, 0, 104227, 149093, 0, 'Inquisitor Sterntide to Player');
