INSERT INTO `creature_text` VALUES (131720, 0, 0, 'Excellent! I\'ll tell you what to add to the cauldron. Be sure to follow my instructions or the results could be.... explosive.', 12, 0, 100, 1, 0, 0, 144144, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 1, 0, 'We need to add some saltpeter to give it some kick!', 12, 0, 100, 1, 0, 28232, 144150, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 2, 0, 'You\'ve done some alchemy before! I can tell!', 12, 0, 100, 0, 0, 0, 144085, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 3, 0, 'Now carefully add in some hearthbloom.', 12, 0, 100, 0, 0, 0, 144151, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 4, 0, 'Well done! Now we need...', 12, 0, 100, 1, 0, 0, 144084, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 5, 0, 'Next, we need a few drops of volatile sap. Be careful not to jostle the jar too much!', 12, 0, 100, 0, 0, 0, 144153, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131720, 6, 0, 'Precisely! Now let\'s see what else...', 12, 0, 100, 0, 0, 0, 144086, 0, 'Warren Ashton to Player');
